INSERT INTO `tblbaiviet` (`idbv`, `tenbv`, `idcm`, `noidung`, `thumb`, `luotxem`, `hienthi`, `linkbv`, `tieude`, `tukhoa`, `mota`, `ngaydang`, `iduser`, `tinnoibac`) VALUES
(1, 'Huawei ra mắt GR5 2017 Pro với 4GB RAM + 64GB bộ nhớ trong', 1, NULL, 'images/Huawei-GR5-2017-Pro-Lifestyle-770x470.jpg', 14, b'1', 'Huawei-ra-mat-GR5-2017-Pro-voi-4GB-RAM-64GB-bo-nho-trong-gia-7-490-000', 'Huawei ra mắt GR5 2017 Pro với 4GB RAM + 64GB bộ nhớ trong, giá 7.490.000', 'Huawei', 'Sau thành công của Huawei GR5 2017 trong năm qua, Huawei chính thức giới thiệu đến người dùng phiên bản...', '2017-05-07', 1, b'0'),
(2, 'Cùng khám phá "nội thất" bên trong của Infinix Zero 4', 1, NULL, 'images/Screen-Shot-2017-03-07-at-8.33.20-AM-770x470.jpg', 2, b'1', 'cung-kham-pha-noi-that-ben-trong-cua-infinix-zero-4', 'Cùng khám phá "nội thất" bên trong của Infinix Zero 4', 'infinix', 'Infinix Zero 4 được giới công nghệ đánh gía cao bởi thiết kế đẹp mắt và hiệu năng tốt. Để đạt được điều...', '2017-03-07', 1, b'1'),
(3, 'Bộ xử lý AMD Ryzen™ 7 cho máy để bàn đã có mặt trên toàn cầu', 1, '', 'images/AMD-Ryzen-7-CPU-insocket1-770x470.jpg', 10, b'1', 'bo-xu-ly-amd-rezen-7', 'Bộ xử lý AMD Ryzen™ 7 cho máy để bàn đã có mặt trên toàn cầu', 'amd, vi xu ly', 'Hôm nay, AMD công bố ba bộ xử lý cho máy để bàn đầu tiên trong dòng CPU AMD Ryzen hiệu năng cao. Nhu cầu mạnh mẽ đối...', '2017-03-07', 1, b'1'),
(4, 'Đã có thể chơi game Don\'t Stop! Eighth Note trên PC', 1, NULL, 'images/1_28372-770x470.png', 4, b'1', 'game-choi-bang-giong-noi', 'Đã có thể chơi tựa game "hò hét" Don\'t Stop! Eighth Note trên PC', 'game hot, eighth', 'Gần đây, tựa game “hò hét” Don\'t Stop! Eighth Note đang làm cộng đồng mạng phát sốt và nhanh chóng truyền tay nhau,...', '2017-03-08', 1, b'1'),
(5, 'Mục đích tồn tại của cái chân bàn phím', 1, '', 'images/north-crown-x-1-img15-770x470.jpg', 1, b'1', 'muc-dich-ton-tai-cua-chan-ban-phim', 'Hầu hết chúng ta đang hiểu nhầm mục đích tồn tại của cái chân bàn phím', 'ban phim, huu ich ban phim', 'Mục đích của 2 cái chân này là gì, bạn biết không? Khi mở ra, đầu bàn phím sẽ được kê lên cao hơn bình thường một chút. Và theo như đa số người được hỏi, họ đều cho rằng lý do chúng xuất hiện là để bạn gõ được thoải mái hơn, giúp...', '2017-03-08', 1, b'1'),
(6, '4 giai đoạn mất nước: Chết vì khát là cái chết như thế nào?', 1, '<p>abhbb</p>\r\n', 'images/gd1-640x470.jpg', 22, b'1', '', 'Đây là 4 giai đoạn của tình trạng mất nước: Chết vì khát là cái chết như thế nào?', 'cuong song', 'Đây là 4 giai đoạn của tình trạng mất nước: Chết vì khát là cái chết như thế nào? Máu bạn sẽ đặc quánh lại...', '2017-03-08', 1, b'1'),
(7, 'PC như thế nào thì phù hợp với VR? Giá cả liệu có đắt?', 2, '<h2>2016 l&agrave; một mốc&nbsp;đ&aacute;ng nhớ&nbsp;đối với mảng VR, khi lần lượt hai thiết bị VR h&agrave;ng đầu l&agrave;&nbsp;Oculus Rift v&agrave; HTC Vive&nbsp;được ra mắt. Từ đ&oacute;&nbsp;đến nay cũng l&agrave; một qu&atilde;ng thời gian kh&aacute; d&agrave;i nếu bạn chưa từng thử VR, v&agrave; hiện tại gi&aacute; th&agrave;nh&nbsp;để x&acirc;y dựng một cấu h&igrave;nh&nbsp;đủ sức c&acirc;n game VR cũng&nbsp;đ&atilde; kh&ocirc;ng c&ograve;n&nbsp;đắt&nbsp;đỏ nữa, b&agrave;i n&agrave;y sẽ gi&uacute;p bạn t&igrave;m hiểu th&ecirc;m về cấu h&igrave;nh cho VR.</h2>\n\n<blockquote>\n<p>Laptop hổ trợ VR cũng l&agrave; một phần của cuộc chơi, nhưng Desktop lại l&agrave; con&nbsp;đường ngắn/rẻ nhất&nbsp;để bạn c&oacute; thể&nbsp;đạt&nbsp;được n&oacute;.</p>\n</blockquote>\n\n<p><strong><em>H&atilde;y&nbsp;điểm sơ qua cấu h&igrave;nh&nbsp;đề nghị:</em></strong></p>\n\n<ul>\n  <li>VGA: Nvidia GTX 970 / AMD R9 290 tương đương&nbsp;hoặc cao hơn</li>\n <li>CPU: Intel i5-4590 / AMD FX-8350 tương&nbsp;đương hoặc cao hơn</li>\n <li>RAM: 8GB RAM hoặc cao hơn</li>\n  <li>Video Output: HDMI 1.3</li>\n <li>USB Ports: 3x USB 3.0, 1x USB 2.0</li>\n  <li>OS:&nbsp;Windows 7 SP1 64bit hoặc bản mới hơn</li>\n</ul>\n\n<p>Thực tế, một cấu h&igrave;nh&nbsp;để chơi Game VR cũng kh&ocirc;ng phải&nbsp;đ&ograve;i hỏi qu&aacute; cao i7 hoặc GTX 1080Ti g&igrave; cả, bạn chỉ cần&nbsp;đến i5 v&agrave; VGA Card cũng nằm&nbsp;ở mức chấp nhận được&nbsp;khi GTX 970 ra mắt cũng&nbsp;đ&atilde; l&acirc;u v&agrave; gi&aacute; hiện tại x&eacute;t tr&ecirc;n diện h&agrave;ng cũ th&igrave; cũng rẻ hơn kh&aacute; nhiều.</p>\n\n<blockquote>\n<p><em>Một số nh&agrave; sản xuất thường c&oacute; những cấu h&igrave;nh/thiết bị sẳn s&agrave;ng với c&aacute;c y&ecirc;u cầu của VR nhưng như thường lệ gaming cao cấp bạn tự x&acirc;y dựng n&ecirc;n sẽ rẻ hơn kh&aacute; nhiều.</em></p>\n</blockquote>\n\n<p>Việc tự x&acirc;y dựng&nbsp;một&nbsp;case m&aacute;y t&iacute;nh đ&aacute;p ứng v&agrave; ph&ugrave; hợp với nhu cầu/t&uacute;i tiền của bạn&nbsp;hiện nay kh&ocirc;ng phải l&agrave; chuyện qu&aacute; kh&oacute; khăn, bạn c&oacute; thể tự t&igrave;m hiểu n&oacute; th&ocirc;ng qua những b&agrave;i viết x&acirc;y dựng cấu h&igrave;nh tr&ecirc;n Internet, hoặc l&agrave; th&ocirc;ng qua c&aacute;c diễn&nbsp;đ&agrave;n, fanpage tr&ecirc;n Facebook.</p>\n\n<p>Dưới&nbsp;đ&acirc;y l&agrave; cấu h&igrave;nh đủ đ&aacute;p ứng khả năng chơi VR nhưng bạn vẫn c&oacute; thể điều chỉnh để ph&ugrave; hợp với nhu cầu của m&igrave;nh:</p>\n\n<ul>\n  <li><strong>CPU:</strong>&nbsp;Intel i5-4590 với Quad-Core 3.3GHz Turbo 3.7GHz</li>\n <li><strong>Motherboard:</strong>&nbsp;ASRock H97M Anniversary Edition</li>\n <li><strong>RAM:</strong>&nbsp;8GB (2x4GB) G.Skill Ripjaws X DDR3 (2133MHz)</li>\n  <li><strong>Video Card:</strong>&nbsp;Sapphire Nitro+ Radeon RX 480 with 4GB RAM ($219)</li>\n  <li><strong>SSD:</strong>&nbsp;64GB A-DATA Premier Pro SP600</li>\n <li><strong>HDD:</strong>&nbsp;2TB Seagate Barracuda</li>\n <li><strong>Case:</strong>&nbsp;DIYPC GameMax-BK</li>\n <li><strong>PSU:</strong>&nbsp;500W Corsair Builder (80+ Bronze)</li>\n <li><strong>OS:</strong>&nbsp;Windows 10</li>\n <li><strong>Tổng cộng: ~$830&nbsp;</strong></li>\n</ul>\n\n<h6>Cấu h&igrave;nh tham thảo MakeUseOf</h6>\n\n<p>Video tham khảo:</p>\n\n<p><iframe allowfullscreen="" frameborder="0" height="433" src="https://www.youtube.com/embed/qQrXlfn7tJA?feature=oembed" width="100%"></iframe></p>\n\n<p>Chi tiết hơn về phần cứng, chiếc card&nbsp;đồ hoạ Radeon RX480 mới vừa&nbsp;được ra mắt gần&nbsp;đ&acirc;y nhận&nbsp;được nhiều nhận x&eacute;t tốt&nbsp;về khả năng xử l&iacute; trong c&aacute;c tr&ograve; chơi VR. V&agrave; nếu bạn trang bị n&oacute; v&agrave;o một cấu h&igrave;nh&nbsp;đ&atilde; 3 năm tuổi như tr&ecirc;n th&igrave; mọi chuyện vẫn hợp l&iacute; khi c&acirc;n bằng được giữa gi&aacute; tiền lẫn hiệu năng.</p>\n\n<p><iframe allowfullscreen="" frameborder="0" height="433" src="https://www.youtube.com/embed/2GdfDCq86Gk?feature=oembed" width="100%"></iframe></p>\n\n<blockquote>\n<p>Bạn kh&ocirc;ng n&ecirc;n ti&ecirc;u tốn qu&aacute; nhiều tiền&nbsp;để&nbsp;đầu tư v&agrave;o VGA Card cao cấp&nbsp;trong khi với những sản phẩm gi&aacute; rẻ cũng&nbsp;đ&atilde; hổ trợ VR.</p>\n</blockquote>\n\n<p>C&ograve;n đối với i5-4590 sẽ vẫn&nbsp;ổn&nbsp;ở thời&nbsp;điểm hiện tại&nbsp;v&agrave; cho cả những game VR, c&ograve;n HDD l&agrave; cần thiết cho c&aacute;c tựa game VR v&igrave; n&oacute; ti&ecirc;u tốn kh&aacute; nhiều bộ nhớ, cũng như việc trang bị&nbsp;SSD&nbsp;sẽ c&agrave;ng l&agrave;m bạn ti&ecirc;u tốn hơn nhưng về hiệu năng chơi game th&igrave; n&oacute; chả kh&aacute;c mấy, n&ecirc;n SSD dừng lại ở 64GB l&agrave; đủ để c&agrave;i hệ điều h&agrave;nh.&nbsp;Đối với RAM cũng vậy, 8GB hiện tại cũng l&agrave;&nbsp;đủ d&ugrave;ng, nhưng nếu c&ocirc;ng việc hiện tại của bạn&nbsp;đ&ograve;i hỏi nhiều hơn, v&agrave; bạn cũng muốn chạy th&ecirc;m một v&agrave;i&nbsp;ứng dụng g&igrave;&nbsp;đ&oacute; khi&nbsp;đang chơi VR th&igrave; bạn c&oacute; thể n&acirc;ng l&ecirc;n th&ecirc;m.</p>\n\n<p><em>C&ograve;n nếu bạn kh&ocirc;ng muốn d&agrave;nh quỹ thời gian hạn hẹp của m&igrave;nh&nbsp;để x&acirc;y dựng cấu h&igrave;nh th&igrave; cứ chạy ra cửa h&agrave;ng gần nhất v&agrave;&nbsp;đem một trong những bộ m&aacute;y n&agrave;y về chơi th&ocirc;i.</em></p>\n\n<h4><strong>DELL XPS 8900 SE ($999.9)</strong></h4>\n\n<p>DELL XPS 8900 SE&nbsp;đ&aacute;p&nbsp;đứng&nbsp;được c&aacute;c y&ecirc;u cầu tối thiểu m&agrave; Oculus Rift y&ecirc;u cầu, v&agrave;&nbsp;đ&acirc;y c&oacute; thể xem l&agrave; chiếc m&aacute;y bộ rẻ nhất&nbsp;đạt&nbsp;được chứng nhận&nbsp;đ&oacute;.</p>\n\n<p><iframe allowfullscreen="" frameborder="0" height="433" src="https://www.youtube.com/embed/CFRbBlGwyQo?feature=oembed" width="100%"></iframe></p>\n\n<p>Cấu h&igrave;nh chi tiết:</p>\n\n<ul>\n  <li><strong>CPU:</strong>&nbsp;2.7GHz Intel Core i5-6400</li>\n <li><strong>RAM:</strong>&nbsp;8GB DDR4 (2133 MHz)</li>\n <li><strong>Graphics:</strong>&nbsp;4GB Nvidia GeForce GTX 970</li>\n <li><strong>Storage:</strong>&nbsp;1TB HDD</li>\n</ul>\n\n<p>Nhưng nếu bạn cần nhiều hơn, kh&ocirc;ng chỉ cấu h&igrave;nh m&agrave; c&ograve;n l&agrave; ngoại h&igrave;nh th&igrave; đ&atilde; c&oacute; Acer Predator G6.</p>\n\n<h4><strong>&nbsp;Acer Predator G6($2.100)</strong></h4>\n\n<p><iframe allowfullscreen="" frameborder="0" height="433" src="https://www.youtube.com/embed/EN_qx1Z8Uj8?feature=oembed" width="100%"></iframe></p>\n\n<p>Predator l&agrave; một d&ograve;ng m&aacute;y&nbsp;được Acer tối&nbsp;ưu cho Gaming, một số game thủ sử dụng d&ograve;ng Predator như l&agrave; thiết bị ch&iacute;nh cho việc chơi game của họ, thế n&ecirc;n việc Predator hổ trợ cho Game VR kh&ocirc;ng phải l&agrave; chuyện l&agrave;. Predator G6&nbsp;được trang bị cấu h&igrave;nh ti&ecirc;u chuẩn cũng như sự&nbsp;đồng bộ cao m&agrave; Acer mang&nbsp;đến cho n&oacute;, c&ugrave;ng&nbsp;đ&oacute; l&agrave; d&aacute;ng vẻ bắt mắt hầm hố của m&igrave;nh dễ d&agrave;ng l&agrave;m&nbsp;đổ gục những game thủ kh&oacute; t&iacute;nh.</p>\n\n<p>Cấu h&igrave;nh:</p>\n\n<ul>\n  <li><strong>CPU:</strong>&nbsp;4GHz Intel Core i7-6700K</li>\n  <li><strong>RAM:</strong>&nbsp;16GB DDR4 (2133 MHz)</li>\n  <li><strong>Graphics:</strong>&nbsp;4GB Nvidia GeForce GTX 980</li>\n <li><strong>SSD:</strong>&nbsp;256GB</li>\n <li><strong>HDD:</strong>&nbsp;2TB</li>\n</ul>\n\n<p>Tuy cấu h&igrave;nh&nbsp;kh&ocirc;ng&nbsp;được xem l&agrave; mới nhất nhưng n&oacute; vẫn&nbsp;đ&aacute;p&nbsp;ứng&nbsp;đầy&nbsp;đủ nhu cầu chơi Game VR của bạn, v&agrave; ngo&agrave;i ra tr&ecirc;n d&ograve;ng Predator c&ograve;n k&egrave;m th&ecirc;m v&agrave;i tinh chỉnh, cũng như t&iacute;nh năng&nbsp;để hổ trợ tốt hơn cho việc chơi game của bạn như việc&nbsp;&eacute;p xung CPU dễ d&agrave;ng th&ocirc;ng qua một n&uacute;t nhấn&nbsp;để tăng cường hiệu xuất m&aacute;y t&iacute;nh cũng như khả năng chơi game, c&ugrave;ng&nbsp;đ&oacute; l&agrave; tặng k&egrave;m b&agrave;n ph&iacute;m v&agrave; chuột&nbsp;để tiết kiệm chi ph&iacute;.</p>\n\n<h4><strong>HTPC:&nbsp;Origin Chronos VR ($1750-$3050)</strong></h4>\n\n<p><iframe allowfullscreen="" frameborder="0" height="433" src="https://www.youtube.com/embed/Nkk05PSCV68?feature=oembed" width="100%"></iframe></p>\n\n<p>Với thiết kế nhỏ gọn cũng như cấu h&igrave;nh mạnh mẽ&nbsp;Origin Chronos VR l&agrave; một sự lựa chọn xứng&nbsp;đ&aacute;ng tiếp theo.&nbsp;Được trang bị tr&ecirc;n&nbsp;Origin Chronos VR l&agrave; Nvidia GTX 1080, một hệ thống tản nhiệt nước AIO n&acirc;ng cao hiệu xuất l&agrave;m m&aacute;t cũng như gi&uacute;p m&aacute;y t&iacute;nh hoạt động trơn tru m&agrave; kh&ocirc;ng gặp phải vấn&nbsp;đề g&igrave;.</p>\n\n<p>Cấu h&igrave;nh:</p>\n\n<ul>\n  <li><strong>CPU:</strong>&nbsp;4GHz Quad-Core Intel Core i7-6700K</li>\n  <li><strong>RAM:</strong>&nbsp;16GB DDR4 (2666 MHz)</li>\n  <li><strong>Graphics:</strong>&nbsp;8GB Nvidia GeForce GTX 1080 Founder&rsquo;s Edition</li>\n  <li><strong>SSD:</strong>&nbsp;256GB</li>\n <li><strong>HDD:</strong>&nbsp;2TB</li>\n</ul>\n\n<p>Điểm kh&oacute; chịu duy nhất Chronos l&agrave; n&oacute; kh&ocirc;ng thể n&acirc;ng cấp một c&aacute;ch dễ như chiếc PC th&ocirc;ng thường, với một số th&agrave;nh phần bạn cần phải gửi n&oacute;&nbsp;đến cho nh&agrave; sản xuất&nbsp;để họ n&acirc;ng cấp gi&ugrave;m, sẽ l&agrave; một lựa chọn kh&oacute; khăn cho nhưng ai th&iacute;ch vọc vạch phần cứng.</p>\n\n<p><strong>Chọn lựa m&agrave;n h&igrave;nh cho VR</strong></p>\n\n<p>Nếu bạn chủ&nbsp;động rằng chiếc m&aacute;y t&iacute;nh l&agrave;&nbsp;để chơi game VR th&igrave; bạn chỉ cần một chiếc m&agrave;n h&igrave;nh b&igrave;nh thường l&agrave; đủ, v&igrave; chủ yếu bạn chơi Game VR th&ocirc;ng qua&nbsp;Rift v&agrave; Vive chứ kh&ocirc;ng phải l&agrave; m&agrave;n h&igrave;nh m&aacute;y t&iacute;nh.</p>\n\n<p>Ngo&agrave;i ra với những m&aacute;y bộ tr&ecirc;n th&igrave; n&oacute;&nbsp;đ&atilde;&nbsp;được c&agrave;i sẳn&nbsp;Windows 10, c&ograve;n&nbsp;đối với cấu h&igrave;nh tự x&acirc;y dựng bạn sẽ cần th&ecirc;m&nbsp;&iacute;t thời gian&nbsp;để c&agrave;i hệ&nbsp;điều h&agrave;nh, v&agrave; ngo&agrave;i ra&nbsp;Windows 10 hiện nay&nbsp;được xem l&agrave; tốt nhất&nbsp;để cho trải nghiệm game ho&agrave;n hảo nếu bạn&nbsp;đang&nbsp;ở&nbsp;Windows 7 hoặc 8 th&igrave; n&ecirc;n n&acirc;ng cấp l&ecirc;n ngay b&acirc;y giờ.</p>\n', 'images/gd1-640x470-1.jpg', 6, b'1', 'vr-tren-pc', 'PC như thế nào thì phù hợp với VR?', 'VR tren PC', '2016 là một mốc đáng nhớ đối với mảng VR, khi lần lượt hai thiđầu là Oculus Rift và HTC Vive được ra mắt. Từ đó đến nay cũng là một quãng thời gian khá dài nếu bạn chưa từng thử VR, và hiện tại giá thành để xây dựng một cấu hình đủ sức cân game VR là...', '2017-03-13', 1, b'1'),
(8, 'Kingston tái cấu trúc văn phòng đại diện tại Việt Nam', 2, '<h2>Để th&iacute;ch hợp với tốc độ thay đổi của thị trường v&agrave; cũng nhằm cung cấp&nbsp; dịch vụ tốt hơn, linh động hơn, hiện tại Kingston đang triển khai t&aacute;i cấu tr&uacute;c bộ m&aacute;y kinh doanh v&agrave; tiếp thị APAC &ndash; Ch&acirc;u &Aacute; Th&aacute;i B&igrave;nh Dương &nbsp;v&agrave; đ&oacute;ng cửa văn ph&ograve;ng đại diện tại Việt Nam kể từ ng&agrave;y 1 th&aacute;ng &nbsp;3 năm 2017</h2>\r\n\r\n<p>Gần một thập kỷ gia nhập thị trường Việt Nam, với nhiều giải thưởng từ c&aacute;c tổ chức kh&aacute;c nhau, Kingston vẫn đang &nbsp;được c&ocirc;ng nhận l&agrave; nh&atilde;n hiệu h&agrave;ng đầu ở c&aacute;c d&ograve;ng sản phẩm RAM v&agrave; bộ nhớ flash tại Việt Nam cũng như xem đ&acirc;y l&agrave; thị trường đầy tiềm năng. Nay, ch&uacute;ng t&ocirc;i tr&acirc;n trọng th&ocirc;ng b&aacute;o việc tiến h&agrave;nh tổ chức lại m&ocirc; h&igrave;nh kinh doanh tại khu vực APAC n&agrave;y nhằm đẩy mạnh &nbsp;mục ti&ecirc;u đ&atilde; từng được đặt ra l&ecirc;n một tầm cao hơn.</p>\r\n\r\n<p>Trong tiến tr&igrave;nh t&aacute;i cấu tr&uacute;c, đội ngũ đại diện b&aacute;n h&agrave;ng của ch&uacute;ng t&ocirc;i vẫn hiện diện tại Việt Nam v&agrave; tiếp tục phục vụ tất cả nhu cầu của qu&yacute; đối t&aacute;c, kh&aacute;ch h&agrave;ng v&agrave; cửa h&agrave;ng. Việc hợp t&aacute;c với c&aacute;c nh&agrave; ph&acirc;n phối, SPC (http://www.spc.com.vn/) v&agrave; FPT (https://www.fpt.com.vn/) vẫn kh&ocirc;ng c&oacute; g&igrave; thay đổi. Ch&uacute;ng t&ocirc;i c&ugrave;ng nhau nỗ lực để bảo đảm duy tr&igrave; chất lượng sản phẩm v&agrave; dịch vụ.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, chế độ bảo h&agrave;nh của Kingston với tất cả c&aacute;c d&ograve;ng sản phẩm cũng kh&ocirc;ng c&oacute; g&igrave; thay đổi. Bất kỳ thắc mắc g&igrave; về sản phẩm hoặc dịch vụ hậu m&atilde;i, vui l&ograve;ng li&ecirc;n hệ nơi b&aacute;n ra sản phẩm, gửi thư điện tử đến technical_support@kingston.com.tw, hoặc gọi c&oacute; t&iacute;nh cước đến số +886-3-500-1715 từ thứ Hai đến thứ S&aacute;u, 07:00 đến 17:00.</p>\r\n', 'images/cv-1.jpg', 4, b'1', 'kingston-tai-cau-truc-van-phong-dai-dien-tai-viet-nam', 'Kingston tái cấu trúc văn phòng đại diện tại Việt Nam', 'kingston', 'Để thích hợp với tốc độ thay đổi của thị trường và cũng nhằm cung cấp  dịch vụ tốt hơn, linh động hơn, hiện tại Kingston đang triển khai tái cấu trúc bộ máy kinh doanh và tiếp thị APAC – Châu Á Thái Bình Dương...', '2017-03-13', 2, b'0'),
(9, 'Fujifilm ra mắt máy ảnh cảm biến Medium Format GFX 50S tại VN', 2, '<h2>H&ocirc;m nay (24/02), Fujifilm Việt Nam đ&atilde; ch&iacute;nh thức đem chiếc m&aacute;y ảnh kh&ocirc;ng gương&nbsp;lật với cảm biến Medium Format t&ecirc;n gọi &ldquo;GFX 50S&rdquo; về Việt Nam v&agrave; chuẩn bị&nbsp;l&ecirc;n kệ từ th&aacute;ng 3 n&agrave;y.</h2>\r\n\r\n<p>D&ograve;ng X-Series của Fuji được ra mắt từ năm 2011 với cảm biến APS-C (crop) v&agrave; d&agrave;nh được nhiều sự quan t&acirc;m của giới nhiếp ảnh. Tuy vậy tới nay h&atilde;ng vẫn chưa c&oacute; d&ograve;ng m&aacute;y ảnh kh&ocirc;ng gương lật&nbsp;n&agrave;o d&ugrave;ng cảm biến Full Frame, ch&iacute;nh v&igrave; thế GFX 50S thực sự l&agrave; một sự đột ph&aacute; lớn khi Fuji đ&atilde; lần đầu đem một cảm biến lớn Medium Format l&ecirc;n d&ograve;ng kh&ocirc;ng gương lật của m&igrave;nh.</p>\r\n\r\n<p style="text-align:center"><a href="http://congngheviet.com/wp-content/uploads/2017/02/DSC09436.jpg" rel="attachment wp-att-28714"><img alt="DSC09436" src="http://localhost/congngheviet/images/DSC09436.jpg" style="float:left; width:96%" /></a></p>\r\n\r\n<p><a href="http://congngheviet.com/wp-content/uploads/2017/02/DSC09437.jpg" rel="attachment wp-att-28713"><img alt="DSC09437" src="http://localhost/congngheviet/images/DSC09437.jpg" style="width:96%" /></a><a href="http://congngheviet.com/wp-content/uploads/2017/02/DSC09438.jpg" rel="attachment wp-att-28712"><img alt="DSC09438" src="http://localhost/congngheviet/images/DSC09438.jpg" style="width:96%" /></a><a href="http://congngheviet.com/wp-content/uploads/2017/02/DSC09440.jpg" rel="attachment wp-att-28711"><img alt="DSC09440" src="http://localhost/congngheviet/images/DSC09440.jpg" style="width:96%" /></a><a href="http://congngheviet.com/wp-content/uploads/2017/02/DSC09444.jpg" rel="attachment wp-att-28709"><img alt="DSC09444" src="http://localhost/congngheviet/images/DSC09444.jpg" style="width:96%" /></a><a href="http://congngheviet.com/wp-content/uploads/2017/02/DSC09435.jpg" rel="attachment wp-att-28715"><img alt="DSC09435" src="http://localhost/congngheviet/images/DSC09435.jpg" style="width:96%" /></a></p>\r\n\r\n<p>Nhờ những ưu điểm của m&aacute;y ảnh kh&ocirc;ng gương lật n&ecirc;n GFX 50S c&oacute; k&iacute;ch thước chỉ bằng một chiếc m&aacute;y ảnh Full Frame khi so với c&aacute;c đối thủ kh&aacute;c, trong khi đ&oacute; cảm biến của n&oacute; lại lớn gấp 1.7 lần. GFX 50S trang bị cảm biến CMOS k&iacute;ch thước 43.8mm x 32.9mm, độ ph&acirc;n giải 51.4MP được tối ưu để thu nhận &aacute;nh s&aacute;ng v&agrave; t&aacute;i tạo t&ocirc;ng m&agrave;u tốt. Những bức ảnh được chụp ra từ m&aacute;y nhờ đ&oacute; sẽ c&oacute; độ ch&iacute;nh x&aacute;c cao gi&uacute;p nhiếp ảnh gia tăng sự s&aacute;ng tạo của m&igrave;nh.</p>\r\n\r\n<p>GFX 50S được trang bị bộ xử l&yacute; X Processor Pro tương tự tr&ecirc;n d&ograve;ng X-Pro 2 v&agrave; X-T2, cho ph&eacute;p th&aacute;o rời ống ngắm EVF v&agrave; trang bị th&ecirc;m một EVF kh&aacute;c c&oacute; khả năng thay đổi g&oacute;c nh&igrave;n (EVF-TL1, b&aacute;n ri&ecirc;ng) đến 90 độ theo chiều thẳng đứng. M&agrave;n h&igrave;nh b&ecirc;n trong EVF đạt độ ph&acirc;n giải 3.69 triệu điểm ảnh, trong khi đ&oacute; m&agrave;n h&igrave;nh LCD 3.2&Prime; đạt 2.36 triệu điểm ảnh v&agrave; cho ph&eacute;p xoay lật 3 chiều.</p>\r\n\r\n<p><a href="http://congngheviet.com/wp-content/uploads/2017/02/DSC09449.jpg" rel="attachment wp-att-28706"><img alt="DSC09449" src="http://congngheviet.com/wp-content/uploads/2017/02/DSC09449.jpg" style="width:96%" /></a></p>\r\n\r\n<p>Để sử dụng GFX 50S, Fujifilm cũng ra mắt hệ thống&nbsp;ống k&iacute;nh FUJINON GS. Những ống k&iacute;nh n&agrave;y rất đắt tiền nhưng chất lượng của ch&uacute;ng rất xứng đ&aacute;ng.&nbsp;Những ống k&iacute;nh&nbsp;được b&aacute;n ra ban đầu bao gồm: GF 63mm f/2.9 R WR (34.9 triệu đồng); GF 32-64mm f/4 R LM WR (54.9 triệu đồng); GF 120mm f/4 R LM OIS WR Macro (64.9 triệu đồng). Trong năm nay&nbsp;h&atilde;ng cũng sẽ tung ra một số ống k&iacute;nh kh&aacute;c để người d&ugrave;ng th&ecirc;m sự lựa chọn, v&agrave; chắc chắn rằng c&aacute;c ống k&iacute;nh tiếp theo cũng sẽ c&oacute; gi&aacute; th&agrave;nh kh&ocirc;ng hề rẻ.</p>\r\n\r\n<p>GFX 50S c&oacute; gi&aacute; b&aacute;n ch&iacute;nh h&atilde;ng l&agrave; 150 triệu đồng với chương tr&igrave;nh đặt trước từ 25/02 đến 10/03, k&egrave;m theo ưu đ&atilde;i qu&agrave; tặng trị gi&aacute; hơn 5 triệu đồng bao gồm: t&uacute;i đựng m&aacute;y ảnh, bộ lau ch&ugrave;i m&aacute;y ảnh + ống k&iacute;nh, thẻ nhớ SanDisk Extreme Pro 64GB 95MB/s, g&oacute;i bảo h&agrave;nh 2 năm cho cả m&aacute;y v&agrave; ống k&iacute;nh.</p>\r\n', 'images/DSC09442-1500x1100.jpg', 2, b'1', 'fujifilm-ra-mat-may-anh-mirrorless-cam-bien-medium-format-gfx-50s-tai-vn', 'Fujifilm ra mắt máy ảnh Mirrorless cảm biến Medium Format GFX 50S tại VN', 'fujifilm', 'Hôm nay (24/02), Fujifilm Việt Nam đã chính thức đem chiếc máy ảnh không gương lật với cảm biến Medium Format tên gọi “GFX 50S” về Việt Nam và chuẩn bị lên kệ từ tháng 3 này.', '2017-03-13', 1, b'1'),
(10, 'Khách hàng đến nhận Galaxy A5 / A7 2017 ngày đầu mở bán', 2, '<h2>H&ocirc;m nay 18/02, bộ đ&ocirc;i Galaxy A5 &amp; A7 đ&atilde; ch&iacute;nh thức l&ecirc;n kệ tr&ecirc;n to&agrave;n quốc. Theo ghi nhận của CNV, rất nhiều kh&aacute;ch h&agrave;ng đ&atilde; đến xếp h&agrave;ng tại FPT Shop, Viễn Th&ocirc;ng A, Hnam Mobile, TGDD&hellip; để được nhận m&aacute;y với&nbsp;những ưu đ&atilde;i kh&aacute;c nhau.</h2>\r\n\r\n<p>Theo&nbsp;Viễn Th&ocirc;ng A, Samsung Galaxy A5/A7 2017 thu h&uacute;t hơn 4426 lượt người đăng k&yacute; đặt trước tr&ecirc;n trang pre-order. Ngay trong ng&agrave;y 18/02, hơn 200 t&iacute;n đồ c&ocirc;ng nghệ đang tr&ocirc;ng chờ được tận tay đập hộp chiếc Galaxy A5/A7 2017. Nhiều kh&aacute;ch h&agrave;ng đ&atilde; c&oacute; mặt từ sớm tại sự kiện mở b&aacute;n để xếp h&agrave;ng chờ mua sản phẩm hot n&agrave;y.&nbsp;C&ugrave;ng trong&nbsp;buổi s&aacute;ng n&agrave;y, kh&aacute;ch h&agrave;ng chờ nhận m&aacute;y ngo&agrave;i niềm vui được cầm tr&ecirc;n tay chiếc điện thoại vượt trội như A5/A7 2017 m&agrave; c&ograve;n nhận được ưu đ&atilde;i đặc biệt của Viễn Th&ocirc;ng A l&agrave; cơ hội sở hữu tai nghe Microlap K290.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/mua-ban-galaxy-a5-2017- no-nuc-02.jpeg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center">Chị Ch&acirc;u &ndash; kh&aacute;ch h&agrave;ng đầu ti&ecirc;n được nhận m&aacute;y cho biết: &ldquo;Chị rất vui khi vừa được mua chiếc điện thoại với nhiều t&iacute;nh năng hấp dẫn, nhiều m&agrave;u sắc để chọn lựa như Galaxy A5 2017, hơn nữa chị c&ograve;n nhận được nhiều qu&agrave; tặng v&agrave; tai nghe Microlap K290 từ Viễn Th&ocirc;ng A&rdquo;.</p>\r\n\r\n<p>Về ph&iacute;a FPT Shop,&nbsp;từ ng&agrave;y 3/2 đ&ecirc;́n 12h trưa ng&agrave;y 17/2, FPT Shop đ&atilde; nh&acirc;̣n được 6.169 đơn h&agrave;ng đặt mua trước d&agrave;nh cho&nbsp;<a href="http://fptshop.com.vn/samsung-galaxy-a5-a7-2017">Galaxy A 2017</a>, trong đ&oacute; c&oacute;&nbsp;&nbsp;3.967 kh&aacute;ch h&agrave;ng đ&atilde; đặt cọc. Tỉ lệ m&aacute;y Galaxy A5 2017 được đặt mua l&agrave; 60%.&nbsp;Đối với Galaxy A5 2017, m&agrave;u đen chiếm tỉ lệ đặt mua cao nhất với 55%, kế đến l&agrave; m&agrave;u v&agrave;ng đồng với 30% v&agrave; 15% d&agrave;nh cho m&agrave;u hồng. C&ograve;n với Galaxy A7 2017, tỉ lệ đặt mua m&agrave;u đen v&agrave; v&agrave;ng đồng ngang bằng nhau chiếm 42%.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/mua-ban-galaxy-a5-2017- no-nuc-03.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/mua-ban-galaxy-a5-2017- no-nuc-04.jpeg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center">Bạn Đ.H.Nho l&agrave; một trong những kh&aacute;ch h&agrave;ng đầu ti&ecirc;n đến nhận m&aacute;y Galaxy A5 2017 tại FPT Shop 202 Nguyễn Thị Minh Khai, quận 3 v&agrave; được tặng th&ecirc;m phiếu mua h&agrave;ng trị gi&aacute; 500.000 đồng b&ecirc;n cạnh bộ qu&agrave; trị gi&aacute; 2.000.000 đồng</p>\r\n\r\n<p>Bộ đ&ocirc;i Galaxy A5, A7 2017 l&agrave; phi&ecirc;n bản mới thừa hưởng những &ldquo;tinh hoa&rdquo; của flagship Galaxy S7 như: khả năng kh&aacute;ng bụi v&agrave; kh&aacute;ng nước đạt chuẩn IP68, bộ đ&ocirc;i camera trước v&agrave; sau đều c&oacute; độ ph&acirc;n giải &ldquo;khủng&rdquo; 16.0MP, thiết kế tinh xảo với sự kết hợp giữa k&iacute;nh v&agrave; kim loại cao cấp.&nbsp;Galaxy A 2017 được đầu tư mạnh về thiết kế, lấy cảm hứng từ d&ograve;ng S cao cấp của năm 2016. Bộ đ&ocirc;i A5, A7 2017 đều sở hữu bộ khung kim loại chắc chắn với mặt lưng cong &ocirc;m s&aacute;t viền m&aacute;y d&ugrave;ng chất liệu k&iacute;nh 3D c&ugrave;ng viền m&agrave;n h&igrave;nh si&ecirc;u mỏng.</p>\r\n\r\n<p>Về cấu h&igrave;nh, Galaxy A5 &amp;&nbsp;A7 2017 đều được trang bị chip xử l&yacute; Exynos 7880 8 nh&acirc;n&nbsp;tốc độ 1,9 GHz,&nbsp;RAM 3GB, bộ nhớ trong 32GB v&agrave; c&oacute; thể mở rộng tới 256GB bằng thẻ MicroSD gi&uacute;p m&aacute;y hoạt động mượt m&agrave; tr&ecirc;n nền hệ điều h&agrave;nh Android 6.0.1. Cả 2 sản phẩm đều sở hữu m&agrave;n h&igrave;nh Super AMOLED với độ ph&acirc;n giải Full HD. Nếu Galaxy A5 2017 sở hữu m&agrave;n h&igrave;nh rộng 5,2 inch c&ugrave;ng vi&ecirc;n pin 3.000mAh th&igrave; m&agrave;n h&igrave;nh của Galaxy A7 2017 rộng đến 5,7 inch c&ugrave;ng vi&ecirc;n pin dung lượng 3.600mAh. Ngo&agrave;i ra, sản phẩm c&ograve;n được trang bị cổng USB Type-C cho tốc độ truyền tải dữ liệu nhanh hơn v&agrave; thời gian sạc pin cũng r&uacute;t ngắn nhiều so với cổng kết nối cũ.</p>\r\n\r\n<p>Th&ecirc;m một ưu điểm tr&ecirc;n sản phẩm n&agrave;y sẽ l&agrave;m h&agrave;i l&ograve;ng những người d&ugrave;ng y&ecirc;u th&iacute;ch chụp ảnh l&agrave; bộ đ&ocirc;i camera c&oacute; c&ugrave;ng độ ph&acirc;n giải &ldquo;khủng&rdquo; 16.0MP. Đồng thời, với khẩu độ f/1.9 sẽ cho khả năng thu s&aacute;ng tốt hơn, mang đến cho bạn những bức ảnh s&aacute;ng r&otilde; trong mọi điều kiện &aacute;nh s&aacute;ng, c&ugrave;ng nhiều t&iacute;nh năng th&uacute; vị như: chụp ảnh x&oacute;a ph&ocirc;ng, tự động lấy n&eacute;t, chạm lấy n&eacute;t, nhận diện khu&ocirc;n mặt&hellip;</p>\r\n\r\n<p>Một số h&igrave;nh ảnh ghi nhận tại FPT Shop, Viễn Th&ocirc;ng A v&agrave; Hnam Mobile.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/mua-ban-galaxy-a5-2017- no-nuc-05.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/mua-ban-galaxy-a5-2017- no-nuc-06.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/mua-ban-galaxy-a5-2017- no-nuc-07.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/mua-ban-galaxy-a5-2017- no-nuc-08.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/mua-ban-galaxy-a5-2017- no-nuc-09.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/mua-ban-galaxy-a5-2017- no-nuc-10.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/mua-ban-galaxy-a5-2017- no-nuc-11.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/mua-ban-galaxy-a5-2017- no-nuc-12.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/mua-ban-galaxy-a5-2017- no-nuc-13.jpg" style="width:96%" /></p>\r\n', 'images/mua-ban-galaxy-a5-2017- no-nuc-01.jpeg', 10, b'1', 'khach-hang-no-nuc-den-nhan-galaxy-a5-a7-2017-ngay-dau-mo-ban', 'Khách hàng nô nức đến nhận Galaxy A5 / A7 2017 ngày đầu mở bán', 'samsung galaxy, galaxy a5 a7 2017, a5 2017, a7 2017', 'Hôm nay 18/02, bộ đôi Galaxy A5 & A7 đã chính thức lên kệ trên toàn quốc. Theo ghi nhận của CNV, rất nhiều khách hàng đã đến xếp hàng tại FPT Shop, Viễn Thông A, Hnam Mobile, TGDD…', '2017-03-13', 1, b'1'),
(11, 'Hình ảnh buổi Tech Offline bộ đôi Galaxy A 2017 của FPT Shop', 1, '<h2>Bộ đ&ocirc;i Galaxy A5, A7 2017 l&agrave; sản phẩm mới nhất của Samsung v&agrave; buổi offline &ldquo;Chơi trong nước, sợ g&igrave; ướt&rdquo; do FPT Shop tổ chức chiều qua ch&iacute;nh l&agrave; một trong những buổi trải nghiệm sản phẩm ho&agrave;nh tr&aacute;ng&nbsp;tại&nbsp;S&agrave;i G&ograve;n.</h2>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hinh-anh-buoi-tech-offline-bo-doi-galaxy-2017-cua-fpt-shop-01.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hinh-anh-buoi-tech-offline-bo-doi-galaxy-2017-cua-fpt-shop-02.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hinh-anh-buoi-tech-offline-bo-doi-galaxy-2017-cua-fpt-shop-03.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hinh-anh-buoi-tech-offline-bo-doi-galaxy-2017-cua-fpt-shop-04.jpg" style="width:96%" /></p>\r\n\r\n<p>Buổi offline n&agrave;y c&ograve;n ghi dấu ấn đặc biệt với người tham gia với tiết mục trải nghiệm t&iacute;nh năng selfie của Galaxy A 2017 tr&ecirc;n những chiếc moto nước, đ&uacute;ng với chủ đề &ldquo;Chơi trong nước, sợ g&igrave; ướt&rdquo;. Hầu hết c&aacute;c th&agrave;nh vi&ecirc;n tham gia v&agrave;o phần trải nghiệm đặc biệt n&agrave;y đều rất h&agrave;o hứng v&agrave; th&iacute;ch th&uacute;, cảm gi&aacute;c đầy mới lạ v&agrave; v&ocirc; c&ugrave;ng th&uacute; vị.</p>\r\n\r\n<p>B&ecirc;n cạnh rất nhiều qu&agrave; tặng thiết thực v&agrave; thẻ nạp điện thoại được trao cho c&aacute;c bạn c&oacute; c&acirc;u hỏi hay v&agrave; những c&acirc;u trả đ&uacute;ng trong phần t&igrave;m hiểu về sản phẩm Galaxy A 2017, FPT Shop c&ograve;n trao 6 phần qu&agrave; gi&aacute; trị cho c&aacute;c bạn may mắn nhất khi tham gia minigame selfie tr&ecirc;n moto nước, bao gồm: 1 điện thoại Galaxy A5 2017 v&agrave; 5 thẻ nạp điện thoại mệnh gi&aacute; 500.000 đồng.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hinh-anh-buoi-tech-offline-bo-doi-galaxy-2017-cua-fpt-shop-05.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hinh-anh-buoi-tech-offline-bo-doi-galaxy-2017-cua-fpt-shop-06.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hinh-anh-buoi-tech-offline-bo-doi-galaxy-2017-cua-fpt-shop-07.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hinh-anh-buoi-tech-offline-bo-doi-galaxy-2017-cua-fpt-shop-08.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hinh-anh-buoi-tech-offline-bo-doi-galaxy-2017-cua-fpt-shop-09.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hinh-anh-buoi-tech-offline-bo-doi-galaxy-2017-cua-fpt-shop-10.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hinh-anh-buoi-tech-offline-bo-doi-galaxy-2017-cua-fpt-shop-11.jpg" style="width:96%" /></p>\r\n', 'images/hinh-anh-buoi-tech-offline-bo-doi-galaxy-2017-cua-fpt-shop-00.jpg', 28, b'1', 'hinh-anh-buoi-tech-offline-bo-doi-galaxy-2017-cua-fpt-shop', 'Hình ảnh buổi Tech Offline bộ đôi Galaxy A 2017 của FPT Shop', 'Galaxy A5 2017, Galaxy A7 2017, Galaxy A 2017', 'Bộ đôi Galaxy A5, A7 2017 là sản phẩm mới nhất của Samsung và buổi offline “Chơi trong nước, sợ gì ướt” do FPT Shop tổ chức chiều qua chính là một trong những buổi trải nghiệm sản phẩm hoành tráng tại Sài Gòn.', '2017-03-13', 1, b'0'),
(12, 'Lộ cấu hình chi tiết OPPO F3 PLUS sắp ra mắt', 1, '<h2>Theo nhiều nguồn tin r&ograve; rỉ, trong th&aacute;ng 3 n&agrave;y OPPO sẽ ra mắt bộ đ&ocirc;i OPPO F3 v&agrave; OPPO F3 Plus, phi&ecirc;n bản tiếp theo của d&ograve;ng OPPO F1 đang rất được y&ecirc;u th&iacute;ch hiện nay.</h2>\r\n\r\n<p>Trang GSM Arena vừa đăng tải h&igrave;nh ảnh về bảng cấu h&igrave;nh chi tiết của OPPO F3 Plus với những th&ocirc;ng số rất ấn tượng. Nổi bật nhất l&agrave; bộ đ&ocirc;i camera trước 16MP v&agrave; 8MP. Với một cụm camera k&eacute;p, chắc chắn OPPO F3 Plus c&oacute; khả năng chụp ảnh x&oacute;a ph&ocirc;ng bằng camera trước, tăng th&ecirc;m phần &ldquo;b&aacute; đạo&rdquo; cho khả năng selfie vốn đ&atilde; trở th&agrave;nh thương hiệu của OPPO. Camera ch&iacute;nh cũng c&oacute; độ ph&acirc;n giải 16MP nhưng chỉ l&agrave; camera đơn. Điều n&agrave;y kh&ocirc;ng c&oacute; g&igrave; kh&oacute; hiểu v&igrave; d&ograve;ng OPPO F lu&ocirc;n tập trung hơn cho camera trước.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-f3-plus-leak.jpg" style="width:80%" /></p>\r\n\r\n<p style="text-align:center">Cấu h&igrave;nh ch&iacute;nh thức OPPO F3 Plus</p>\r\n\r\n<p>Cấu h&igrave;nh của OPPO F3 Plus cũng thuộc h&agrave;ng cao cấp khi m&aacute;y chạy bộ vi xử l&yacute; Qualcomm Snapdragon 652, một con chip mới rất mạnh về đồ họa của Qualcomm. Bộ nhớ RAM l&agrave; 4GB, bộ nhớ trong 64GB hỗ trợ thẻ nhớ tối đa 256GB. OPPO F3 Plus c&oacute; thiết kế mỏng chỉ 7,35mm, được b&aacute;n ra với hai m&agrave;u l&agrave; đen v&agrave; v&agrave;ng. M&aacute;y sở hữu m&agrave;n h&igrave;nh lớn đến 6 inch, độ ph&acirc;n giải Full HD. Một điểm ấn tượng kh&aacute;c l&agrave; vi&ecirc;n pin dung lượng l&ecirc;n tới 4000 mAh. Điện thoại OPPO từ trước đến nay lu&ocirc;n quản l&yacute; pin rất tốt v&agrave; OPPO F3 Plus hứa hẹn sẽ sử dụng được khoảng 3 ng&agrave;y nhờ vi&ecirc;n pin lớn như vậy.</p>\r\n\r\n<p>R&otilde; r&agrave;ng đ&acirc;y l&agrave; những th&ocirc;ng số rất hấp dẫn cho sản phẩm sắp tới của OPPO. B&ecirc;n cạnh OPPO F3 Plus, ch&uacute;ng ta c&ograve;n một lựa chọn m&agrave;n h&igrave;nh nhỏ hơn l&agrave; OPPO F3 (c&oacute; thể l&agrave; m&agrave;n h&igrave;nh 5,5 inch). Trước đ&oacute; những h&igrave;nh ảnh quảng c&aacute;o tại Trung Quốc đ&atilde; cho thấy G-Dragon l&agrave; đại diện của OPPO F3 v&agrave; OPPO F3 Plus. Theo dự kiến, bộ đ&ocirc;i n&agrave;y sẽ ra mắt ch&iacute;nh thức v&agrave;o ng&agrave;y 23 th&aacute;ng 3 tới.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-f3-plus-leak-1.jpg" style="width:80%" /></p>\r\n\r\n<p style="text-align:center">Những tấm h&igrave;nh quảng c&aacute;o về OPPO F3 v&agrave; OPPO F3 Plus đ&atilde; xuất hiện tại Trung Quốc</p>\r\n', 'images/Oppo-R9S-Plus.jpg', 18, b'1', 'lo-cau-hinh-chi-tiet-oppo-f3-plus-sap-ra-mat', 'Lộ cấu hình chi tiết OPPO F3 PLUS sắp ra mắt', 'oppo, oppo F3 plus', 'Theo nhiều nguồn tin rò rỉ, trong tháng 3 này OPPO sẽ ra mắt bộ đôi OPPO F3 và OPPO F3 Plus, phiên bản tiếp theo của dòng OPPO F1 đang rất được yêu thích hiện nay.', '2017-03-13', 1, b'0'),
(13, 'Mở hộp OPPO F1s 2017: nâng cấp phần cứng, thiết kế giữ nguyên', 3, '<h2>Sau th&agrave;nh c&ocirc;ng với d&ograve;ng OPPO F1s trong năm 2016, OPPO đ&atilde; tung ra phi&ecirc;n bản 2017 với thiết kế kh&ocirc;ng thay đổi nhưng n&acirc;ng cấp về cấu h&igrave;nh.</h2>\r\n\r\n<p>OPPO F1s 2017 vẫn mang tr&ecirc;n m&igrave;nh thiết kế nh&ocirc;m nguy&ecirc;n khối rất đẹp từ đời trước, bạn sẽ kh&ocirc;ng thể nhận ra sự kh&aacute;c biệt n&agrave;o nếu nh&igrave;n v&agrave;o 2 đời m&aacute;y. Gi&aacute; b&aacute;n cho bản 2017 n&agrave;y l&agrave; 6.990.000 đồng, trong khi đ&oacute; bản 2016 l&agrave; 5.990.000 đồng, nghĩa l&agrave; đắt hơn 1 triệu đồng.</p>\r\n\r\n<p>Sau đ&acirc;y l&agrave; một số h&igrave;nh ảnh mở hộp sản phẩm.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-f1s-2017-danh-gia-01.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-f1s-2017-danh-gia-02.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-f1s-2017-danh-gia-03.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-f1s-2017-danh-gia-04.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-f1s-2017-danh-gia-05.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-f1s-2017-danh-gia-06.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-f1s-2017-danh-gia-07.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-f1s-2017-danh-gia-08.jpg" style="width:96%" /></p>\r\n\r\n<p>Như đ&atilde; n&oacute;i b&ecirc;n tr&ecirc;n, điểm kh&aacute;c biệt duy nhất l&agrave; cấu h&igrave;nh v&agrave; tr&ecirc;n thực tế đ&oacute; l&agrave; RAM + bộ nhớ trong. OPPO F1s 2017 được n&acirc;ng cấp RAM l&ecirc;n 4GB v&agrave; bộ nhớ trong l&ecirc;n 64GB (so với 3GB RAM + 32GB bộ nhớ trong tr&ecirc;n đời 2016). Phần c&ograve;n lại vẫn giống như trước bao gồm m&agrave;n h&igrave;nh 5.5 inch HD, hỗ trợ 2 SIM Nano v&agrave; thẻ nhớ ngo&agrave;i, bộ xử l&yacute; MT6750 8 nh&acirc;n, pin 3075mAh, camera sau 13MP v&agrave; camera trước 16MP vốn được đ&aacute;nh gi&aacute; rất cao về khả năng selfie.</p>\r\n\r\n<p>Thiết bị m&igrave;nh chụp trong b&agrave;i c&oacute; m&agrave;u v&agrave;ng v&agrave; v&agrave;ng hồng, phi&ecirc;n bản 2016 c&oacute; m&agrave;u x&aacute;m v&agrave; khắc t&ecirc;n.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-f1s-2017-danh-gia-09.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-f1s-2017-danh-gia-10.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-f1s-2017-danh-gia-11.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-f1s-2017-danh-gia-11.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-f1s-2017-danh-gia-12.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-f1s-2017-danh-gia-13.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-f1s-2017-danh-gia-14.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-f1s-2017-danh-gia-15.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-f1s-2017-danh-gia-16.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-f1s-2017-danh-gia-16.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-f1s-2017-danh-gia-17.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-f1s-2017-danh-gia-18.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-f1s-2017-danh-gia-19.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-f1s-2017-danh-gia-20.jpg" style="width:96%" /></p>\r\n', 'images/oppo-f1s-2017-danh-gia-00.jpg', 3, b'1', 'mo-hop-oppo-f1s-2017-nang-cap-phan-cung-thiet-ke-giu-nguyen', 'Mở hộp OPPO F1s 2017: nâng cấp phần cứng, thiết kế giữ nguyên', 'oppo f1s, f1s 2017', 'Sau thành công với dòng OPPO F1s trong năm 2016, OPPO đã tung ra phiên bản 2017 với thiết kế không thay đổi nhưng nâng cấp về cấu hình.', '2017-03-13', 1, b'1'),
(14, 'Cận cảnh Seoul 7, chiếc smartphone mới dưới 4 triệu đồng!', 3, '<p>Hyundai Seoul 7 l&agrave; d&ograve;ng m&aacute;y tiếp theo được tung ra thị trường trong năm 2017 n&agrave;y, đ&acirc;y l&agrave; sản phẩm c&oacute; thiết kế đẹp với kim loại nguy&ecirc;n khối v&agrave; trang bị cảm biến v&acirc;n tay một chạm ở mặt lưng.</p>\r\n\r\n<p>&Ocirc;ng Nguyễn Huy Cần, Gi&aacute;m đốc c&ocirc;ng ty TNHH-TM Li&ecirc;n Hiệp Mo Bi,&nbsp;đơn vị được nhượng quyền kinh doanh thương hiệu điện thoại di động Hyundai tại Việt Nam&nbsp;chia sẻ:</p>\r\n\r\n<blockquote>\r\n<p>&ldquo;Ch&uacute;ng t&ocirc;i cũng như tập thể l&atilde;nh đạo của Hyundai tr&ecirc;n to&agrave;n quốc cam kết x&acirc;y dựng một c&ocirc;ng ty cung cấp sản phẩm c&oacute; gi&aacute; trị vượt ngo&agrave;i &yacute; nghĩa c&ocirc;ng ty kinh doanh thương mại th&ocirc;ng thường trong t&acirc;m tr&iacute; của kh&aacute;ch h&agrave;ng. Theo ch&uacute;ng t&ocirc;i&nbsp; th&igrave; kh&aacute;ch h&agrave;ng kh&ocirc;ng chỉ đơn giản mua sản phẩm m&agrave; l&agrave; đang c&oacute; một sự lựa chọn th&ocirc;ng minh với một sản phẩm an to&agrave;n, thời trang, đ&aacute;ng tin cậy m&agrave; lại c&oacute; mức gi&aacute; phải chăng.&rdquo;</p>\r\n</blockquote>\r\n\r\n<p>M&aacute;y c&oacute; 3 t&ugrave;y chọn m&agrave;u bao gồm: Silver (bạc), Gold (v&agrave;ng) v&agrave; Rose gold (v&agrave;ng hồng), th&ocirc;ng số kỹ thuật như sau:</p>\r\n\r\n<ul>\r\n <li>Bộ xử l&yacute;: MediaTek MT6735P</li>\r\n  <li>RAM 2GB, bộ nhớ trong 16GB (hỗ trợ mở rộng thẻ nhớ 64GB)</li>\r\n <li>M&agrave;n h&igrave;nh: 5.5 inch HD IPS</li>\r\n  <li>Camera sau: 13MP, camera trước: 5MP</li>\r\n  <li>Pin 3000mAh</li>\r\n  <li>OS: Android 6.0</li>\r\n  <li>Gi&aacute; b&aacute;n: 3.890.000 đồng</li>\r\n</ul>\r\n\r\n<p>CNV hiện đang c&oacute; tr&ecirc;n tay phi&ecirc;n bản thương mại m&agrave;u silver, xin mời c&aacute;c bạn c&ugrave;ng xem qua sản phẩm. Ch&uacute;ng t&ocirc;i sẽ gửi b&agrave;i review đến c&aacute;c bạn trong thời gian tới.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hyundai-Seoul 7-01.jpg" style="width:70%" /></p>\r\n\r\n<p style="text-align:center">Hộp m&aacute;y vẫn giữ thiết kế đơn giản nhưng đẹp</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hyundai-Seoul 7-02.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center">Mặt sau in th&ocirc;ng tin m&aacute;y v&agrave; m&agrave;u</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hyundai-Seoul 7-03.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center">Que chọc sim đi k&egrave;m trong hộp</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hyundai-Seoul 7-04.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center">Phụ kiện, c&aacute;p, tai nghe đ&oacute;ng rất tinh tế</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hyundai-Seoul 7-05.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center">M&aacute;y b&aacute;n ra tặng k&egrave;m &oacute;p lưng silicon</p>\r\n\r\n<p><strong>Một số h&igrave;nh ảnh sản phẩm:</strong></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hyundai-Seoul 7-06.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hyundai-Seoul 7-07.jpg" style="width:80%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hyundai-Seoul 7-08.jpg" style="width:80%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hyundai-Seoul 7-09.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hyundai-Seoul 7-10.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hyundai-Seoul 7-11.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hyundai-Seoul 7-12.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center">&nbsp;</p>\r\n', 'images/hyundai-Seoul 7-00.jpg', 4, b'1', 'can-canh-seoul-7-chiec-smartphone-moi-duoi-4-trieu-dong', 'Cận cảnh Seoul 7, chiếc smartphone mới dưới 4 triệu đồng từ Hyundai', 'can-canh-seoul-7-chiec-smartphone-moi-duoi-4-trieu-dong-tu-hyundai', 'Sau khi tung ra hai chiếc Seoul 5 và 6 thì nhà sản xuất Hyundai smartphone lại tiếp tục ‘rục rịch’ chuẩn bị cho lên kệ dòng sản phẩm mới với mức giá dưới 4 triệu đồng.', '2017-03-13', 1, b'1'),
(15, 'Đã có root Android 7.0 Samsung Galaxy S6 Edge', 4, '<h2>Nếu bạn n&agrave;o muốn vọc m&aacute;y, c&agrave;i Xposed Framework, c&aacute;c tweak,&hellip; th&igrave; phải Root trước nh&eacute;, b&ecirc;n dưới l&agrave; hướng dẫn chi tiết&nbsp;Root Galaxy S6 Edge Android 7.0 Nougat.</h2>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/galaxy-s6-edge-vs-s6-edge-10.jpg" style="width:96%" /></p>\r\n\r\n<p>Đầu ti&ecirc;n c&aacute;c bạn c&agrave;i TWRP Recovery, sau đ&oacute; copy file Magisk Root v&agrave;o bộ nhớ, v&agrave;o Recovery Install file Magisk Root, khởi động lại m&aacute;y l&agrave; xong !!!</p>\r\n\r\n<p><strong>Bước 1:</strong>&nbsp;C&agrave;i TWRP Galaxy S6 Edge Android 7.0<br />\r\n&ndash; Download TWRP:&nbsp;<a href="https://twrp.me/devices/samsunggalaxys6edge.html" target="_blank">https://twrp.me/devices/samsunggalaxys6edge.html</a><br />\r\nXem m&aacute;y model l&agrave; g&igrave; th&igrave; chọn download cho model đ&oacute;, như m&aacute;y m&igrave;nh m&atilde; l&agrave; G925F th&igrave; m&igrave;nh chọn &ldquo;<strong>International GSM (SM-G925F, SM-G925FD, SM-G925I)</strong>&rdquo;</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/galaxy-s6-edge-vs-s6-edge-11.jpg" style="width:96%" /></p>\r\n\r\n<p><strong>Chọn bản TWRP mới nhất:</strong><br />\r\n<img alt="upload_2017-3-10_12-24-47.png" src="http://forum.mobilecity.vn/attachments/upload_2017-3-10_12-24-47-png.424/" /></p>\r\n\r\n<p><strong>Rồi ấn Download:&nbsp;</strong><br />\r\n<img alt="upload_2017-3-10_12-25-12.png" src="http://forum.mobilecity.vn/attachments/upload_2017-3-10_12-25-12-png.425/" /><br />\r\n&ndash; Download v&agrave; c&agrave;i&nbsp;<strong>Driver&nbsp;</strong>cho m&aacute;y t&iacute;nh nếu chưa c&agrave;i:&nbsp;<a href="http://developer.samsung.com/technical-doc/view.do?v=T000000117" target="_blank">SAMSUNG_USB_Driver_for_Mobile_Phones.zip (15.3MB)</a></p>\r\n\r\n<p>&ndash; Download bộ tool&nbsp;<strong>Odin Odin v3.12.3</strong>:&nbsp;<a href="http://www.fshare.vn/file/JJR1JBPDEMMZ" target="_blank">http://www.fshare.vn/file/JJR1JBPDEMMZ</a></p>\r\n\r\n<p>&ndash; Giải n&eacute;n rồi mở Odin l&ecirc;n, chọn mục&nbsp;<strong>AP&nbsp;</strong>rồi mở đến file twrp đ&atilde; tải về (đu&ocirc;i .tar)</p>\r\n\r\n<p>&ndash; Đưa m&aacute;y v&agrave;o&nbsp;<strong>Download mode</strong>, cắm cắp USB với m&aacute;y t&iacute;nh rồi tr&ecirc;n Odin nhấn ph&iacute;m&nbsp;<strong>Start&nbsp;</strong>để flash !!!</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/galaxy-s6-edge-vs-s6-edge-12.jpg" style="width:90%" /></p>\r\n\r\n<p><strong>Bước 2: Install file Magisk root để Root m&aacute;y</strong><br />\r\n&ndash; Download file:&nbsp;<a href="https://www.androidfilehost.com/?fid=529152257862708983" target="_blank">Magisk_Root_Android_Nougat.zip</a><br />\r\n&ndash; Copy v&agrave;o bộ nhớ trong m&aacute;y<br />\r\n&ndash; V&agrave;o Recovery, chọn Install file&nbsp;<a href="https://www.androidfilehost.com/?fid=529152257862708983" target="_blank">Magisk_Root_Android_Nougat.zip</a><br />\r\n&ndash; Đợi c&agrave;i đặt xong nhấn Reboot system để khởi động lại m&aacute;y.<br />\r\n&ndash; M&aacute;y mở l&ecirc;n c&aacute;c bạn t&igrave;m mở ứng dụng&nbsp;<strong>Magisk Mannager&nbsp;</strong>để quản l&yacute; quyền Root</p>\r\n\r\n<p>Ch&uacute;c bạn th&agrave;nh c&ocirc;ng!</p>\r\n', 'images/galaxy-s6-edge-vs-s6-edge-9.jpg', 9, b'1', 'da-co-root-android-70-samsung-galaxy-s6-edge', 'Đã có root Android 7.0 Samsung Galaxy S6 Edge', 'galaxy s6 edge', 'Nếu bạn nào muốn vọc máy, cài Xposed Framework, các tweak,… thì phải Root trước nhé, bên dưới là hướng dẫn chi tiết Root Galaxy S6 Edge Android 7.0 Nougat.', '2017-03-16', 1, b'1');
INSERT INTO `tblbaiviet` (`idbv`, `tenbv`, `idcm`, `noidung`, `thumb`, `luotxem`, `hienthi`, `linkbv`, `tieude`, `tukhoa`, `mota`, `ngaydang`, `iduser`, `tinnoibac`) VALUES
(16, 'Mời tải ứng dụng iOS miễn phí ngày 10/3 ', 3, '<h2>Như th&ocirc;ng lệ, C&ocirc;ng Nghệ Việt xin&nbsp;gửi đến qu&yacute; bạn đọc ứng dụng iOS miễn ph&iacute; trong thời gian ngắn. C&aacute;c bạn n&ecirc;n nhanh tay tải về trước khi t&iacute;nh ph&iacute; trở lại v&agrave; nhớ đổi Apple ID sang v&ugrave;ng Mỹ nh&eacute;.</h2>\r\n\r\n<p><em>(lưu &yacute; nhỏ tại thời điểm viết b&agrave;i, c&aacute;c ứng dụng được giới thiệu đều miễn ph&iacute;)</em></p>\r\n\r\n<h3><a href="https://itunes.apple.com/us/app/danmaku-unlimited-2-bullet-hell-shmup/id580663396?mt=8">* Danmaku Unlimited 2&ndash;$5</a>&nbsp;</h3>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/iOS-free-2.jpeg" style="width:60%" /></p>\r\n\r\n<p>Danmaku Unlimited 2 l&agrave;&nbsp;game bắn s&uacute;ng&nbsp;bullet hell shooter cổ điển rất hấp dẫn, lần đầu ti&ecirc;n được miễn ph&iacute; cho người d&ugrave;ng iOS</p>\r\n\r\n<p><a href="https://itunes.apple.com/us/app/danmaku-unlimited-2-bullet-hell-shmup/id580663396?mt=8"><strong>Download Danmaku Unlimited 2</strong></a></p>\r\n\r\n<p style="text-align:center"><strong>=============================================</strong></p>\r\n\r\n<h3><a href="https://itunes.apple.com/us/app/love-you-to-bits/id941057494?mt=8">* Love You to Bits&ndash;$5</a></h3>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/iOS-free-3.jpeg" style="width:60%" /></p>\r\n\r\n<p>Love You to Bits l&agrave;&nbsp;game&nbsp;puzzle với đồ họa r&aacute;t dễ thương, nội dung thu h&uacute;t người chơi cũng lần đầu ti&ecirc;n được giảm gi&aacute; cho người d&ugrave;ng iOS</p>\r\n\r\n<p><a href="https://itunes.apple.com/us/app/love-you-to-bits/id941057494?mt=8"><strong>Download Love You to Bits</strong></a></p>\r\n\r\n<p style="text-align:center"><strong>=============================================</strong></p>\r\n\r\n<h3><a href="https://itunes.apple.com/us/app/quick-fit-7-minute-workout-abs-and-yoga/id783214985?mt=8">* Quick Fit&ndash;$2</a></h3>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/iOS-free-4.jpeg" style="width:60%" /></p>\r\n\r\n<p>Quick Fit l&agrave; ứng dụng hướng dẫn bạn tập thể dục thể thao, chỉ cần 7 ph&uacute;t một ng&agrave;y để c&oacute; th&acirc;n h&igrave;nh đẹp khỏe khoắn</p>\r\n\r\n<p><a href="https://itunes.apple.com/us/app/quick-fit-7-minute-workout-abs-and-yoga/id783214985?mt=8"><strong>Download Quick Fit</strong></a></p>\r\n\r\n<p style="text-align:center"><strong>=============================================</strong></p>\r\n\r\n<h3><a href="https://itunes.apple.com/us/app/guide-for-world-of-tanks/id469390215?mt=8">* Guide for World of Tanks&ndash;$2</a></h3>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/iOS-free-5.jpeg" style="width:60%" /></p>\r\n\r\n<p>Guide for World of Tanks l&agrave; ứng dụng hướng dẫn bạn c&aacute;ch chơi, chọn s&uacute;ng ống, vũ kh&iacute; trong tr&ograve; bắn xe tăng</p>\r\n\r\n<p><a href="https://itunes.apple.com/us/app/guide-for-world-of-tanks/id469390215?mt=8"><strong>Download Guide for World of Tanks</strong></a></p>\r\n\r\n<p style="text-align:center"><strong>=============================================</strong></p>\r\n\r\n<h3><a href="https://itunes.apple.com/us/app/color-accent/id453170202?mt=8">* Color Accent&ndash;$1</a></h3>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/iOS-free-6.jpeg" style="width:60%" /></p>\r\n\r\n<p>Color Accent l&agrave; ứng dụng chỉnh cho bức ảnh trắng đen hết v&agrave; chỉ c&ograve;n chừa m&agrave;u ở v&ugrave;ng bạn chọn, l&agrave;m nổi bật chủ thể bạn cần</p>\r\n\r\n<p><a href="https://itunes.apple.com/us/app/color-accent/id453170202?mt=8"><strong>Download Color Accent</strong></a></p>\r\n\r\n<p style="text-align:center"><strong>=============================================</strong></p>\r\n\r\n<h3><a href="https://itunes.apple.com/us/app/top-dog-farmyard-adventures/id367309144?mt=8">* Top Dog&ndash;$1</a></h3>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/iOS-free-7.jpeg" style="width:60%" /></p>\r\n\r\n<p>Top Dog l&agrave;&nbsp;game vui nhộn ph&ugrave; hợp mọi lứa tuổi nhưng c&aacute;c b&eacute; vẫn l&agrave; th&iacute;ch hơn cả với ch&uacute; ch&oacute; dễ thương tinh nghịch</p>\r\n\r\n<p><a href="https://itunes.apple.com/us/app/top-dog-farmyard-adventures/id367309144?mt=8"><strong>Download Top Dog</strong></a></p>\r\n\r\n<p style="text-align:center"><strong>=============================================</strong></p>\r\n\r\n<h3><a href="https://itunes.apple.com/us/app/giffiti-make-gifs-by-adding-animated-stickers-funny/id1028641039?mt=8">* Giffiti&ndash;$1</a></h3>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/iOS-free-8.jpeg" style="width:60%" /></p>\r\n\r\n<p>Giffiti l&agrave; ứng dụng th&ecirc;m ảnh động (sticker) v&agrave;o bức h&igrave;nh của bạn</p>\r\n\r\n<p><a href="https://itunes.apple.com/us/app/giffiti-make-gifs-by-adding-animated-stickers-funny/id1028641039?mt=8"><strong>Download Giffiti</strong></a></p>\r\n\r\n<p style="text-align:center"><strong>=============================================</strong></p>\r\n\r\n<h3><a href="https://itunes.apple.com/us/app/mybrushes-pro-draw-paint-sketch/id557477822?mt=8&amp;ign-mpt=uo%3D4">* MyBrushes Pro&ndash;$5</a></h3>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/iOS-free-9.jpeg" style="width:60%" /></p>\r\n\r\n<p>MyBrushes Pro l&agrave; ứng dụng tập hợp c&aacute;c c&ocirc;ng cụ hữu &iacute;ch cho s&aacute;ng t&aacute;c vẽ&nbsp;vời, ph&aacute;c họa, phối m&agrave;u, rất th&iacute;ch hợp cho người y&ecirc;u hội họa, kiến tr&uacute;c</p>\r\n\r\n<p><strong><a href="https://itunes.apple.com/us/app/mybrushes-pro-draw-paint-sketch/id557477822?mt=8&amp;ign-mpt=uo%3D4">Download MyBrushes Pro</a></strong></p>\r\n\r\n<p style="text-align:center"><strong>=============================================</strong></p>\r\n\r\n<h3><a href="https://itunes.apple.com/us/app/sketch-calendar-doodle-and-paint-your-day/id950133189?mt=8">* Sketch Calendar&ndash;$4</a></h3>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/iOS-free-10.jpeg" style="width:60%" /></p>\r\n\r\n<p>Sketch Calendar l&agrave; ứng dụng ghi ch&uacute; l&ecirc;n lịch kh&aacute; th&uacute; vị, ngo&agrave;i việc ghi ch&uacute; bạn c&ograve;n c&oacute; thể vẽ l&ecirc;n đ&oacute;, th&ecirc;m k&iacute; hiệu như đang ghi tr&ecirc;n bảng đen vậy</p>\r\n\r\n<p><a href="https://itunes.apple.com/us/app/sketch-calendar-doodle-and-paint-your-day/id950133189?mt=8"><strong>Download Sketch Calendar</strong></a></p>\r\n\r\n<p style="text-align:center"><strong>=============================================</strong></p>\r\n\r\n<h3><a href="https://itunes.apple.com/us/app/battery-doc-professional-care-and-information/id578400895?mt=8">* Battery Doc&ndash;$2</a></h3>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/iOS-free-11.jpeg" style="width:60%" /></p>\r\n\r\n<p>Battery Doc l&agrave; ứng dụng kiểm tra đ&aacute;nh gi&aacute; sưc khỏe vi&ecirc;n pin của bạn một c&aacute;ch to&agrave;n vẹn nhất</p>\r\n\r\n<p><a href="https://itunes.apple.com/us/app/battery-doc-professional-care-and-information/id578400895?mt=8"><strong>Download Battery Doc</strong></a></p>\r\n\r\n<p style="text-align:center"><strong>=============================================</strong></p>\r\n\r\n<h3><a href="https://itunes.apple.com/us/app/pro-collage-creator-add-beautiful-text-artwork-to-photos/id1126647014?mt=8">* Pro Collage Creator&ndash;$2</a></h3>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/iOS-free-12.jpeg" style="width:60%" /></p>\r\n\r\n<p>Pro Collage Creator l&agrave; ứng dụng chỉnh sửa ảnh, ngo&agrave;i ra bạn c&oacute; thể th&ecirc;m c&aacute;c bộ sticker rất đẹp cho ảnh</p>\r\n\r\n<p><a href="https://itunes.apple.com/us/app/pro-collage-creator-add-beautiful-text-artwork-to-photos/id1126647014?mt=8"><strong>Download Pro Collage Creator</strong></a></p>\r\n\r\n<p style="text-align:center"><strong>=============================================</strong></p>\r\n', 'images/iOS-free-1.jpeg', 70, b'1', 'moi-tai-ung-dung-ios-mien-phi-ngay-103', 'Mời tải ứng dụng iOS miễn phí ngày 10/3 ', 'free ios, ios apps free', 'Như thông lệ, Công Nghệ Việt xin gửi đến quý bạn đọc ứng dụng iOS miễn phí trong thời gian ngắn. Các bạn nên nhanh tay tải về trước khi tính phí trở lại và nhớ đổi Apple ID sang vùng Mỹ nhé...', '2017-03-16', 3, b'1'),
(17, 'FPT Shop khai trương 80 khu trải nghiệm Macbook đạt chuẩn Apple ', 2, '<h2>Từ th&aacute;ng 3/2017, FPT Shop ch&iacute;nh thức khai trương 80 khu trải nghiệm Macbook đạt chuẩn Apple to&agrave;n cầu. Đồng thời, FPT Shop cũng tặng qu&agrave; tổng trị gi&aacute; đến 1,6 tỷ đồng cho tất cả kh&aacute;ch h&agrave;ng chọn mua Macbook.</h2>\r\n\r\n<p>Theo đ&oacute;, bạn sẽ được trải nghiệm c&aacute;c sản phẩm m&aacute;y t&iacute;nh x&aacute;ch tay trứ danh của Apple tại 80 khu trải nghiệm Macbook đạt chuẩn to&agrave;n cầu của Apple thuộc hệ thống FPT Shop. &nbsp;Đ&acirc;y thật sự l&agrave; một tin vui với người d&ugrave;ng tại Việt Nam n&oacute;i chung v&agrave; c&aacute;c iFan n&oacute;i ri&ecirc;ng khi c&oacute; thể thỏa th&iacute;ch trải nghiệm c&aacute;c d&ograve;ng Macbook mới nhất của &ldquo;nh&agrave; T&aacute;o&rdquo; để dễ d&agrave;ng đưa ra quyết định sở hữu một trong những sản phẩm c&ocirc;ng nghệ h&agrave;ng đầu n&agrave;y.</p>\r\n\r\n<p>Khu trải nghiệm Macbook theo chuẩn Apple đ&atilde; được &aacute;p dụng triển khai tại 80 cửa h&agrave;ng FPT Shop tr&ecirc;n to&agrave;n quốc. Đến với c&aacute;c cửa h&agrave;ng n&agrave;y, bạn kh&ocirc;ng chỉ được trải nghiệm c&aacute;c sản phẩm mới một c&aacute;ch nhanh nhất m&agrave; c&ograve;n được tư vấn đầy đủ trước khi quyết định mua. Mỗi khu vực trưng b&agrave;y v&agrave; b&aacute;n sản phẩm đều c&oacute; đội ngũ chuy&ecirc;n vi&ecirc;n tư vấn v&agrave; cố vấn kỹ thuật được đ&agrave;o tạo b&agrave;i bản, kỹ lưỡng bởi c&aacute;c chuy&ecirc;n gia Apple, sẽ tư vấn cho kh&aacute;ch h&agrave;ng một c&aacute;ch chuy&ecirc;n nghiệp nhất về c&aacute;c sản phẩm, ứng dụng v&agrave; c&ocirc;ng nghệ mới nhất từ Apple. Bạn c&oacute; thể t&igrave;m thấy th&ocirc;ng tin&nbsp; điểm trải nghiệm gần nhất ngay tại website:&nbsp;<a href="http://fptshop.com.vn/diem-trung-bay-MacBook-uy-quyen">http://fptshop.com.vn/diem-trung-bay-MacBook-uy-quyen</a></p>\r\n\r\n<p>Kể từ th&aacute;ng 8/2016, FPT Shop đ&atilde; ch&iacute;nh thức trở th&agrave;nh đại l&yacute; ủy quyền cao cấp nhất của Apple tại Việt Nam, b&aacute;n đầy đủ c&aacute;c sản phẩm của Apple bao gồm: iPhone, iPad, Macbook v&agrave; Apple Watch. Việc mở khu trải nghiệm Macbook theo đ&uacute;ng chuẩn Apple to&agrave;n cầu l&agrave; một trong những nỗ lực của FPT Shop nhằm mang những trải nghiệm tối ưu nhất đến với c&aacute;c kh&aacute;ch h&agrave;ng của m&igrave;nh khi chọn tham quan, mua sắm tại hệ thống n&agrave;y th&ocirc;ng qua khu trưng b&agrave;y sản phẩm được thiết kế theo đ&uacute;ng chuẩn cao cấp của Apple cũng như dịch vụ hậu m&atilde;i 6 sao.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/khuyen-mai-pft-02.png" style="width:80%" /></p>\r\n\r\n<p style="text-align:center">Kể từ th&aacute;ng 3/2017, kh&aacute;ch h&agrave;ng đ&atilde; c&oacute; thể trải nghiệm c&aacute;c sản phẩm Macbook ch&iacute;nh h&atilde;ng tại 80 khu trưng b&agrave;y Macbook đạt chuẩn Apple to&agrave;n cầu của FPT Shop</p>\r\n\r\n<p><strong>100% tr&uacute;ng qu&agrave; trị gi&aacute; đến 1,6 tỷ khi mua Macbook tại FPT Shop</strong></p>\r\n\r\n<p>Nh&acirc;n sự kiện FPT Shop ch&iacute;nh thức mở 80 khu trải nghiệm Macbook theo chuẩn Apple to&agrave;n cầu, FPT Shop sẽ tặng qu&agrave; với tổng trị gi&aacute; đến 1,6 tỷ đồng cho tất cả kh&aacute;ch h&agrave;ng chọn mua Macbook.</p>\r\n\r\n<p>Từ nay đến ng&agrave;y 31/3, chọn mua Macbook tại FPT Shop, bạn sẽ c&oacute; 100% cơ hội tr&uacute;ng 1 trong 4 m&oacute;n qu&agrave; gi&aacute; trị bao gồm: m&agrave;n h&igrave;nh LG 27inch 4K trị gi&aacute; 15.999.000 đồng, Apple Watch S1 Sport trị gi&aacute; 8.999.000 đồng, Apple TV Gen3 trị gi&aacute; 1.999.000 đồng v&agrave; bộ Microsoft Office 365 trị gi&aacute; 1.300.000 đồng.</p>\r\n\r\n<p>Thiết thực hơn nữa, c&aacute;c t&iacute;n đồ T&aacute;o khuyết sẽ dễ d&agrave;ng sở hữu được chiếc Macbook mơ ước với khoản trả trước chỉ 50% gi&aacute; m&aacute;y v&agrave; hưởng 0% l&atilde;i suất trong thời gian trả g&oacute;p, kỳ hạn đến 8 th&aacute;ng. Đồng thời, bạn sẽ c&oacute; 100% cơ hội được tặng đến 10.000.000 đồng khi mua trả g&oacute;p Macbook tại FPT Shop trong th&aacute;ng 3 n&agrave;y.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/khuyen-mai-pft-01.png" style="width:90%" /></p>\r\n\r\n<p style="text-align:center">Nh&acirc;n sự kiện khai trương 80 khu trưng b&agrave;y Macbook đạt chuẩn Apple to&agrave;n cầu, FPT Shop sẽ d&agrave;nh nhiều qu&agrave; tặng với tổng trị gi&aacute; đến 1,6 tỷ đồng cho c&aacute;c kh&aacute;ch h&agrave;ng chọn mua Macbook</p>\r\n', 'images/khuyen-mai-pft-01.png', 20, b'1', 'fpt-shop-chinh-thuc-khai-truong-80-khu-trai-nghiem-macbook-dat-chuan-apple-toan-cau', 'FPT Shop chính thức khai trương 80 khu trải nghiệm Macbook đạt chuẩn Apple toàn cầu', 'fpt khai truong', 'Từ tháng 3/2017, FPT Shop chính thức khai trương 80 khu trải nghiệm Macbook đạt chuẩn Apple toàn cầu. Đồng thời, FPT Shop cũng tặng quà tổng trị giá đến 1,6 tỷ đồng cho tất cả khách hàng mua Macbook....', '2017-03-16', 2, b'1'),
(19, 'Chán giao diện cũ, hãy đổi mới Facebook', 4, '<h2>Kh&ocirc;ng những đẹp, &ldquo;lớp &aacute;o&rdquo; mới n&agrave;y c&ograve;n gi&uacute;p điện thoại của bạn đỡ hao pin hơn hẳn.</h2>\r\n\r\n<p>Giao diện ứng dụng Facebook tr&ecirc;n điện thoại d&ugrave;ng m&atilde;i cũng ch&aacute;n, tại sao kh&ocirc;ng thay &ldquo;lớp &aacute;o&rdquo; cho n&oacute; để mang đến sự mới mẻ v&agrave; th&iacute;ch th&uacute; hơn? Chỉ với v&agrave;i bước sau đ&acirc;y, bạn sẽ mang lại diện mạo mới cho ứng dụng Facebook của m&igrave;nh.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/app-facebook-mini-2.png" style="width:96%" /></p>\r\n\r\n<p>Đầu ti&ecirc;n bạn tải về ứng dụng MaterialFBook theo&nbsp;<a href="https://drive.google.com/file/d/0B57M-fB75XUGYnZaZ2lrTFZYcWM/view?usp=sharing" rel="nofollow" target="_blank" title="đường link này">đường link n&agrave;y</a>, lưu &yacute; rằng chỉ &aacute;p dụng cho điện thoại chạy hệ điều h&agrave;nh Android th&ocirc;i nh&eacute;.</p>\r\n\r\n<p>Sau khi điện thoại b&aacute;o tải xong, bạn chọn v&agrave;o tập tin v&agrave; để ứng dụng được c&agrave;i đặt. Nếu m&aacute;y kh&ocirc;ng cho ph&eacute;p c&agrave;i, h&atilde;y v&agrave;o phần&nbsp;<strong><em>Settings &gt; Security &gt; bật Unknown Sources l&ecirc;n.</em></strong></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/app-facebook-mini-3.png" style="width:90%" /></p>\r\n\r\n<p>Sau khi c&agrave;i xong l&agrave; bạn đ&atilde; c&oacute; giao diện Facebook mới theo phong c&aacute;ch phẳng thế n&agrave;y đ&acirc;y:</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/app-facebook-mini-4.png" style="width:80%" /></p>\r\n\r\n<p>Đừng vội, đ&acirc;y chỉ mới l&agrave; bước khởi đầu, bạn c&oacute; thể v&agrave;o Settings của ứng dụng để thay đổi nhiều kiểu thiết kế cũng như m&agrave;u sắc giao diện t&ugrave;y theo sở th&iacute;ch của m&igrave;nh.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/app-facebook-mini-5.png" style="width:90%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/app-facebook-mini-6.png" style="width:90%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/app-facebook-mini-7.png" style="width:90%" /></p>\r\n\r\n<p>Ngo&agrave;i ra, MaterialFBook c&ograve;n cho ph&eacute;p bạn chat thẳng từ trong ứng dụng m&agrave; kh&ocirc;ng cần phải c&agrave;i đặt Messenger nữa. Đối với những ai th&iacute;ch sự gọn nhẹ hoặc m&aacute;y c&oacute; cấu h&igrave;nh yếu th&igrave; đ&acirc;y l&agrave; sự lựa chọn tuyệt vời.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/app-facebook-mini-8.png" style="width:90%" /></p>\r\n\r\n<p>Nh&igrave;n chung, qua một ng&agrave;y d&ugrave;ng thử, ch&uacute;ng t&ocirc;i nhận thấy ứng dụng n&agrave;y hoạt động kh&aacute; ổn định v&agrave; chưa thấy bất kỳ lỗi n&agrave;o xảy ra. B&ecirc;n cạnh đ&oacute;, MaterialFBook kh&aacute; nhẹ v&agrave; kh&ocirc;ng g&acirc;y tốn nhiều RAM đ&atilde; gi&uacute;p m&aacute;y phần n&agrave;o &ldquo;trụ&rdquo; pin được l&acirc;u hơn so với việc d&ugrave;ng ứng dụng gốc. Những &ldquo;con nghiện&rdquo; Facebook giờ đ&acirc;y c&oacute; thể v&ocirc; tư sống ảo nhiều hơn m&agrave; chẳng lo hao pin rồi nh&eacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align:center">&nbsp;</p>\r\n', 'images/app-facebook-mini-1.png', 16, b'1', 'chan-giao-dien-cu-hay-doi-moi-facebook-theo-phong-cach-thiet-ke-phang-tuyet-dep-nay', 'Chán giao diện cũ, hãy đổi mới Facebook theo phong cách thiết kế phẳng tuyệt đẹp này', 'app facebook', 'Không những đẹp, “lớp áo” mới này còn giúp điện thoại của bạn đỡ hao pin hơn hẳn...', '2017-03-16', 1, b'0'),
(20, 'Origin tặng game Syberia II trị giá $13, nhanh tay tải về', 4, '<h2>Mới đ&acirc;y, Origin đ&atilde; thay đổi tựa game Mass Effect 2 trong đợt tặng lần rồi bằng tựa game kh&aacute;c, đ&oacute; l&agrave; Syberia II, đ&acirc;y cũng l&agrave; một tựa game thuộc h&agrave;ng &ldquo;cổ&rdquo; rồi n&ecirc;n bạn c&oacute; thể thoải m&aacute;i tải về chơi m&agrave; kh&ocirc;ng cần lo lắng m&aacute;y m&igrave;nh c&oacute; ph&ugrave; hợp cấu h&igrave;nh kh&ocirc;ng.</h2>\r\n\r\n<blockquote>\r\n<p>Giới thiệu qua một ch&uacute;t về Syberia 2. Game tiếp tục cho người chơi theo ch&acirc;n Kate Walker &ndash; một nữ luật sư xinh đẹp đ&atilde; qu&aacute; ch&aacute;n gh&eacute;t c&ocirc;ng việc văn ph&ograve;ng n&ecirc;n quyết định thực hiện chuyến th&aacute;m hiểm tới v&ugrave;ng đất giả tưởng Syberia lạnh gi&aacute;, nơi lo&agrave;i voi ma m&uacute;t cổ đại vẫn đang sinh sống. Syberia 2 c&oacute; lối chơi giải đố đơn giản v&agrave; nhẹ nh&agrave;ng, thậm ch&iacute; bạn c&ograve;n kh&ocirc;ng thể&hellip; chết như trong những tựa game kh&aacute;c n&ecirc;n rất ph&ugrave; hợp với mục đ&iacute;ch giải tr&iacute; khi mệt mỏi, như trước khi đi ngủ chẳng hạn. Game nhận được đ&aacute;nh gi&aacute; kh&aacute; cao từ c&aacute;c tạp ch&iacute; game, đạt điểm số trung b&igrave;nh 82/100 theo tổng hợp của Metacritic.</p>\r\n\r\n<p>-GameK</p>\r\n</blockquote>\r\n\r\n<p>Kh&ocirc;ng r&otilde; khi n&agrave;o Origin sẽ ngưng tặng game Syberia II, nhưng theo th&ocirc;ng lệ của Origin từ xưa giờ th&igrave; thường một game trong&nbsp;<strong>On The House&nbsp;</strong>kết th&uacute;c tặng th&igrave; sẽ c&oacute; tựa game kh&aacute;c được thay thế, v&igrave; vậy, bạn h&atilde;y theo d&otilde;i thường xuy&ecirc;n để c&oacute; thể c&oacute; được những tựa game bản quyền miễn ph&iacute; đến từ nh&agrave; sản xuất EA nh&eacute;.</p>\r\n\r\n<p>Để nhận game, bạn h&atilde;y truy cập v&agrave;o&nbsp;<a href="https://www.origin.com/sgp/en-us/store/syberia/syberia-2/standard-edition" target="_blank">đ&acirc;y.</a></p>\r\n\r\n<p>Nếu chưa c&oacute; t&agrave;i khoản Origin th&igrave; bạn h&atilde;y đăng k&yacute; nhanh cho m&igrave;nh một t&agrave;i khoản nh&eacute;. Tiếp theo, bạn h&atilde;y nhấn&nbsp;<strong>Add to Game Library</strong>&nbsp;trong &ocirc; m&agrave;u cam như h&igrave;nh dưới.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/origin-tang-game-syberia-ii-tri-gia-13-nhanh-tay-tai-ve-02.jpg" style="width:96%" /></p>\r\n\r\n<p>Như vậy l&agrave; xong, giờ bạn đ&atilde; c&oacute; game Syberia II trong Library của m&igrave;nh.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/origin-tang-game-syberia-ii-tri-gia-13-nhanh-tay-tai-ve-03.jpg" style="width:96%" /></p>\r\n\r\n<p>Nếu chưa tải Origin th&igrave; bạn h&atilde;y tiếp tục bấm v&agrave;o&nbsp;<strong>Install Origin &amp; Play</strong>, để tải Origin về để c&oacute; thể c&agrave;i đặt game.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/origin-tang-game-syberia-ii-tri-gia-13-nhanh-tay-tai-ve-04.jpg" style="width:96%" /></p>\r\n\r\n<p>&nbsp;V&agrave; cuối c&ugrave;ng l&agrave; kiểm tra lại game đ&atilde; c&oacute; trong mục&nbsp;<strong>My Game Library&nbsp;</strong>của m&igrave;nh chưa l&agrave; xong.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/origin-tang-game-syberia-ii-tri-gia-13-nhanh-tay-tai-ve-05.jpg" style="width:96%" /></p>\r\n\r\n<p><strong>Ch&uacute;c c&aacute;c bạn th&agrave;nh c&ocirc;ng!</strong></p>\r\n', 'images/origin-tang-game-syberia-ii-tri-gia-13-nhanh-tay-tai-ve-01.jpg', 6, b'0', 'origin-tang-game-syberia-ii-tri-gia-13-nhanh-tay-tai-ve', 'Origin tặng game Syberia II trị giá $13, nhanh tay tải về', 'Syberia II', 'Mới đây, Origin đã thay đổi tựa game Mass Effect 2 trong đợt tặng lần rồi bằng tựa game khác, đó là Syberia II, đây cũng là một tựa game thuộc hàng “cổ” rồi nên bạn có thể thoải mái tải về chơi mà không cần lo lắng...', '2017-03-16', 1, b'0'),
(21, 'OPPO F3 Plus cho đặt trước từ ngày 24/3, tặng bộ quà trị giá 2,150,000 đồng', 3, '<h2>Từ ng&agrave;y 24/3 đến hết ng&agrave;y 31/3/2017, khi đặt mua trước sản phẩm F3 Plus bạn sẽ nhận được bộ qu&agrave; tặng trị gi&aacute; 2,150,000 đồng</h2>\r\n\r\n<p>Bộ qu&agrave; tặng n&agrave;y c&oacute; trị gi&aacute; 2,150,000 đồng, bao gồm 2 thiết bị cực &ldquo;chất&rdquo; v&agrave; tiện lợi: tai nghe Bluetooth thời trang v&agrave; v&ograve;ng đeo tay H-band c&aacute; t&iacute;nh. Ngo&agrave;i ra,&nbsp;<strong>khi đến nhận m&aacute;y tại cửa h&agrave;ng v&agrave;o ng&agrave;y 1/4/2017</strong>, kh&aacute;ch h&agrave;ng c&ograve;n được ưu đ&atilde;i th&ecirc;m một g&oacute;i bảo h&agrave;nh cho m&agrave;n h&igrave;nh (kể cả bị rơi vỡ). G&oacute;i bảo h&agrave;nh đặc biệt n&agrave;y cũng được &aacute;p d&ugrave;ng cho nh&oacute;m kh&aacute;ch h&agrave;ng mua h&agrave;ng trực tiếp trong ng&agrave;y h&ocirc;m đ&oacute;, d&ugrave; kh&ocirc;ng tham gia chương tr&igrave;nh đặt h&agrave;ng trước.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-f3-plus-dat-hang-02.jpg" style="width:80%" /></p>\r\n\r\n<p>Chi tiết:&nbsp;<a href="http://oppomobile.vn/oppo-f3-plus-countdown">http://oppomobile.vn/oppo-f3-plus-countdown</a></p>\r\n\r\n<p>OPPO F3 Plus l&agrave; sản phẩm đang được mong đợi sẽ ra mắt v&agrave;o c&ugrave;ng ng&agrave;y 23/3 n&agrave;y. Theo GSM Arena, sản phẩm n&agrave;y c&oacute; cấu h&igrave;nh gồm:</p>\r\n\r\n<ul style="margin-left:40px">\r\n  <li>Camera selfie k&eacute;p</li>\r\n <li>C&oacute; cảm biến v&acirc;n tay</li>\r\n <li>Sạc nhanh VOOC</li>\r\n <li>Vi xử l&yacute; l&otilde;i 8 với 4GB RAM &amp; 64GB ROM.</li>\r\n</ul>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-f3-plus-dat-hang-03.jpg" style="width:80%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-f3-plus-dat-hang-04.jpg" style="width:70%" /></p>\r\n\r\n<p>Bộ sản phẩm qu&agrave; tặng khi đặt mua F3 Plus:</p>\r\n\r\n<p style="margin-left:40px"><strong>1. TAI NGHE THỜI TRANG TEKIN BLUETOOTH 4.1</strong></p>\r\n\r\n<ul style="margin-left:40px">\r\n <li>THIẾT KẾ SANG TRỌNG, HIỆN ĐẠI Tai nghe Bluetooth TEKIN l&agrave; d&ograve;ng sản phẩm cao cấp với thiết kế full size, m&agrave;ng bọc loa v&agrave; th&acirc;n tai nghe l&agrave;m bằng da cao cấp, mang lại cảm gi&aacute;c thoải m&aacute;i cho người nghe v&agrave; sự sang trọng, đẳng cấp. Ph&iacute;a tr&ecirc;n tai nghe c&oacute; thiết kế r&atilde;nh trượt để nới rộng hoặc co hẹp lại khung cho&agrave;ng đầu để đảm bảo độ vừa vặn v&agrave; thoảng m&aacute;i khi đeo, ph&ugrave; hợp với hầu hết k&iacute;ch thước đầu của mọi người.</li>\r\n  <li>C&Ocirc;NG NGHỆ &Acirc;M THANH CỰC CHẤT M&agrave;ng loa được l&agrave;m từ nam ch&acirc;m neodymium mang lại chất lượng &acirc;m thanh trung thực v&agrave; mạnh mẽ. Dải &acirc;m thanh lớn, th&iacute;ch hợp để nghe với nhiều d&ograve;ng nhạc kh&aacute;c nhau, cho bạn trải nghiệm &acirc;m thanh cực chất nhất. B&ecirc;n cạnh đ&oacute;, sản phẩm c&ograve;n được thiết kế đặc biệt với chất liệu giảm &acirc;m nhằm đảm bảo thể hiện tốt nhất tiếng bass.</li>\r\n <li>T&Iacute;CH HỢP C&Aacute;C PH&Iacute;M ĐIỀU CHỈNH V&Agrave; CỔNG SẠC B&ecirc;n cạnh tai nghe được t&iacute;ch hợp những ph&iacute;m chức năng cơ bản để mở nguồn, kết nối bluetooth, tăng giảm &acirc;m lượng&hellip; để bạn c&oacute; thể dễ d&agrave;ng thao t&aacute;c hơn. Ngo&agrave;i ra tai nghe TEKIN c&ograve;n trang bị cổng sạc Micro USB khi bạn sử dụng chức năng Bluetooth v&agrave; một jack cắm tai nghe nếu bạn muốn sử dụng như tai nghe c&oacute; d&acirc;y. &bull; C&Ocirc;NG NGHỆ BLUETOOTH 4.1 Trang bị c&ocirc;ng nghệ Bluetooth 4.1 tương th&iacute;ch hầu hết với c&aacute;c thiết bị di động hiện nay cho ph&eacute;p bạn dễ d&agrave;ng kết nối v&agrave; chơi nhạc mọi l&uacute;c mọi nơi.</li>\r\n <li>TH&Ocirc;NG SỐ KỸ THUẬT Phi&ecirc;n bản Bluetooth: Bluetooth 4.1, APT-X Tần số nghe: 20HZ-20.000HZ Tỉ lệ nguồn: 20mW SNR: &gt;=90dB THD: &lt;=3% Thời gian nghe: 12 giờ Thời gian sạc: 2 giờ K&iacute;ch thước: 156.5x179x66.5mm Trọng lượng: 134g</li>\r\n</ul>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-f3-plus-dat-hang-05.jpg" style="width:80%" /></p>\r\n\r\n<p><strong>2. V&Ograve;NG TAY TH&Ocirc;NG MINH ĐO SỨC KHỎE H-BAND HIỆN ĐẠI</strong></p>\r\n\r\n<p>V&ograve;ng tay th&ocirc;ng minh H-band nổi bật bởi sở hữu nhiều t&iacute;nh năng tiện lợi v&agrave; thiết thực:</p>\r\n\r\n<ul>\r\n <li>Chăm s&oacute;c sức khỏe: đo nhịp tim, quản l&yacute; giấc ngủ, đo huyết &aacute;p, đếm bước ch&acirc;n, qu&atilde;ng đường, đo calorie cho c&aacute;c vận động</li>\r\n  <li>Tiện dụng với c&aacute;c notifications: b&aacute;o cuộc gọi (c&oacute; hiển thị số gọi đến), b&aacute;o SMS, facebook &hellip; &bull; B&aacute;o thức, chống mất trộm</li>\r\n  <li>Sạc pin dễ d&agrave;ng</li>\r\n <li>Thời trang v&agrave; c&aacute; t&iacute;nh với thiết kế hiện đại, trẻ trung, lịch l&atilde;m</li>\r\n <li>Giao diện th&acirc;n thiện, dễ sử dụng. V&ograve;ng tay th&ocirc;ng minh H-band được kết nối với điện thoại th&ocirc;ng qua Bluetooth v&agrave; ứng dụng H-band c&oacute; thể dễ d&agrave;ng tải được từ kho ứng dụng CH Play tr&ecirc;n F1s v&agrave; c&aacute;c smartphone sử dụng Android kh&aacute;c.</li>\r\n</ul>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-f3-plus-dat-hang-06.jpg" style="width:80%" /></p>\r\n', 'images/oppo-f3-plus-dat-hang-01.jpg', 34, b'1', 'oppo-f3-plus-cho-dat-truoc-tu-ngay-243-tang-bo-qua-tri-gia-2150000-dong', 'OPPO F3 Plus cho đặt trước từ ngày 24/3, tặng bộ quà trị giá 2,150,000 đồng', 'OPPO F3 Plus, OPPO', 'Bộ quà tặng này có trị giá 2,150,000 đồng, bao gồm 2 thiết bị cực “chất” và tiện lợi: tai nghe Bluetooth thời trang và vòng đeo tay H-band cá tính. Ngoài ra, khi đến nhận máy tại cửa hàng vào ngày 1/4/2017, khách hàng còn được ưu đãi thêm một gói bảo hành...', '2017-03-17', 2, b'0'),
(22, 'Huawei thắng lớn tại MWC 2017 với Huawei P10 và Huawei Watch 2 ', 3, '<h2>Trong hai ng&agrave;y đầu ti&ecirc;n của hội nghị di động thế giới (MWC), Huawei đ&atilde; vinh dự được nhận 15 giải thưởng. Ngay sau sự kiện ra mắt to&agrave;n cầu sản phẩm cao cấp Huawei P10, Huawei P10 Plus v&agrave; Huawei Watch 2, giới truyền th&ocirc;ng đ&atilde; đặc biệt hướng ngồi b&uacute;t đến cụm camera k&eacute;p đồng chế t&aacute;c với thương hiệu Leica nổi tiếng thế giới v&agrave; t&iacute;nh năng chụp ảnh ch&acirc;n dung ho&agrave;n hảo. Huawei watch 2 cũng rất thu h&uacute;t bởi c&ocirc;ng nghệ 4G v&agrave; GPS.</h2>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/huawei-thang-lon-MWC-2017-01.png" style="width:90%" /></p>\r\n\r\n<p style="text-align:center"><span style="color:#bdc3c7">H&igrave;nh ảnh giải thưởng của P10 v&agrave; P10 Plus trong MWC 2017</span></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/huawei-thang-lon-MWC-2017-02.png" style="width:80%" /></p>\r\n\r\n<p>K&ecirc;nh truyền th&ocirc;ng Android Authority đ&atilde; t&ocirc;n vinh giải thưởng Huawei P10 l&agrave; sản phẩm điện thoại th&ocirc;ng minh &ldquo;Xuất sắc nhất MWC 2017: Điện thoại th&ocirc;ng minh tốt nhất&rdquo;. Ph&oacute;ng vi&ecirc;n c&ocirc;ng nghệ y&ecirc;u th&iacute;ch chế độ chụp ch&acirc;n dung bằng camera k&eacute;p v&agrave; phi&ecirc;n bản m&agrave;u xanh Dương huyền thoại. Android Authority c&oacute; 23 triệu độc giả mỗi th&aacute;ng.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/huawei-thang-lon-MWC-2017-03.png" style="width:80%" /></p>\r\n\r\n<p>C&ograve;n T3 lại trao cho Huawei P10 giải thưởng &ldquo;Giải thưởng MWC 2017: Điện thoại xuất sắc nhất.&rdquo; C&aacute;c nh&agrave; b&aacute;o c&ocirc;ng nghệ đặc biệt ấn tượng bởi thương hiệu m&aacute;y ảnh chuy&ecirc;n nghiệp Leica gắn liền với chiếc điện thoại th&ocirc;ng minh Huawei ở cả ống k&iacute;nh trước v&agrave; sau&rdquo;, ngo&agrave;i ra</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/huawei-thang-lon-MWC-2017-04.png" style="width:80%" /></p>\r\n\r\n<p>C&ograve;n HDblog đ&atilde; c&ocirc;ng nhận Huawei P10 &ldquo;Giải thưởng HDblog: Giải Editor Choice xuất sắc nhất&rdquo;. C&aacute;c ph&oacute;ng vi&ecirc;n c&ocirc;ng đ&atilde; kh&ocirc;ng tiếc lời khen ngợi cho thiết bị bởi hệ điều h&agrave;nh EMUI 5.1, cảm biến v&acirc;n tay tuyệt vời v&agrave; chipset Kirin 960 mạnh mẽ. HDbog c&oacute; 10.2 triệu người đọc mỗi th&aacute;ng.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/huawei-thang-lon-MWC-2017-05.png" style="width:80%" /></p>\r\n\r\n<p>PhoneArena vinh danh Huawei P10 với giải thưởng :&rdquo;Điện thoại v&agrave; thiết bị mới xuất sắc nhất&rdquo;. Họ nhấn mạnh những chi tiết s&aacute;ng tạo tỉ mỉ khi s&aacute;ng chế ra cụm camera k&eacute;p tr&ecirc;n điện thoại di động, phần mềm v&agrave; chế độ chụp ch&acirc;n dung. Một t&iacute;nh năng nổi bật nữa l&agrave; vết cắt laser sang trọng v&agrave; ho&agrave;n hảo, gi&uacute;p chiếc điện thoại tỏa s&aacute;ng m&agrave; ko bị hằn dấu vấn tay. PhoneArena c&oacute; 17.7 triệu độc giả mỗi th&aacute;ng.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/huawei-thang-lon-MWC-2017-06.png" style="width:80%" /></p>\r\n\r\n<p>TechAvisor trao cho chiếc Huawei P10 giải thưởng &ldquo;Top pick&rdquo;. C&aacute;c nh&agrave; b&aacute;o c&ocirc;ng nghệ đ&atilde; hướng sự quan t&acirc;m của m&igrave;nh v&agrave;o chế độ chụp ch&acirc;n dung v&agrave; chụp g&oacute;c rộng của camera trước. TechAdvisor đ&atilde; c&oacute; hơn 14 triệu độc giả một th&aacute;ng.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/huawei-thang-lon-MWC-2017-07.png" style="width:80%" /></p>\r\n\r\n<p>FoneArena đ&atilde; b&igrave;nh chọn Huawei P10 với giải thưởng &ldquo;Editor&rsquo;s Pick of MWC 2017&rdquo;. Sự lựa chọn ho&agrave;n hảo của thiết bị với những m&agrave;u sắc thời thượng v&agrave; đặc biệt l&agrave; camera k&eacute;p. FoneAreana c&oacute; 7.9 triệu độc giả một th&aacute;ng.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/huawei-thang-lon-MWC-2017-08.png" style="width:80%" /></p>\r\n\r\n<p>Trusted Reviews đ&atilde; b&igrave;nh chọn Huawei P10 v&agrave; Huawei&nbsp; P10 Plus l&agrave; một trong những &ldquo;Điện thoại th&ocirc;ng minh xuất sắc nhất MWC.&rdquo; Đ&acirc;y l&agrave; sản phẩm kế nhiệm ho&agrave;n hảo của P9, với camera k&eacute;p ho&agrave;n hảo,&nbsp; cải tiến vượt trội về thời lượng pin. Trusted Reviews c&oacute; 27 triệu độc giả h&agrave;ng th&aacute;ng.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/huawei-thang-lon-MWC-2017-09.png" style="width:80%" /></p>\r\n\r\n<p>Recombu đ&atilde; v&iacute; chiếc Huawei P10 Plus&nbsp; như&nbsp; một sản phẩm &ldquo;xuất sắc nhất MWC 2017&rdquo;. Điểm đặc biệt ch&uacute; &yacute; của sản phẩm l&agrave; m&agrave;n h&igrave;nh 5.5 inches, chế độ HD, camera v&agrave; bộ vi xử l&yacute;. Recombu c&oacute; 4.6 triệu độc giả mỗi th&aacute;ng.</p>\r\n\r\n<p><strong>N</strong><strong>hững giải thưởng d&agrave;nh cho sản phẩm Huawei Watch 2&nbsp;</strong><strong>tại</strong><strong>&nbsp;MWC:</strong></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/huawei-thang-lon-MWC-2017-10.png" style="width:60%" /></p>\r\n\r\n<p>T3 đ&atilde; vinh danh Huawei Watch 2 với t&ecirc;n gọi &ldquo;Giải thưởng MWC 2017: Chiếc đồng hồ tuyệt vời nhất&rdquo;. Giới b&aacute;o ch&iacute; đặc biệt quan t&acirc;m đến điểm nhấn về t&iacute;nh năng đo nhịp tim v&agrave; định vị GPS. T3 c&oacute; 2.4 triệu&nbsp; độc giả mỗi ng&agrave;y.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/huawei-thang-lon-MWC-2017-11.png" style="width:60%" /></p>\r\n\r\n<p>HDblog trao cho Huawei Watch 2 giải thưởng &ldquo;HDblog: Chiếc điện thoại xuất sắc nhất&rdquo;. C&aacute;c ph&oacute;ng vi&ecirc;n đ&atilde; b&igrave;nh chọn cho chiếc đồng hồ về thời lượng pin;&nbsp; t&iacute;nh năng kết nối v&agrave; chứng nhận IP68. HDblog c&oacute; 10.2 triệu độc giả mỗi th&aacute;ng.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/huawei-thang-lon-MWC-2017-12.png" style="width:80%" /></p>\r\n\r\n<p>Pocket-lint đ&atilde; b&igrave;nh chọn cho chiếc Huawei Watch 2 với giải thưởng &ldquo;Đồng hồ xuất sắc nhất MWC 2017&rdquo;. C&aacute;c ph&oacute;ng vi&ecirc;n đ&atilde; n&ecirc;u bật t&iacute;nh năng cảm biến, đặc biệt khi người d&ugrave;ng đang tập thể thao hoặc đang chạy m&agrave; kh&ocirc;ng cần mang theo điện thoại. Pocket-lint c&oacute; 3 triệu độc giả mỗi th&aacute;ng.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/huawei-thang-lon-MWC-2017-13.png" style="width:80%" /></p>\r\n\r\n<p>ExpertReviews đ&atilde; d&agrave;nh cho Huawei Watch 2 giải thưởng &ldquo;Chiếc đồng hồ tốt nhất&rdquo;. C&aacute;c nh&agrave; b&aacute;o c&ocirc;ng nghệ thực sự bị cuốn h&uacute;t bởi t&iacute;nh năng định vị c&ugrave;ng 4G v&agrave; như vậy bạn c&oacute; thể t&ugrave;y &yacute; điều khiển chiếc đồng hồ của m&igrave;nh. ExpertReviews c&oacute; 15 triệu người đọc mỗi th&aacute;ng.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/huawei-thang-lon-MWC-2017-14.png" style="width:60%" /></p>\r\n\r\n<p>TechRadar đ&atilde; vinh danh Huawei Watch 2 như &ldquo;chiếc đồng hồ tuyệt vời nhất&rdquo; tại MWC 2017. Họ đ&atilde; thực sự bị cuốn h&uacute;t bởi thiết kế thể thao của chiếc đồng hồ. TechRadar c&oacute; hơn 48 triệu người đọc mỗi th&aacute;ng.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/huawei-thang-lon-MWC-2017-15.png" style="width:60%" /></p>\r\n\r\n<p>PC Mag đ&atilde; lựa chọn chiếc Huawei Watch 2 l&agrave; &ldquo;đ&ocirc;ng hồ đặc sắc nhất MWC&rdquo;. N&oacute; đặc sắc bởi tuổi thọ pin v&agrave; khả năng t&iacute;ch hợp GPS. PC Mag c&oacute; hơn 43 triệu người đọc mỗi th&aacute;ng.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/huawei-thang-lon-MWC-2017-16.png" style="width:60%" /></p>\r\n\r\n<p>Recombu đ&atilde; c&ocirc;ng nhận Huawei Watch 2 l&agrave; sản phẩm &ldquo;Tốt nhất MWC&rdquo;. C&aacute;c ph&oacute;ng vi&ecirc;n c&ocirc;ng nghệ đ&atilde; gọi sản phẩm l&agrave; &ldquo;chiếc đồng hồ th&ocirc;ng minh vượt trội&rdquo;. Họ đ&aacute;nh gi&aacute; cao thiết kế h&igrave;nh khối v&agrave; Andoid&nbsp; Wear 2.0. Recombu đ&atilde; c&oacute; 4.6 triệu người đọc mỗi ng&agrave;y.</p>\r\n\r\n<p style="text-align:center">&nbsp;</p>\r\n\r\n<p style="text-align:center">&nbsp;</p>\r\n', 'images/huawei-thang-lon-MWC-2017-01.png', 5, b'1', 'huawei-thang-lon-tai-mwc-2017-voi-huawei-p10-va-huawei-watch-2', 'Huawei thắng lớn tại MWC 2017 với Huawei P10 và Huawei Watch 2 ', 'MWC 2017, Huawei P10,Huawei Watch 2 ', 'Trong hai ngày đầu tiên của hội nghị di động thế giới (MWC), Huawei đã vinh dự được nhận 15 giải thưởng. Ngay sau sự kiện ra mắt toàn cầu sản phẩm cao cấp Huawei P10, Huawei P10 Plus và Huawei Watch 2...', '2018-01-30', 2, b'0'),
(23, 'OPPO trình làng F3 Plus camera selfie kép với giá 10.690.000 đồng', 2, '<h2>OPPO F3 Plus l&agrave; chiếc smartphone đầu ti&ecirc;n đến từ nh&agrave; sản xuất OPPO được trang bị camera selfie k&eacute;p rộng 120 độ. Dự kiến chiếc điện thoại n&agrave;y sẽ tạo ra xu hướng selfie với trải nghiệm ho&agrave;n to&agrave;n mới.</h2>\r\n\r\n<p>Theo đ&oacute;, Việt Nam c&ugrave;ng c&aacute;c thị trường kh&aacute;c bao gồm Indo, Ấn Độ, Philipin, Myanma đ&atilde; đồng loạt ra mắt OPPO F3 Plus.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-trinh-lang-f3-plus-camera-selfie-kep-01.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-trinh-lang-f3-plus-camera-selfie-kep-02.jpg" style="width:96%" /></p>\r\n\r\n<p>Camera selfie k&eacute;p l&agrave; t&iacute;nh năng được nhắc đến nhiều nhất tr&ecirc;n sản phẩm lần n&agrave;y. OPPO đ&atilde; c&ugrave;ng hợp t&aacute;c với Sony để trang bị cụm camera 16MP + 8MP (xử l&yacute; g&oacute;c rộng) cảm biến IMX398 ở mặt trước, từ đ&oacute; việc selfie nh&oacute;m đ&ograve;i hỏi g&oacute;c rộng đ&atilde; trở n&ecirc;n dễ d&agrave;ng hơn. Mặc d&ugrave; OPPO kh&ocirc;ng phải l&agrave; h&atilde;ng đầu ti&ecirc;n đem camera selfie k&eacute;p l&ecirc;n điện thoại th&ocirc;ng minh nhưng h&atilde;ng n&agrave;y đ&atilde; c&oacute; bề d&agrave;y trong việc nghi&ecirc;n cứu ph&aacute;t triển những sản phẩm c&oacute; chất lượng camera tuyệt vời, điển h&igrave;nh như OPPO N1, F1 Plus v&agrave; giờ đ&acirc;y l&agrave; F3 Plus.</p>\r\n\r\n<p>Tr&ecirc;n thực tế F3 Plus l&agrave; phi&ecirc;n bản kế nhiệm của F1s &ldquo;chuy&ecirc;n gia selfie&rdquo; với cảm biến 1/3&Prime; cho độ s&aacute;ng ấn tượng, kết hợp với đ&oacute; l&agrave; một camera g&oacute;c rộng 120 độ (rộng hơn 150% so với c&aacute;c smartphone kh&aacute;c) c&oacute; độ ph&acirc;n giải 8MP hệ thống thấu k&iacute;nh 6 lớp. Dĩ nhi&ecirc;n phần cứng sẽ kh&ocirc;ng đ&aacute;ng kể nếu kh&ocirc;ng c&oacute; phần mềm tận dụng n&oacute;, ở đ&acirc;y OPPO đ&atilde; trang bị t&iacute;nh năng &ldquo;Group Selfie&rdquo; tr&ecirc;n camera mặc định, n&oacute; sẽ tự th&ocirc;ng b&aacute;o để k&iacute;ch hoạt khi ph&aacute;t hiện c&oacute; hơn 3 người trong một bức ảnh. B&ecirc;n cạnh đ&oacute;, ch&uacute;ng ta c&ograve;n c&oacute; t&iacute;nh năng l&agrave;m đẹp Beautify 4.0, chế độ xo&aacute; ph&ocirc;ng, flash m&agrave;n h&igrave;nh, c&aacute;c filter m&agrave;u&hellip;</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/oppo-trinh-lang-f3-plus-camera-selfie-kep-03.jpg" style="width:96%" /></p>\r\n\r\n<p>Camera trước đ&atilde; ấn tượng nhưng OPPO c&ograve;n cho biết camera sau của F3 Plus cũng rất đ&aacute;ng quan t&acirc;m. H&atilde;ng đ&atilde; trang bị camera c&oacute; khẩu độ lớn f/1.7 với cảm biến IMX398 ph&aacute;t triển bởi Sony, trong đ&oacute; t&iacute;nh năng Dual PDAF lấy n&eacute;t hai pha nhanh hơn 40% trong m&ocirc;i trường thiếu s&aacute;ng.</p>\r\n\r\n<p>OPPO F3 Plus được trang bị cấu h&igrave;nh mạnh mẽ, bao gồm bộ xử l&yacute; Snapdragon 653, 4GB RAM, 64GB bộ nhớ trong, hỗ trợ 2 SIM, pin 4000mAh t&iacute;ch hợp sạc nhanh VOOC, cảm biến v&acirc;n tay một chạm, m&agrave;n h&igrave;nh lớn 6 inch Full HD mặt k&iacute;nh cong 2.5D. M&aacute;y c&oacute; hai m&agrave;u V&agrave;ng v&agrave; Đen được b&aacute;n ra với mức gi&aacute; 10.690.000 đồng, tặng k&egrave;m tai nghe Bluetooth TEKIN v&agrave; v&ograve;ng đeo H-Band, l&ecirc;n kệ từ ng&agrave;y 01/04.</p>\r\n', 'images/oppo-trinh-lang-f3-plus-camera-selfie-kep-00.jpg', 34, b'1', 'oppo-trinh-lang-f3-plus-camera-selfie-kep-voi-gia-10690000-dong', 'OPPO trình làng F3 Plus camera selfie kép với giá 10.690.000 đồng', 'oppo f3 plus, oppo', 'OPPO F3 Plus là chiếc smartphone đầu tiên đến từ nhà sản xuất OPPO được trang bị camera selfie kép rộng 120 độ. Dự kiến chiếc điện thoại này sẽ tạo ra xu hướng selfie với trải nghiệm hoàn toàn mới.', '2018-01-30', 1, b'1'),
(24, 'Những sự thật ít người biết về hệ điều hành macOS ', 4, '<h2>Thomas Reed, chuy&ecirc;n gia bảo mật của Malwarebytes, chia sẻ kinh nghiệm của m&igrave;nh về những lầm tưởng của người d&ugrave;ng khi nhắc đến MacOS.</h2>\r\n\r\n<p>Theo&nbsp;<em>Forbes,</em>&nbsp;nhiều người chưa từng sử dụng macOS c&oacute; thể cho rằng hệ điều h&agrave;nh n&agrave;y rất bảo mật, tuy nhi&ecirc;n sự thật lại kh&ocirc;ng như vậy. Chuy&ecirc;n gia bảo mật của Malwarebytes, Thomas Reed, đ&atilde; chia sẻ những điều hay bị lầm tưởng về hệ điều h&agrave;nh n&agrave;y.</p>\r\n\r\n<p><strong>M&aacute;y t&iacute;nh Mac c&oacute; thể d&iacute;nh virus</strong></p>\r\n\r\n<p>Theo Thomas Reed, nếu virus được coi như một phần mềm độc hại hay m&atilde; độc (malware), l&acirc;y lan trong m&aacute;y t&iacute;nh bằng c&aacute;ch &ldquo;tự d&aacute;n&rdquo; m&igrave;nh v&agrave;o những tập tin kh&aacute;c, khi đ&oacute;, macOS cũng như nhiều hệ điều h&agrave;nh kh&aacute;c sẽ &ldquo;trong sạch&rdquo; do những virus hoạt động theo phương thức đ&oacute; phần lớn kh&ocirc;ng c&ograve;n hiện diện ng&agrave;y nay.</p>\r\n\r\n<p>Tr&ecirc;n thực tế, m&atilde; độc thực chất l&agrave; một phần mềm c&oacute; thể g&acirc;y hại đến m&aacute;y t&iacute;nh v&agrave; người d&ugrave;ng, bao gồm cả virus, backdoors (phần mềm cửa sau), spyware (phần mềm gi&aacute;n điệp) v&agrave; nhiều dạng kh&aacute;c. V&igrave; thế, &ocirc;ng khẳng định macOS vẫn c&oacute; thể d&iacute;nh virus, do hệ điều h&agrave;nh n&agrave;y từng bị ph&aacute;t hiện c&aacute;c chủng loại m&atilde; độc kh&aacute;c nhau. Chỉ ri&ecirc;ng năm 2012, đ&atilde; c&oacute; 11 loại m&atilde; độc được ph&aacute;t hiện tr&ecirc;n hệ điều h&agrave;nh n&agrave;y.</p>\r\n\r\n<p><strong>Kh&ocirc;ng c&oacute; nhiều loại m&atilde; độc tr&ecirc;n macOS</strong></p>\r\n\r\n<p style="text-align:center"><strong><img alt="" src="http://localhost/congngheviet/images/nhung-su-nguoi-biet-ve-dieu-hanh-macos-01.jpg" style="width:80%" /></strong></p>\r\n\r\n<p style="text-align:center">Người d&ugrave;ng sẽ v&ocirc; t&igrave;nh c&agrave;i v&agrave;o m&aacute;y c&aacute;c PUP nếu l&agrave;m theo những cảnh b&aacute;o hiện tr&ecirc;n tr&igrave;nh duyệt. Ảnh: Malwarebytes.</p>\r\n\r\n<p>Theo Thomas, việc d&iacute;nh m&atilde; độc tr&ecirc;n macOS l&agrave; rất hiếm. Số liệu của Net Market Share cho thấy thị phần của macOS tr&ecirc;n thị trường chỉ chiếm 6,19%, trong khi hệ điều h&agrave;nh Windows chiếm 91,76%. Sẽ dễ hiểu khi tin tặc chọn viết m&atilde; độc tấn c&ocirc;ng v&agrave;o hệ điều h&agrave;nh c&oacute; nhiều người sử dụng hơn.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, c&aacute;c chuy&ecirc;n gia đ&atilde; ghi nhận được nhiều hơn c&aacute;c mối nguy hiểm kh&aacute;c kh&ocirc;ng phải virus đang nhắm v&agrave;o người d&ugrave;ng macOS. Ch&uacute;ng bao gồm adware (phần mềm hiện quảng c&aacute;o) v&agrave; c&aacute;c chương tr&igrave;nh kh&ocirc;ng mong muốn (PUP).</p>\r\n\r\n<p>Adware sẽ c&agrave;i v&agrave;o tr&igrave;nh duyệt người d&ugrave;ng c&ocirc;ng cụ t&igrave;m kiếm (search engine) của m&atilde; độc n&agrave;y v&agrave; th&ecirc;m v&agrave;o đ&oacute; những trang quảng c&aacute;o kh&ocirc;ng mong muốn. Những m&aacute;y t&iacute;nh nhiễm adware sẽ trở th&agrave;nh c&ocirc;ng cụ gi&uacute;p c&aacute;c b&ecirc;n thứ 3 gian lận quảng c&aacute;o hay c&ocirc;ng cụ t&igrave;m kiếm.</p>\r\n\r\n<p>Song song với adware, PUP l&agrave; c&aacute;c chương tr&igrave;nh kh&ocirc;ng mong muốn xuất hiện tr&ecirc;n m&aacute;y t&iacute;nh. Ch&uacute;ng c&oacute; thể được gắn c&aacute;c t&ecirc;n gọi như phần mềm gi&aacute;m s&aacute;t con trẻ, qu&eacute;t dọn r&aacute;c m&aacute;y t&iacute;nh hay thậm ch&iacute; l&agrave; phần mềm diệt, chống virus.</p>\r\n\r\n<p>Những mối nguy hiểm n&agrave;y d&ugrave; kh&ocirc;ng thực sự g&acirc;y hại cho m&aacute;y t&iacute;nh nhưng vẫn chứa nguy cơ tiềm ẩn. V&agrave;o năm 2015, một dạng PUP c&oacute; li&ecirc;n quan đến phần mềm MacKeeper được ph&aacute;t hiện đ&atilde; tạo lỗ hổng tr&ecirc;n c&aacute;c m&aacute;y t&iacute;nh Mac c&agrave;i chương tr&igrave;nh n&agrave;y.</p>\r\n\r\n<p><strong>macOS bảo mật tốt hơn Windows</strong></p>\r\n\r\n<p style="text-align:center"><strong><img alt="" src="http://localhost/congngheviet/images/nhung-su-nguoi-biet-ve-dieu-hanh-macos-02.jpg" style="width:80%" /></strong></p>\r\n\r\n<p style="text-align:center">D&ugrave; c&oacute; &iacute;t mối đe dọa hơn Windows, macOS vẫn c&ograve;n nhiều lo ngại về bảo mật. Ảnh: Times Higher Education.</p>\r\n\r\n<p>macOS hiện tại vẫn c&oacute; &iacute;t mối đe dọa hơn so với đối thủ Windows. Nhiều loại m&atilde; độc kh&aacute;c nhau chạy tr&ecirc;n Windows vẫn được ph&aacute;t hiện h&agrave;ng ng&agrave;y, nhưng kh&aacute;c với đ&oacute;, chỉ cần một loại m&atilde; độc mới xuất hiện tr&ecirc;n macOS, người ta sẽ tốn cả th&aacute;ng để giải quyết n&oacute;, Thomas n&oacute;i.</p>\r\n\r\n<p>D&ugrave; kh&oacute; c&oacute; thể chỉ ra kh&aacute;c biệt r&otilde; r&agrave;ng giữa hệ thống bảo mật của macOS v&agrave; Windows, hệ điều h&agrave;nh c&oacute; logo tr&aacute;i t&aacute;o n&agrave;y vẫn c&oacute; xu hướng an to&agrave;n hơn khi thống k&ecirc; c&aacute;c mối đe dọa thực sự.</p>\r\n\r\n<p><strong>MacOS c&oacute; sẵn một phần mềm chống m&atilde; độc</strong></p>\r\n\r\n<p>Đ&acirc;y l&agrave; phần mềm mang t&ecirc;n XProtect, được mặc định ẩn đi v&agrave; kh&ocirc;ng thể tắt. N&oacute; sử dụng những k&yacute; hiệu nhận dạng (signatures) để nhận biết c&aacute;c m&atilde; độc khi người d&ugrave;ng mở chạy một phần mềm. Nhưng XProtect chỉ c&oacute; thể chặn được malware v&agrave; c&aacute;c m&atilde; độc từng được biết đến, do đ&oacute; lời khuy&ecirc;n từ Thomas l&agrave; người d&ugrave;ng kh&ocirc;ng n&ecirc;n qu&aacute; tin tưởng v&agrave;o XProtect.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, c&aacute;c t&iacute;n đồ Mac c&oacute; thể sử dụng th&ecirc;m phần mềm chống m&atilde; độc như ESET Cyber Security, Kaspersky Internet Security hay F-Secure Anti-Virus.</p>\r\n', 'images/nhung-su-nguoi-biet-ve-dieu-hanh-macos-00.jpg', 24, b'1', 'nhung-su-that-it-nguoi-biet-ve-he-dieu-hanh-macos', 'Những sự thật ít người biết về hệ điều hành macOS', 'macOS', 'Thomas Reed, chuyên gia bảo mật của Malwarebytes, chia sẻ kinh nghiệm của mình về những lầm tưởng của người dùng khi nhắc đến MacOS. Theo Forbes, nhiều người chưa từng sử dụng macOS có thể cho rằng...', '2018-01-30', 1, b'0');
INSERT INTO `tblbaiviet` (`idbv`, `tenbv`, `idcm`, `noidung`, `thumb`, `luotxem`, `hienthi`, `linkbv`, `tieude`, `tukhoa`, `mota`, `ngaydang`, `iduser`, `tinnoibac`) VALUES
(25, 'iPhone 7 màu Red sẽ về VN, giá từ 21,690,000 đồng ', 3, '<h2>Apple vừa ch&iacute;nh thức c&ocirc;ng bố th&ecirc;m m&agrave;u mới cho d&ograve;ng sản phẩm iPhone 7 v&agrave; 7 Plus l&agrave; m&agrave;u RED.&nbsp;Bộ đ&ocirc;i n&agrave;y được Apple ra mắt nhằm kỷ niệm 10 năm hợp t&aacute;c giữa Apple v&agrave; (RED) &ndash; một tổ chức g&acirc;y quỹ hỗ trợ ph&ograve;ng chống AIDS.</h2>\r\n\r\n<p>M&agrave;u đỏ của iPhone 7 / 7 Plus lần n&agrave;y l&agrave; m&agrave;u đặc biệt chưa từng c&oacute; trước đ&acirc;y tr&ecirc;n c&aacute;c mẫu iPhone cũ. Tr&ecirc;n Apple Store, iPhone 7 c&oacute; gi&aacute; 749 USD trong khi iPhone 7 Plus c&oacute; gi&aacute; 869 USD, kh&ocirc;ng c&oacute; lựa chọn 32 GB v&igrave; vậy bạn chỉ c&oacute; 2 lựa chọn l&agrave; 128 GB v&agrave; 256 GB.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/iphone-7-mau-red-se-ve-vn-gia-tu-21690000-dong-01.jpg" style="width:96%" /></p>\r\n\r\n<p>Tại FPT Shop 2 mẫu m&aacute;y iPhone 7/7+ Red sẽ&nbsp;<strong>c&oacute; gi&aacute; bằng với c&aacute;c m&agrave;u hiện tại</strong>. D&ugrave; đ&acirc;y l&agrave; m&agrave;u mới nhưng gi&aacute; vẫn kh&ocirc;ng đổi v&igrave; FPT Shop l&agrave; đại l&yacute; ủy quyền cao nhất kinh doanh c&aacute;c sản phẩm Apple ch&iacute;nh h&atilde;ng tại Việt Nam. Cụ thể về gi&aacute; của phi&ecirc;n bản iPhone 7/7 Plus đặc biệt n&agrave;y bạn c&oacute; thể tham khảo dưới đ&acirc;y</p>\r\n\r\n<ul style="margin-left:40px">\r\n <li>iPhone 7 128GB&nbsp;Red: 21,690,000 đồng</li>\r\n <li>iPhone 7 256 GB Red:&nbsp;24,590,000 đồng</li>\r\n  <li>iPhone 7 Plus 128GB&nbsp;Red: 25,190,000 đồng</li>\r\n  <li>iPhone 7 Plus 256GB&nbsp;Red: 27,990,000 đồng</li>\r\n</ul>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/iphone-7-mau-red-se-ve-vn-gia-tu-21690000-dong-02.jpg" style="width:96%" /></p>\r\n\r\n<p>FPT Shop đ&atilde; cho đặt trước iPhone 7 / 7 Plus Red ngay từ h&ocirc;m ngay với rất&nbsp;<strong>nhiều qu&agrave; tặng cực k&igrave; hấp dẫn</strong>. Link đặt mua sẽ được cập nhật nhanh nhất c&oacute; thể dưới b&agrave;i viết n&agrave;y hoặc bạn c&oacute; thể gọi tổng đ&agrave;i&nbsp;1800-6601 để được hỗ trợ th&ecirc;m về đặt h&agrave;ng.</p>\r\n\r\n<div class="youtube-embed-wrapper" style="height:0; overflow:hidden; padding-bottom:56.25%; padding-top:30px; position:relative"><iframe allowfullscreen="" frameborder="0" height="360" src="//www.youtube.com/embed/VDsjYTMyh94" style="position: absolute;top: 0;left: 0;width: 100%;height: 100%;" width="640"></iframe></div>\r\n\r\n<p>&nbsp;</p>\r\n', 'images/iphone-7-mau-red-se-ve-vn-gia-tu-21690000-dong-00.jpg', 67, b'1', 'iphone-7-mau-red-se-ve-vn-gia-tu-21690000-dong', 'iPhone 7 màu Red sẽ về VN, giá từ 21,690,000 đồng', 'iphone 7 red', 'Apple vừa chính thức công bố thêm màu mới cho dòng sản phẩm iPhone 7 và 7 Plus là màu RED. Bộ đôi này được Apple ra mắt nhằm kỷ niệm 10 năm hợp tác giữa Apple và (RED) – một tổ chức gây quỹ hỗ trợ phòng chống AIDS...', '2018-01-30', 1, b'0'),
(26, 'Mở hộp và Trên tay Xiaomi Mi Mix bản 18K bán chính hãng tại Việt Nam', 3, '<h2>So với bản b&aacute;n ra tại thị trường nội địa Trung Quốc, chiếc Mi Mix b&aacute;n ra tại Việt Nam kh&ocirc;ng kh&aacute;c g&igrave; về ngoại h&igrave;nh, chỉ c&oacute; một số thay đổi về phần mềm nhằm ph&ugrave; hợp với người d&ugrave;ng.</h2>\r\n\r\n<p>Trước đ&acirc;y m&igrave;nh đ&atilde; c&oacute; một b&agrave;i tr&ecirc;n tay Xiaomi Mi Mix (<a href="http://congngheviet.com/tren-tay-xiaomi-mi-mix/" target="_blank">xem lại tại đ&acirc;y</a>), nhưng đ&oacute; l&agrave; phi&ecirc;n bản nội địa Trung Quốc, c&ograve;n chiếc m&aacute;y m&igrave;nh n&oacute;i đến trong b&agrave;i n&agrave;y l&agrave; bản thương mại sẽ b&aacute;n ra tại thị trường Việt Nam. So với chiếc Mi Mix trước đ&acirc;y th&igrave; bản b&aacute;n ra ở Việt Nam c&oacute; cấu h&igrave;nh cao nhất l&agrave; 6GB RAM v&agrave; 256GB bộ nhớ trong, v&agrave; chỉ c&oacute; bản m&agrave;u đen được mạ v&agrave;ng 18K. Về phần mềm, Xiaomi Mi Mix b&aacute;n ra tại Việt Nam sẽ được c&agrave;i sẵn c&aacute;c dịch vụ của Google, c&oacute; cả Play Store (bản nội địa TQ kh&ocirc;ng c&oacute;), cũng như kh&ocirc;ng c&ograve;n những ứng dụng của c&aacute;c nh&agrave; mạng Trung Quốc.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/Xiaomi_Mi_Mix_CNV_19_02.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><span style="color:#bdc3c7">C&aacute;ch đ&oacute;ng hộp cũng kh&ocirc;ng kh&aacute;c, vẫn đẹp v&agrave; sang trọng, với bản cao cấp n&agrave;y th&igrave; chữ ngo&agrave;i hộp cũng được mạ v&agrave;ng 18K</span>&nbsp;</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/Xiaomi_Mi_Mix_CNV_19_03.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/Xiaomi_Mi_Mix_CNV_19_04.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/Xiaomi_Mi_Mix_CNV_19_05.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><span style="color:#bdc3c7">Trọn bộ phụ kiện đi k&egrave;m với Xiaomi Mi Mix, vẫn c&oacute; c&aacute;i ốp lưng da&nbsp;</span></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/Xiaomi_Mi_Mix_CNV_19_06.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><span style="color:#bdc3c7">Mi Mix tại Việt Nam vẫn chạy bản ROM như thị trường nội địa, nhưng c&oacute; bổ sung th&ecirc;m Tiếng Việt v&agrave; Tiếng Anh</span></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/Xiaomi_Mi_Mix_CNV_19_07.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><span style="color:#bdc3c7">Cạnh tr&ecirc;n c&oacute; jack tai nghe 3.5mm, mic thu &acirc;m</span></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/Xiaomi_Mi_Mix_CNV_19_08.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><span style="color:#bdc3c7">Cạnh dưới sẽ c&oacute; loa ngo&agrave;i v&agrave; c&ocirc;ng USB Type-C</span></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/Xiaomi_Mi_Mix_CNV_19_09.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><span style="color:#bdc3c7">Mặt lưng b&oacute;ng bảy, viền camera v&agrave; d&ograve;ng chữ &ldquo;MIX DESIGN BY MI&rdquo; được mạ v&agrave;ng 18K rất sang trọng.</span></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/Xiaomi_Mi_Mix_CNV_19_10.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><span style="color:#bdc3c7">Mi Mix b&aacute;n ra tại Việt Nam được c&agrave;i sẵn bộ dịch vụ của Google v&agrave; c&oacute; cả ứng dụng CH Play.</span></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/Xiaomi_Mi_Mix_CNV_19_11.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/Xiaomi_Mi_Mix_CNV_19_12.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/Xiaomi_Mi_Mix_CNV_19_13.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/Xiaomi_Mi_Mix_CNV_19_14.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/Xiaomi_Mi_Mix_CNV_19_15.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/Xiaomi_Mi_Mix_CNV_19_16.jpg" style="width:96%" /></p>\r\n', 'images/Xiaomi_Mi_Mix_CNV_19_01.jpg', 57, b'1', 'mo-hop-va-tren-tay-xiaomi-mi-mix-ban-18k-ban-chinh-hang-tai-viet-nam', 'Mở hộp và Trên tay Xiaomi Mi Mix bản 18K bán chính hãng tại Việt Nam', 'xiaomi', 'So với bản bán ra tại thị trường nội địa Trung Quốc, chiếc Mi Mix bán ra tại Việt Nam không khác gì về ngoại hình, chỉ có một số thay đổi về phần mềm nhằm phù hợp với người dùng. Trước đây mình đã có một bài trên tay Xiaomi Mi Mix...', '2018-01-30', 3, b'1'),
(27, 'Buổi offline LG G6 khoe khả năng chống nước tuyệt vời', 2, '<h2>Cửa h&agrave;ng điện thoại di động Huca, chuy&ecirc;n ph&acirc;n phối d&ograve;ng sản phẩm LG tại thị trường Việt Nam đ&atilde; tổ chức buổi offline với t&ecirc;n gọi &ldquo;LG G6 &ndash; Niềm đam m&ecirc; bất tận&rdquo;.</h2>\r\n\r\n<p>Mục ti&ecirc;u của buổi offline nhằm hướng đến việc x&acirc;y dựng một cộng đồng người d&ugrave;ng y&ecirc;u th&iacute;ch sản phẩm của LG cũng như fan của h&atilde;ng sản xuất H&agrave;n Quốc n&agrave;y. Đại diện của Huca l&agrave; &ocirc;ng Nguyễn Huy Cảnh cho biết: &ldquo;Đ&acirc;y l&agrave; lần đầu ti&ecirc;n ch&uacute;ng t&ocirc;i tổ chức offline cho c&aacute;c th&agrave;nh vi&ecirc;n y&ecirc;u th&iacute;ch d&ograve;ng sản phẩm LG, đặc biệt l&agrave; chiếc smartphone LG G6. Trong tương lai ch&uacute;ng t&ocirc;i sẽ tiếp tục thực hiện c&ocirc;ng việc n&agrave;y khi nh&agrave; sản xuất smartphone LG ra mắt thiết bị mới&rdquo;.</p>\r\n\r\n<p>Tại buổi offline diễn ra tại hồ bơi Lam Sơn &ndash; quận 5, kh&aacute; nhiều bạn trẻ y&ecirc;u th&iacute;ch c&ocirc;ng nghệ cũng như fan của smartphone LG đ&atilde; đến tham dự cũng như trải nghiệm chiếc flagship mới nhất của h&atilde;ng l&agrave; LG G6. Đặc biệt, trong đ&oacute; c&oacute; sự xuất hiện của anh Tony Ph&ugrave;ng &ndash; một Reviewer c&ocirc;ng nghệ nổi tiếng tr&ecirc;n Youtube, anh đ&atilde; chia sẻ kh&aacute; nhiều về những t&iacute;nh năng v&agrave; trải nghiệm của c&aacute; nh&acirc;n anh khi sử dụng chiếc LG G6 trong một thời gian.</p>\r\n\r\n<p>Hiện Huca đang c&oacute; những chương tr&igrave;nh khuyến m&atilde;i rất tốt cho những sản phẩm của LG, HTC,.. đặc biệt l&agrave; LG G6. Bạn c&oacute; thể xem&nbsp;<a href="https://huca.vn/tin-tuc/offline-trai-nghiem-lg-g6-co-hoi-so-huu-qua-tang-len-den-50-trieu-dong.html" target="_blank">chi tiết tại đ&acirc;y</a>&nbsp;v&agrave;&nbsp;<a href="https://huca.vn/lg-g6-new.html" target="_blank">đặt mua LG G6 tại đ&acirc;y</a></p>\r\n\r\n<ul>\r\n <li>Giảm 1 triệu đồng khi đặt mua LG V20, G4, HTC 10</li>\r\n <li>Giảm 500.000 cho kh&aacute;ch h&agrave;ng đặt mua LG G6</li>\r\n  <li>Tặng đồng hồ thời trang trị gi&aacute; 890.000 cho kh&aacute;ch đặt mua c&aacute;c sản phẩm LG kh&aacute;c</li>\r\n</ul>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hinh-anh-buoi-offline-lg-g6-khoe-kha-nang-chong-nuoc-02.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hinh-anh-buoi-offline-lg-g6-khoe-kha-nang-chong-nuoc-03.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hinh-anh-buoi-offline-lg-g6-khoe-kha-nang-chong-nuoc-04.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hinh-anh-buoi-offline-lg-g6-khoe-kha-nang-chong-nuoc-05.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hinh-anh-buoi-offline-lg-g6-khoe-kha-nang-chong-nuoc-06.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hinh-anh-buoi-offline-lg-g6-khoe-kha-nang-chong-nuoc-07.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hinh-anh-buoi-offline-lg-g6-khoe-kha-nang-chong-nuoc-08.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hinh-anh-buoi-offline-lg-g6-khoe-kha-nang-chong-nuoc-09.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hinh-anh-buoi-offline-lg-g6-khoe-kha-nang-chong-nuoc-10.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hinh-anh-buoi-offline-lg-g6-khoe-kha-nang-chong-nuoc-11.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hinh-anh-buoi-offline-lg-g6-khoe-kha-nang-chong-nuoc-12.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hinh-anh-buoi-offline-lg-g6-khoe-kha-nang-chong-nuoc-13.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hinh-anh-buoi-offline-lg-g6-khoe-kha-nang-chong-nuoc-14.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hinh-anh-buoi-offline-lg-g6-khoe-kha-nang-chong-nuoc-15.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/hinh-anh-buoi-offline-lg-g6-khoe-kha-nang-chong-nuoc-16.jpg" style="width:96%" /></p>\r\n\r\n<p style="text-align:center">&nbsp;</p>\r\n\r\n<p style="text-align:center">&nbsp;</p>\r\n', 'images/hinh-anh-buoi-offline-lg-g6-khoe-kha-nang-chong-nuoc-01.jpg', 67, b'1', 'buoi-offline-lg-g6-khoe-kha-nang-chong-nuoc-tuyet-voi', 'Buổi offline LG G6 khoe khả năng chống nước tuyệt vời tại hồ bơi', 'Lg G6, offline LG G6', 'Mục tiêu của buổi offline nhằm hướng đến việc xây dựng một cộng đồng người dùng yêu thích sản phẩm của LG cũng như fan của hãng sản xuất Hàn Quốc này. Đại diện của Huca là ông Nguyễn Huy Cảnh cho biết: Đây là lần đầu tiên chúng tôi tổ chức offline cho...', '2018-01-29', 2, b'1'),
(28, 'Đánh giá Moto M tầm trung nhưng chất lượng cao cấp', 3, '<h2>Kh&ocirc;ng thể phủ nhận rằng Motorola đ&atilde; thay đổi rất nhiều từ khi được Lenovo th&acirc;u t&oacute;m. Họ cho ra đời những sản phẩm đầy đủ t&iacute;nh năng tuyệt vời với mức gi&aacute; b&aacute;n hợp l&yacute;, điển h&igrave;nh như Moto Z từng l&agrave; &ldquo;killer high-end&rdquo; khi ra mắt, trong khi đ&oacute; Moto G4, Moto G4 play, v&agrave; Moto G4 plus đều l&agrave; những sản phẩm đ&aacute;ng mua ph&ugrave; hợp với t&uacute;i tiền người d&ugrave;ng.</h2>\r\n\r\n<p>Moto M nằm trong ph&acirc;n kh&uacute;c tầm trung c&oacute; gi&aacute; b&aacute;n 6.990.000 đồng. Theo như th&ocirc;ng số được ghi tr&ecirc;n hộp của sản phẩm th&igrave; đ&acirc;y l&agrave; một smartphone c&oacute; đầy đủ t&iacute;nh năng, thiết kế tốt cũng như gi&aacute; b&aacute;n ho&agrave;n to&agrave;n hợp l&yacute;. Tuy nhi&ecirc;n th&ocirc;ng số chỉ l&agrave; tr&ecirc;n giấy, c&ograve;n ở trải nghiệm thực tế, liệu Moto M c&oacute; đ&aacute;ng gi&aacute; từng đồng tiền b&aacute;t gạo?</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/danh-gia-môt-rola-1.jpg" style="width:80%" /></p>\r\n\r\n<h3>Th&ocirc;ng số kỹ thuật:</h3>\r\n\r\n<ul>\r\n <li>M&agrave;n h&igrave;nh 5.5&Prime; IPS LCD of 1,080 x 1,920px</li>\r\n <li>K&iacute;ch thước&nbsp;151.4 x 75.4 x 7.9 mm, 163g</li>\r\n <li>Vi xử l&yacute; Mediatek Helio P15, octa-core 2.2GHz</li>\r\n <li>4GB RAM, 32GB ROM</li>\r\n  <li>Android 6.0.1 Marshmallow</li>\r\n  <li>Camera ch&iacute;nh 16MP khẩu độ f/2.0, k&iacute;ch thước pixel 1.0&micro;m, PDAF, dual-tone LED flash</li>\r\n <li>Camera trước 8MP</li>\r\n <li>Cảm biến v&acirc;n tay</li>\r\n <li>Dual-SIM; Cat. 6 LTE; 802.11 a/g/b/n/ac; Bluetooth 4.1, A2DP, LE; GPS; FM radio</li>\r\n  <li>Dung lượng Pin 3,050mAh</li>\r\n  <li>Chống thấm nước</li>\r\n</ul>\r\n\r\n<h3>Thiết kế</h3>\r\n\r\n<p>Moto M sở hữu một ng&ocirc;n ngữ thiết kế quen thuộc, thường thấy tr&ecirc;n những chiếc điện thoại Android. M&aacute;y được l&agrave;m bằng kim loại nguy&ecirc;n khối, mặt lưng được v&aacute;t cong kết hợp mặt k&iacute;nh 2.5D, cảm gi&aacute;c cầm thiết bị trong tay cực k&igrave; mềm mại v&agrave; thoải m&aacute;i. Ngay tại mặt lưng ta cũng c&oacute; thể thấy hai dải ngăn s&oacute;ng ph&iacute;a tr&ecirc;n v&agrave; dưới c&ugrave;ng với logo của Motorola.</p>\r\n\r\n<p>Cụm camera v&agrave; đ&egrave;n flash của m&aacute;y thon d&agrave;i, được l&agrave;m lồi hẳn l&ecirc;n th&acirc;n m&aacute;y. Cụm camera được thiết kế như thể m&aacute;y được trang bị camera k&eacute;p, c&oacute; lẽ đội ngũ thiết kế của Motorola cố t&igrave;nh tạo một điểm nhấn cho chiếc Moto M.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/danh-gia-môt-rola-2.jpg" style="width:80%" /></p>\r\n\r\n<p>Ngay ph&iacute;a b&ecirc;n dưới camera l&agrave; cảm biến v&acirc;n tay, thiết kế n&agrave;y tiện v&agrave; đẹp hơn rất nhiều sơ với một n&uacute;t h&igrave;nh vu&ocirc;ng ph&iacute;a trước như tr&ecirc;n Moto Z.&nbsp;Motorola Moto M l&agrave; chiếc điện thoại Moto đầu ti&ecirc;n c&oacute; cảm biến v&acirc;n tay ở mặt lưng.</p>\r\n\r\n<p>Nh&igrave;n về ph&iacute;a mặt trước, một logo chữ &ldquo;moto&rdquo; nằm chễm chệ ngay ph&iacute;a dưới nơi m&agrave; đ&aacute;ng lẽ ra phải đặt ba ph&iacute;m điều hướng. Cạnh tr&ecirc;n ta sẽ bắt gặp những thứ thường thấy, loa thoại ngay ch&iacute;nh giữa, cảm biến v&agrave; m&aacute;y ảnh b&ecirc;n cạnh, ngo&agrave;i ra c&ograve;n c&oacute; một đ&egrave;n LED th&ocirc;ng b&aacute;o nhỏ nằm ẩn m&igrave;nh ph&iacute;a b&ecirc;n&nbsp;tr&aacute;i (đ&egrave;n LED n&agrave;y chỉ c&oacute; một m&agrave;u trắng).</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/danh-gia-môt-rola-3.jpg" style="width:80%" /></p>\r\n\r\n<p>C&aacute;c đường viền cắt kim cương xung quanh cạnh viền tạo một cảm gi&aacute;c sang trọng. Viền m&aacute;y kh&aacute; mỏng cầm trong tay tr&ocirc;ng rất gọn g&agrave;ng v&agrave; tinh tế. Khe SIM v&agrave; thẻ nhớ đặt b&ecirc;n cạnh tr&aacute;i, b&ecirc;n cạnh phải bố tr&iacute; n&uacute;t nguồn v&agrave; tăng giảm &acirc;m lượng hợp tầm với của ng&oacute;n tay. Ph&iacute;a cạnh dưới cổng USB Type-C đặt ngay ch&iacute;nh giữa 2 dải loa, thực ra loa của m&aacute;y đặt một b&ecirc;n, một b&ecirc;n loa chỉ để trang tr&iacute; cho đẹp.</p>\r\n\r\n<h3>M&agrave;n h&igrave;nh hiển thị</h3>\r\n\r\n<p>M&agrave;n h&igrave;nh hiển thị IPS LCD 5,5 inch độ ph&acirc;n giải 1080 x 1920. Độ s&aacute;ng ở mức tối đa tương đối cao c&oacute; thể sử dụng được ngo&agrave;i trời nhưng kh&ocirc;ng thực sự thoải m&aacute;i. Về chất lượng hiển thị, Moto M cho h&igrave;nh ảnh trong trẻo, sắc n&eacute;t.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/danh-gia-môt-rola-4.jpg" style="width:80%" /></p>\r\n\r\n<p>C&oacute; hai thiết lập chế độ m&agrave;u&nbsp;l&agrave; ti&ecirc;u chuẩn v&agrave; sặc sỡ. Ở chế đọ ti&ecirc;u chuẩn Moto M hơi c&oacute; xu hướng l&agrave;m qu&aacute; m&agrave;u xanh l&aacute;, m&agrave;u v&agrave;ng v&agrave; m&agrave;u đỏ nhưng kh&ocirc;ng qu&aacute; mức. Qua chế độ sặc sỡ, m&agrave;u sắc trở n&ecirc;n cực k&igrave; sống động v&agrave; ho&agrave;n to&agrave;n kh&ocirc;ng ch&iacute;nh x&aacute;c với thực tế, tuy nhi&ecirc;n nh&igrave;n rất th&iacute;ch mắt.</p>\r\n\r\n<p>Độ ph&acirc;n giải hiển thị tr&ecirc;n Moto M chưa hợp l&yacute;, c&aacute;c icon rất to, nội dung hiển thị tr&ecirc;n m&agrave;n h&igrave;nh n&agrave;y kh&ocirc;ng&nbsp;được tối ưu.</p>\r\n\r\n<h3>Giao diện v&agrave; t&iacute;nh năng</h3>\r\n\r\n<p>Motorola Moto M chạy giao diện thuần Google, bạn sẽ thấy sự tương đồng với c&aacute;c thiết bị Google Nexus chạy Marshmallow. Phần lớn ứng dụng c&agrave;i đặt sẵn tr&ecirc;n điện thoại l&agrave; của Google. Giao diện n&agrave;y rất mượt, phản hồi nhanh, tuy nhi&ecirc;n đối với một số người sẽ cảm thấy nh&agrave;m ch&aacute;n v&igrave; kh&ocirc;ng được t&ugrave;y biến nhiều t&iacute;nh năng. Sẽ tốt hơn rất nhiều nếu h&atilde;ng cập nhật thiết bị l&ecirc;n Android Nougat sớm.</p>\r\n\r\n<p>Cảm biến v&acirc;n tay ở mặt lưng l&agrave;m việc rất tốt, phản hồi nhanh, ch&iacute;nh x&aacute;c.&nbsp;Đ&aacute;ng tiếc, bạn kh&ocirc;ng thể t&ugrave;y chỉnh&nbsp;n&oacute; như l&agrave; một ph&iacute;m tắt cho c&aacute;c chức năng kh&aacute;c, điều m&agrave; đa số c&aacute;c h&atilde;ng kh&aacute;c đều cho ph&eacute;p.</p>\r\n\r\n<p>Điểm nổi bật tr&ecirc;n chiếc Moto M nằm ở chất lượng &acirc;m thanh xuất sắc khi sở hữu c&ocirc;ng nghệ Dolby Atmos của Lenovo. Ngo&agrave;i ra thiết bị c&ograve;n một t&iacute;nh năng đ&aacute;ng ch&uacute; &yacute; kh&aacute;c l&agrave; chống thấm nước.</p>\r\n\r\n<h3>Hiệu năng</h3>\r\n\r\n<p>B&ecirc;n trong Moto M l&agrave; con chip&nbsp;MediaTek Helio P15 octa-core xung nhịp 2.2GHz c&ugrave;ng với 4GB RAM. Cấu h&igrave;nh n&agrave;y gi&uacute;p ta sử dụng tốt c&aacute;c t&aacute;c vụ cơ bản h&agrave;ng ng&agrave;y, c&ograve;n về xử l&yacute; chuy&ecirc;n s&acirc;u hơn th&igrave; m&aacute;y c&oacute; vẻ hơi &ldquo;đuối sức&rdquo;.&nbsp;Sẽ hơi thất vọng với c&aacute;c game thủ, những tr&ograve; chơi đơn giản th&iacute;ch hợp với Moto M hơn, khi chơi c&aacute;c game đồ họa nặng một ch&uacute;t như Dead Effect, Dead Trigger hay Morden Combat&hellip; dễ thấy sự giật lag, giảm khung h&igrave;nh. M&aacute;y sẽ n&oacute;ng l&ecirc;n khi sử dụng trong một thời gian d&agrave;i.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/danh-gia-môt-rola-5.jpg" style="width:80%" /></p>\r\n\r\n<p>Bộ nhớ trong 32GB đ&aacute;p ứng được nhu cầu của đa số người d&ugrave;ng, nếu cảm thấy vẫn chưa đủ Moto M c&ograve;n c&oacute; khe cắm thẻ nhớ microSD.</p>\r\n\r\n<h3>Camera</h3>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/danh-gia-môt-rola-6.jpg" style="width:80%" /></p>\r\n\r\n<p>Camera sau 16MP chất lượng c&oacute; thể n&oacute;i l&agrave; ổn so với c&aacute;c thiết bị trong c&ugrave;ng tầm gi&aacute;. Ứng dụng m&aacute;y ảnh kh&ocirc;ng thuần Google&nbsp;m&agrave; của của Motorola thiết kế với một giao diện trực quan với đầy đủ c&aacute;c chế độ v&agrave; chức năng.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/danh-gia-môt-rola-7.jpg" style="width:80%" /></p>\r\n\r\n<p>X&eacute;t về chất lượng h&igrave;nh ảnh, m&aacute;y ảnh của Moto M chỉ đạt mức trung b&igrave;nh. Ở điều kiện &aacute;nh s&aacute;ng thuận lợi, ảnh chụp c&oacute; độ chi tiết tốt. Trong m&ocirc;i trường &aacute;nh s&aacute;ng yếu, m&aacute;y thể hiện r&otilde; rệt điểm yếu của m&igrave;nh, ảnh mờ độ chi tiết giảm xuống, xuất hiện nhiễu hạt. Ảnh rất dễ bị rung nh&ograve;e nếu kh&ocirc;ng cầm chắc điện thoại.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/danh-gia-môt-rola-8.jpg" style="width:60%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/danh-gia-môt-rola-9.jpg" style="width:60%" /></p>\r\n\r\n<p>Thật may mắn, đ&oacute; dường như chỉ l&agrave; yếu điểm duy nhất của camera tr&ecirc;n Moto M. M&agrave;u sắc v&agrave; độ b&atilde;o h&ograve;a của ảnh gần với thực tế, kh&ocirc;ng bị l&agrave;m qu&aacute; l&ecirc;n, c&acirc;n bằng trắng ổn định. Việc tự động lấy n&eacute;t theo pha cũng rất nhanh, rất hiếm khi kh&ocirc;ng lấy được n&eacute;t.</p>\r\n\r\n<p>M&aacute;y ảnh trước c&oacute; độ ph&acirc;n giải 8MP khẩu độ &nbsp;f/2.0. Ảnh Selfie chi tiết, m&agrave;u da thực tế. T&iacute;nh năng l&agrave;m đẹp t&iacute;ch hợp trong ứng dụng l&agrave;m mịn da, &ldquo;c&agrave;&rdquo; mụn, tuy nhi&ecirc;n đừng chỉnh l&ecirc;n mức tối đa, sẽ l&agrave;m mất tự nhi&ecirc;n.</p>\r\n\r\n<h3>Thời lượng Pin</h3>\r\n\r\n<p>Với dung lượng Pin&nbsp;3050mAh, kh&ocirc;ng qu&aacute; cao đối với một thiết bị c&oacute; m&agrave;n h&igrave;nh 5,5 inch, nhưng c&oacute; thể dễ d&agrave;ng đ&aacute;p ứng nhu cầu sử dụng từ s&aacute;ng đến tối. Đa số m&igrave;nh d&ugrave;ng m&aacute;y&nbsp;để lướt web, youtube, facebook, nghe nhạc, kh&ocirc;ng chơi game, với việc sử dụng như vậy m&aacute;y dư sức đ&aacute;p ứng một ng&agrave;y d&agrave;i sử dụng. Tất nhi&ecirc;n việc chơi game hay sử dụng 3G li&ecirc;n tục sẽ l&agrave;m ti&ecirc;u tốn pin hơn rất nhiều.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/danh-gia-môt-rola-10.jpg" style="width:80%" /></p>\r\n\r\n<p>Ngo&agrave;i ra, Moto M c&ograve;n được trang bị c&ocirc;ng nghệ sạc pin nhanh n&ecirc;n việc hết pin kh&ocirc;ng c&ograve;n l&agrave; vấn đ&egrave; nữa. Th&ocirc;ng thường m&igrave;nh mất khoảng 2 giờ để sạc đầy pịn của m&aacute;y.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/danh-gia-môt-rola-11.jpg" style="width:80%" /></p>\r\n\r\n<h3>Kết luận</h3>\r\n\r\n<p>Moto M thực sự l&agrave; một thiết bị tốt, nằm ở ph&acirc;n kh&uacute;c tầm trung nhưng chất lượng cao cấp. Hiện tại sản phẩm đ&atilde; được b&aacute;n tr&ecirc;n to&agrave;n quốc với mức gi&aacute; 6.990.000 đồng, khi mua được tặng k&egrave;m ốp lưng chống sốc, va đập.</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'images/danh-gia-môt-rola-0.jpg', 11, b'1', 'danh-gia-moto-m-tam-trung-nhung-chat-luong-cao-cap', 'Đánh giá Moto M tầm trung nhưng chất lượng cao cấp', 'danh gia moto,', 'Không thể phủ nhận rằng Motorola đã thay đổi rất nhiều từ khi được Lenovo thâu tóm. Họ cho ra đời những sản phẩm đầy đủ tính năng tuyệt vời với mức giá bán hợp lý, điển hình như Moto Z từng là “killer high-end” khi ra mắt, trong khi đó Moto G4...', '2018-01-29', 1, b'0');
INSERT INTO `tblbaiviet` (`idbv`, `tenbv`, `idcm`, `noidung`, `thumb`, `luotxem`, `hienthi`, `linkbv`, `tieude`, `tukhoa`, `mota`, `ngaydang`, `iduser`, `tinnoibac`) VALUES
(29, 'Samsung Galaxy S8: Dẫn đầu đường cong', 3, '<h2>Người ta n&oacute;i&nbsp;nhiều&nbsp;về kh&aacute;i niệm ho&agrave;n hảo, nhưng họ cũng hay n&oacute;i kh&ocirc;ng c&oacute; g&igrave; ho&agrave;n hảo. Galaxy S8 v&agrave; S8 Plus tất nhi&ecirc;n cũng như vậy, nhưng n&oacute; cũng đạt đến mức tiệm cận chỉnh chu cần c&oacute;.</h2>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/danh-gia-s8-2.jpg" style="width:80%" /></p>\r\n\r\n<h3>Thiết kế v&agrave; hiển thị</h3>\r\n\r\n<p>Phản ứng m&agrave; hầu hết mọi người khi cầm tr&ecirc;n t&acirc;y S8 đ&oacute; l&agrave; &ldquo;WOW&rdquo;, v&agrave; n&oacute; cũng sẽ l&agrave; phản ứng của bạn khi được cầm tr&ecirc;n tay. Đến thời điểm n&agrave;y S8 l&agrave; thiết bị duy nhất c&oacute; một tỉ lệ m&agrave;n h&igrave;nh độc đ&aacute;o, kết hợp với viềng cong hai b&ecirc;n tạo n&ecirc;n một thiết bị tuyệt đẹp khiến bất k&igrave; ai cũng phải lung lay ngay lần đầu chạm tay v&agrave;o n&oacute;.</p>\r\n\r\n<p>Với t&ecirc;n gọi l&agrave; &ldquo;Infinity Display&rdquo; c&oacute; tỷ lệ 18,5:9 hơi kh&aacute;c biệt về con số nhưng ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; g&igrave; kh&aacute;c biệt thực tế khi so s&aacute;nh với LG G6 l&agrave; 18:9. Điều n&agrave;y khiến S8 trở n&ecirc;n cao hơn, v&agrave; sẽ khiến kh&ocirc;ng gian m&agrave;n h&igrave;nh rộng r&atilde;i hơn, to hơn cho S8 thường l&agrave; 5,8 inch c&ograve;n với S8 Plus l&agrave; 6,2 inch. Khi thử đặt S8 cạnh iPhone 7 PLUS sẽ c&oacute; kh&aacute; nhiều từ ch&ecirc; tr&aacute;ch cho Apple khi iPhone 7 Plus trở th&ocirc; kệch v&agrave; c&oacute; qu&aacute; nhiều phần dư thừa tr&ecirc;n thiết bị của họ.</p>\r\n\r\n<p>M&agrave;n h&igrave;nh của S8 n&agrave;y được tr&agrave;n l&ecirc;n c&aacute;c cạnh của khung điện thoại c&ugrave;ng đ&oacute; hai cạnh tr&ecirc;n v&agrave; dưới cũng được l&agrave;m rất mỏng tạo n&ecirc;n một thiết kế ấn tượng với 83 phần trăm của mặt trước l&agrave; của kh&ocirc;ng gian m&agrave;n h&igrave;nh để bạn c&oacute; thể sử dụng n&oacute;. Ngo&agrave;i ra, giữ được n&eacute;t đặc trưng cong hai b&ecirc;n kết hợp m&agrave;n h&igrave;nh sắc n&eacute;t c&oacute; độ tương phản cao tạo cho bạn cảm gi&aacute;c như chỉ đang giữ lấy m&agrave;n h&igrave;nh hiển thị m&agrave; kh&ocirc;ng c&oacute; khung viền hay g&igrave; kh&aacute;c.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/danh-gia-s8-3.jpg" style="width:50%" /></p>\r\n\r\n<p>Samsung cũng khẳng định, m&agrave;n h&igrave;nh cong hai b&ecirc;n ngo&agrave;i tạo n&ecirc;n vẻ đẹp thẩm mĩ m&agrave; c&ograve;n gi&uacute;p người d&ugrave;ng dễ sử dụng hơn, cảm gi&aacute;c cầm, chạm vuốt mềm mại hơn so với c&aacute;c m&agrave;n h&igrave;nh phẳng hiện c&oacute; tr&ecirc;n thị trường.</p>\r\n\r\n<blockquote>\r\n<p>M&agrave;n h&igrave;nh cong tr&agrave;n cả bốn cạnh l&agrave; một bước đi rủi ro phải trả gi&aacute;</p>\r\n</blockquote>\r\n\r\n<p>Về vấn đề tương th&iacute;ch ứng dụng, đến thời điểm hiện tại cũng đ&atilde; c&oacute; nhiều ứng dụng hổ trợ tỷ lệ m&agrave;n h&igrave;nh n&agrave;y, tuy nhi&ecirc;n những ứng dụng phổ biến như với người d&ugrave;ng tr&ecirc;n thế giới như Pocket, Netflix, Speedtest,.. th&igrave; hiện tại vẫn kh&ocirc;ng thể sử dụng hết m&agrave;n h&igrave;nh m&agrave; chừa lại c&aacute;c phần viềng đen tr&ecirc;n v&agrave; dưới m&agrave;n h&igrave;nh, c&ograve;n những ứng dụng th&ocirc;ng thường th&igrave; kh&ocirc;ng gặp bất k&igrave; vấn đề n&agrave;o trong việc sử dụng cả.</p>\r\n\r\n<p>Về c&aacute;c tr&ograve; chơi th&igrave; hiện tại, v&iacute; dụ như Super Mario Run, tr&ograve; chơi n&agrave;y hiện tại vẫn chưa cập nhật để hổ trợ tỷ lệ m&agrave;n h&igrave;nh tr&ecirc;n n&ecirc;n nếu bạn &eacute;p buộc m&agrave;n h&igrave;nh sử dụng hết th&igrave; khả năng sẽ ảnh hưởng đến c&aacute;c trải nghiệm sử dụng ứng dụng, v&iacute; dụ như việc ảnh hướng tới c&aacute;c n&uacute;t bấm được đặt trong m&agrave;n h&igrave;nh.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/danh-gia-s8-4.jpg" style="width:80%" /></p>\r\n\r\n<p>May mắn thay, Google đang khuyến kh&iacute;ch c&aacute;c nh&agrave; ph&aacute;t triển, ph&aacute;t triển ứng dụng của họ chung một mẫu ti&ecirc;u chuẩn nhất định để sau n&agrave;y khi c&oacute; c&aacute;c tỷ lệ m&agrave;n h&igrave;nh mới được ra mắt vẫn c&oacute; thể sử dụng linh hoạt m&agrave;n h&igrave;nh m&agrave; kh&ocirc;ng cần phải c&oacute; th&ecirc;m những bản cập nhật th&ecirc;m tỷ lệ cho n&oacute;.</p>\r\n\r\n<p>Samsung cho biết tỷ lệ n&agrave;y l&agrave; ph&ugrave; hợp hơn để xem video m&agrave;n ảnh rộng v&agrave; lưu &yacute; rằng diện t&iacute;ch tr&ecirc;n S8 lớn hơn khoảng 36% so với S7 chơi c&ugrave;ng một đoạn video 21:9. Chắc chắn, điều n&agrave;y sẽ khiến bạn tận hưởng kh&ocirc;ng gian giải tr&iacute; tr&ecirc;n thiết bị di động tốt hơn. Những vẫn k&egrave;m theo bất cập đ&oacute; l&agrave; c&aacute;c phim tr&ecirc;n Google Play Phim hiện kh&ocirc;ng tự động sử dụng to&agrave;n bộ m&agrave;n h&igrave;nh, mặc d&ugrave; c&oacute; thể nhấn một n&uacute;t để l&agrave;m như vậy, trong khi video trong Netflix vẫn bị kho&aacute; 16:9 bất kể bạn sử dụng m&agrave;n h&igrave;nh tỷ lệ n&agrave;o. Cũng như c&aacute;c tr&ograve; chơi, đ&acirc;y l&agrave; vấn đề c&oacute; thể sẽ được giải quyết nếu bạn cho n&oacute; th&ecirc;m thời gian, v&igrave; hiện tại hầu hết c&aacute;c dạng h&igrave;nh ảnh đề u kh&ocirc;ng sử dụng hết m&agrave;n h&igrave;nh tr&ecirc;n S8.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/danh-gia-s8-5.jpg" style="width:80%" /></p>\r\n\r\n<p>Được trang bị tr&ecirc;n bộ đ&ocirc;i S8 v&agrave; S8 Plus l&agrave; m&agrave;n h&igrave;nh AMOLED, Quad HD Super AMOLED t&aacute;i tạo m&agrave;u sắc tuyệt vời cho độ s&aacute;ng cao ngay cả khi l&agrave; đang dưới &aacute;nh s&aacute;ng mặt trời trực tiếp. V&agrave; hiện tại m&agrave;n h&igrave;nh của S8 đang được đ&aacute;nh gi&aacute; l&agrave; m&agrave;n h&igrave;nh điện thoại tốt nhất tr&ecirc;n thế giới theo DisplayMate.</p>\r\n\r\n<p>Bởi h&igrave;nh dạng đặc trưng của m&agrave;n h&igrave;nh n&ecirc;n điều n&agrave;y chắc chắn ảnh hưởng đến phần cứng của S8, v&agrave; v&igrave; thế đ&aacute;nh đổi được một m&agrave;n h&igrave;nh lớn sắc n&eacute;t t&iacute;ch hợp trong kh&ocirc;ng gian nhỏ th&igrave; việc loại bỏ n&uacute;t home l&agrave; một việc xứng đ&aacute;ng v&agrave; c&ugrave;ng đ&oacute; l&agrave; th&ecirc;m v&agrave;o n&uacute;t ảo tr&ecirc;n m&agrave;n h&igrave;nh v&agrave; thay đổi h&igrave;nh dạng n&oacute; tạo n&ecirc;n một sự mới mẻ cho người d&ugrave;ng thiết bị Android.</p>\r\n\r\n<p>Tại vị tr&iacute; n&uacute;t home cũ, Samsung cũng th&ecirc;m một cảm biến &aacute;p lực b&ecirc;n dưới, do đ&oacute; bạn c&oacute; thể nhấn mạnh hơn một ch&uacute;t để đ&aacute;nh thức m&agrave;n h&igrave;nh hoặc quay trở lại m&agrave;n h&igrave;nh ch&iacute;nh tại bất kỳ thời điểm n&agrave;o, ngay cả trong c&aacute;c ứng dụng to&agrave;n m&agrave;n h&igrave;nh nơi m&agrave; n&uacute;t home kh&ocirc;ng được hiển thị. N&oacute; kh&ocirc;ng kh&aacute;c g&igrave; t&iacute;nh năng Force Touch của iPhone, nhưng n&oacute; chỉ giới hạn ở một vị tr&iacute; nhất định v&agrave; bạn sẽ kh&ocirc;ng thấy được n&uacute;t home v&igrave; m&agrave;n h&igrave;nh đ&atilde; chiếm vị tr&iacute; của n&oacute;. Đ&oacute; l&agrave; một giải ph&aacute;p th&ocirc;ng minh v&agrave; cung cấp hầu như c&ugrave;ng một trải nghiệm như n&uacute;t home, v&igrave; vậy người d&ugrave;ng Samsung l&acirc;u năm sẽ cảm thấy n&uacute;t home vẫn ở đ&oacute; m&agrave; chẳng mất đi đ&acirc;u cả.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/danh-gia-s8-6.jpg" style="width:80%" /></p>\r\n\r\n<p>Tất nhi&ecirc;n, kh&ocirc;ng c&oacute; n&uacute;t home th&igrave; sẽ kh&ocirc;ng c&oacute; m&aacute;y qu&eacute;t v&acirc;n tay ở mặt trước của điện thoại. Samsung đ&atilde; giải quyết vấn đề n&agrave;y bằng c&aacute;ch đặt cảm biến v&acirc;n tay ở mặt sau của thiết bị, đ&oacute; l&agrave; điều m&agrave; Google, Huawei, LG v&agrave; những người kh&aacute;c đ&atilde; l&agrave;m trong thời gian gần đ&acirc;y. Nhưng thay v&igrave; đặt m&aacute;y qu&eacute;t dấu v&acirc;n tay b&ecirc;n dưới m&aacute;y ảnh, hoặc gần giữa điện thoại, nơi ng&oacute;n tay trỏ của bạn trong l&uacute;c lấy điện thoại ra khỏi t&uacute;i hoặc l&agrave; đang thao t&aacute;c tr&ecirc;n m&agrave;n h&igrave;nh th&igrave;, Samsung lại đặt n&oacute; ngay cạnh m&aacute;y ảnh.</p>\r\n\r\n<p>Tại vị tr&iacute; n&agrave;y, chỉ duy nhất một sự hợp l&iacute; đ&oacute; l&agrave; độ c&acirc;n xứng cho vị tr&iacute; camera, một b&ecirc;n l&agrave; vị tr&iacute; của đ&egrave;n flash b&ecirc;n c&ograve;n lại l&agrave; nơi đặt cảm biến v&acirc;n tay. Nhưng x&eacute;t đến việc bất hợp l&iacute; th&igrave; chiếm phần nhiều hơn, thứ nhất l&agrave; ở vị tr&iacute; n&agrave;y cảm biến đặt kh&aacute; cao n&ecirc;n muốn mở m&aacute;y bằng cảm biến v&acirc;n tay bạn phải dịch tay l&ecirc;n một ch&uacute;t đối với S8 v&agrave; nhiều hơn ch&uacute;t nữa với S8 Plus. Thứ hai, bởi v&igrave; n&oacute; nằm ngay b&ecirc;n cạnh m&aacute;y ảnh v&agrave; c&oacute; h&igrave;nh dạng v&agrave; cảm gi&aacute;c tương tự đối với m&ocirc;-đun m&aacute;y ảnh, n&ecirc;n việc chạm phải cụm k&iacute;nh camera l&agrave; chuyện thường gặp phải, v&agrave; bạn sẽ kh&ocirc;ng qu&aacute; ngạc nhi&ecirc;n khi thấy ảnh chụp bổng chốc hơi mờ một ch&uacute;t so với thường lệ. Việc &aacute;p dụng c&ocirc;ng nghệ qu&eacute;t v&acirc;n tay ngay tr&ecirc;n m&agrave;n h&igrave;nh chậm trễ khiến S8 trở n&ecirc;n l&agrave; một phi&ecirc;n bản kh&ocirc;ng được ho&agrave;n hảo cho lắm, nếu x&eacute;t về to&agrave;n diện.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/danh-gia-s8-7.jpg" style="width:80%" /></p>\r\n\r\n<p>Nhưng nếu bạn sợ gặp phải t&igrave;nh trạng m&aacute;y ảnh mờ th&igrave; vẫn c&ograve;n phương ph&aacute;p bảo mật kh&aacute;c như nhận diện khu&ocirc;n mặt hoặc l&agrave; qu&eacute;t mống mắt. Đến hiện tại th&igrave; phương ph&aacute;p qu&eacute;t mống mắt vẫn được xem l&agrave; an to&agrave;n nhất hiện nay v&agrave; chưa c&oacute; c&aacute;ch n&agrave;o để vượt qua được phương ph&aacute;p bảo mật n&agrave;y, với việc mở m&agrave;n h&igrave;nh khởi động qu&eacute;t mống mắt bằng thao t&aacute;c vuốt th&igrave; nhanh ch&oacute;ng bạn c&oacute; thể sử dụng thiết bị ngay lập tức.</p>\r\n\r\n<blockquote>\r\n<p>Ngoại trừ cảm biến v&acirc;n tay, phần cứng của S8 thực sự ho&agrave;n hảo</p>\r\n</blockquote>\r\n\r\n<p>T&iacute;nh năng qu&eacute;t khu&ocirc;n mặt được t&iacute;ch hợp tr&ecirc;n S8 l&agrave; phương ph&aacute;p thuận tiện nhất để mở kh&oacute;a điện thoại, nhưng hiện tại hầu hết tr&ecirc;n c&aacute;c diễn đ&agrave;n đều đ&aacute;nh gi&aacute; phương ph&aacute;p n&agrave;y kh&ocirc;ng an to&agrave;n, phương ph&aacute;p n&agrave;y c&oacute; thể bị qua mặt bởi một bức ảnh n&agrave;o đ&oacute; của bạn, hoặc ngay cả ảnh selfie. N&ecirc;n tốt nhất l&agrave; bạn kh&ocirc;ng n&ecirc;n qu&aacute; tin tưởng v&agrave;o phương ph&aacute;p bảo mật n&agrave;y m&agrave; chuyển sang d&ugrave;ng mống mắt hoặc l&agrave; m&atilde; PIN sẽ tốt hơn.</p>\r\n\r\n<p>Tổng thể S8 l&agrave; ho&agrave;n to&agrave;n tuyệt vời với một thiết bị d&ugrave;ng cho cả c&ocirc;ng việc v&agrave; giải tr&iacute;, tuy c&oacute; ch&uacute;t rắc rối với m&agrave;n h&igrave;nh v&agrave; mặt k&iacute;nh sau hơi b&aacute;m v&acirc;n tay nhưng nếu l&agrave; một người y&ecirc;u c&aacute;i đẹp th&igrave; vấn đề n&agrave;y kh&ocirc;ng c&oacute; g&igrave; phải ph&agrave;n n&agrave;n cả. Ngo&agrave;i ra mặt k&iacute;nh ph&iacute;a sau S8 cũng được t&iacute;ch hợp sạc kh&ocirc;ng d&acirc;y cho bạn khả năng sạc điện thoại tiện lợi m&agrave; kh&ocirc;ng vướng v&iacute;u bởi đống d&acirc;y sạc hiện c&oacute;. C&ugrave;ng đ&oacute; S8 cũng cho khả năng chống nước theo ti&ecirc;u chuẩn IP68 30 ph&uacute;t ở 1,5 m&eacute;t, x&eacute;t về thực tế bạn sẽ kh&ocirc;ng phải lo lắng khi sử dụng S8 ở ngo&agrave;i mưa hoặc l&agrave; đi bơi.</p>\r\n\r\n<p>S8 v&agrave; S8 Plus sử dụng cổng USB Type &ndash; C để sạc v&agrave; truyền dữ liệu, v&agrave; cả hai điện thoại đều c&oacute; giắc cắm tai nghe 3,5 mm, Samsung cũng đang tung ra một bộ tai nghe c&oacute; d&acirc;y mang nh&atilde;n hiệu AKG với c&aacute;c mẫu S8 (gần đ&acirc;y đ&atilde; mua c&ocirc;ng ty mẹ của AKG l&agrave; Harman). Được đ&aacute;nh gi&aacute; l&agrave; tốt hơn khi so với EarPods của Apple, v&agrave; cho cảm gi&aacute;c đeo thoải m&aacute;i v&agrave; dễ d&agrave;ng hơn Samsung cũng cho biết chiếc tai nghe c&oacute; gi&aacute; l&ecirc;n đến $99.</p>\r\n\r\n<h3>Phần mềm</h3>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/danh-gia-s8-8.jpg" style="width:80%" /></p>\r\n\r\n<p>Ứng dụng kh&ocirc;ng phải l&agrave; thế mạnh của Samsung khi tạo dựng thương hiện tr&ecirc;n thị trường di động hiện nay. Được x&acirc;y dựng tr&ecirc;n hệ điều h&agrave;nh Android 7.0 Nougat, ứng dụng tr&ecirc;n S8 c&oacute; những điểm hạn chế đ&aacute;ng ch&uacute; &yacute;. C&oacute; thể thấy điều n&agrave;y qua việc m&agrave;n h&igrave;nh ch&iacute;nh kh&aacute; đơn giản với một ph&iacute;m tắt, bảng th&ocirc;ng b&aacute;o thời tiết hiển thị kh&aacute; lớn b&ecirc;n tr&ecirc;n m&agrave;n h&igrave;nh ch&iacute;nh. Để mở khay ứng dụng, người d&ugrave;ng chỉ cần vuốt m&agrave;n h&igrave;nh từ dưới l&ecirc;n v&agrave; v&uacute;t th&ecirc;m 1 lần nữa để quay lại trang ch&iacute;nh.</p>\r\n\r\n<p>Menu c&agrave;i đặt cũng đơn giản h&oacute;a tương tự, dễ d&agrave;ng điều hướng v&agrave; c&oacute; th&ecirc;m chức năng t&igrave;m kiếm để người d&ugrave;ng t&igrave;m kiếm c&aacute;c c&agrave;i đặt c&oacute; b&ecirc;n trong. N&oacute;i l&agrave; đơn giản nhưng phần c&agrave;i đặt vẫn đầy đủ c&aacute;c t&iacute;nh năng cần c&oacute; để người d&ugrave;ng c&oacute; thể điều chỉnh chiếc điện thoại của m&igrave;nh theo sở th&iacute;ch của m&igrave;nh. Người d&ugrave;ng c&oacute; thể c&agrave;i đặt thứ tự của c&aacute;c n&uacute;t hiển thị tr&ecirc;n m&agrave;n h&igrave;nh, c&aacute;c chủ đề giao diện,&hellip;</p>\r\n\r\n<blockquote>\r\n<p>Phần mềm tr&ecirc;n Galaxy S8 c&oacute; v&agrave;i hạn chế đ&aacute;ng ch&uacute; &yacute;</p>\r\n</blockquote>\r\n\r\n<p>T&iacute;nh năng mới v&agrave; đ&aacute;ng tr&ocirc;ng đợi nhất ch&iacute;nh l&agrave; trợ l&yacute; ảo của Samsung, Bixby. Samsung cho rằng Bixby sẽ l&agrave; một yếu tố quan trọng, mang t&iacute;nh quyết định cho sản phẩm của m&igrave;nh n&ecirc;n đ&atilde; gi&agrave;nh ri&ecirc;ng một n&uacute;t vật l&yacute; b&ecirc;n cạnh của chiếc smartphone chỉ để k&iacute;ch hoạt nhanh Bixby. Tuy nhi&ecirc;n đến thời điểm n&agrave;y, Bixby vẫn chưa l&agrave;m được g&igrave; nhiều so với mong đợi.</p>\r\n\r\n<p>Sau khi nhấn n&uacute;t k&iacute;ch hoạt Bixby 2 lần, người d&ugrave;ng sẽ truy cập v&agrave;o trang ch&iacute;nh của trợ l&yacute; ảo n&agrave;y, trang n&agrave;y sẽ hiển thị c&aacute;c thẻ v&agrave; c&aacute;c th&ocirc;ng tin m&agrave; người d&ugrave;ng thường xuy&ecirc;n truy cập đến. Ngo&agrave;i ra Bixby c&oacute; thể t&iacute;ch hợp v&agrave;o trong c&aacute;c ứng dụng của b&ecirc;n thứ 3 như Twitter, Facebook, Spotify, v&agrave;&nbsp;Foursquare, th&ocirc;ng b&aacute;o t&igrave;nh h&igrave;nh thời tiết, c&aacute;c c&ocirc;ng việc, ch&uacute; &yacute; đ&atilde; được ghi lại trước đ&oacute;. Tuy nhi&ecirc;n, Google, HTC v&agrave; một số h&atilde;ng kh&aacute;c đ&atilde; điều n&agrave;y từ rất l&acirc;u về trước. Samsung cho biết Bixby được thiết kế để c&oacute; thể học những th&oacute;i quen của người d&ugrave;ng v&agrave; sau đ&oacute; đưa ra c&aacute;c th&ocirc;ng tin dựa theo những g&igrave; đ&atilde; học được.</p>\r\n\r\n<blockquote>\r\n<p>Samsung kh&ocirc;ng x&acirc;y dựng ngữ cảnh tốt để mọi người c&oacute; l&yacute; do để d&ugrave;ng Bixby</p>\r\n</blockquote>\r\n\r\n<p>Ngo&agrave;i ra Bixby cũng c&oacute; thể truy cập nhanh camera v&agrave; thư viện ảnh, cung cấp c&aacute;c t&iacute;nh năng tương tự như những g&igrave; Amazon cung cấp trong ứng dụng của m&igrave;nh, cũng như những g&igrave; Google Goggles đ&atilde; l&agrave;m từ nhiều năm qua. Được gọi l&agrave;&nbsp;Bixby Vision, t&iacute;nh năng n&agrave;y sẽ sử dụng qu&eacute;t h&igrave;nh ảnh của m&aacute;y t&iacute;nh để x&aacute;c định một đối tượng hoặc đoạn văn bản sau đ&oacute; li&ecirc;n kết đến những h&igrave;nh ảnh tương tự tr&ecirc;n&nbsp;Pinterest hoặc c&aacute;c t&ugrave;y chọn để mua sản phẩm tr&ecirc;n Amazon. Ngo&agrave;i ra, Bixby Vision sử dụng Google Translate để dịch văn bản, x&aacute;c định mốc thời gian, th&ocirc;ng tin v&agrave; đường dẫn c&oacute; nội dung li&ecirc;n&nbsp;với vật đang đặt trước camera.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/danh-gia-s8-9.jpg" style="width:80%" /></p>\r\n\r\n<p>X&eacute;t về mặt thực tế, t&iacute;nh năng n&agrave;y kh&ocirc;ng thực sự hữu &iacute;ch đối với người d&ugrave;ng. Chỉ thực sự hữu &iacute;ch khi người d&ugrave;ng cần t&igrave;m mua một vật g&igrave; đ&oacute; tr&ecirc;n Amazon m&agrave; kh&ocirc;ng cần phải t&igrave;m th&ocirc;ng qua Google. Bixby được nh&agrave; sản xuất hứa rằng sẽ c&oacute; kiểu hỗ trợ giọng n&oacute;i mới, gi&uacute;p c&aacute;c thao t&aacute;c tr&ecirc;n điện thoại được dễ d&agrave;ng hơn, nhanh ch&oacute;ng hơn. Nhưng tới thời điểm hiện tại, điều đ&oacute; vẫn chưa đi v&agrave;o thực tế. Samsung cho biết họ ph&aacute;t h&agrave;nh t&iacute;nh năng n&agrave;y v&agrave;o &ldquo;cuối m&ugrave;a xu&acirc;n&rdquo;, v&agrave; vẫn chưa c&oacute; một ng&agrave;y ấn định cụ thể.</p>\r\n\r\n<p>Một điểm trừ của ứng dụng đ&oacute; l&agrave; c&oacute; những ứng dụng dư thừa xuất hiện tr&ecirc;n sản phẩm. Người d&ugrave;ng kh&ocirc;ng cần đến 2 ứng dụng email hay thư viện tr&ecirc;n c&ugrave;ng 1 sản phẩm, v&agrave; một số ứng dụng kh&aacute;c c&oacute; c&ugrave;ng chức năng nhưng lại mang t&ecirc;n kh&aacute;c nhau đến từ 2 nh&agrave; sản xuất kh&aacute;c nhau l&agrave; Google v&agrave; Samsung. Như vậy thật l&agrave; l&atilde;ng ph&iacute; dung lượng bộ nhớ. Samsung n&ecirc;n th&ecirc;m thiết lập c&agrave;i đặt ứng dụng hoặc cho ph&eacute;p người d&ugrave;ng gỡ bỏ c&aacute;c ứng dụng m&agrave; họ cảm thấy kh&ocirc;ng cần thiết để tối ưu h&oacute;a dung lượng bộ nhớ.</p>\r\n\r\n<p>Được t&iacute;ch hợp sẵn tr&ecirc;n S8, ứng dụng bảo mật&nbsp;Lookout được cho l&agrave; kh&aacute; phiền to&aacute;i khi lu&ocirc;n ph&aacute;t ra c&aacute;c th&ocirc;ng b&aacute;o g&acirc;y kh&oacute; chịu cho người d&ugrave;ng mỗi khi c&agrave;i đặt th&ecirc;m một ứng dụng n&agrave;o mới. Th&ocirc;ng b&aacute;o n&agrave;y chỉ biến mất khi người d&ugrave;ng nhấn v&agrave;o xem nội dung v&agrave; tắt n&oacute; đi. Chiếc S8 c&oacute; lẽ đ&atilde; tốt hơn nếu kh&ocirc;ng c&oacute; những ứng dụng phiền to&aacute;i n&agrave;y.</p>\r\n\r\n<h3>Hiệu năng</h3>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/danh-gia-s8-0.jpg" style="width:80%" /></p>\r\n\r\n<p>Kh&ocirc;ng g&igrave; ngạc nhi&ecirc;n, Galaxy S8 v&agrave; S8 Plus sở hữu một th&ocirc;ng số cấu h&igrave;nh v&agrave; t&iacute;nh năng của một con qu&aacute;i vật thật sự, một trong số đ&oacute; hướng đến tương lai m&agrave; thậm ch&iacute; bạn kh&ocirc;ng thể tận dụng được n&oacute;. Những chiếc m&aacute;y được b&aacute;n ra tại Mỹ v&agrave; một số thị trường phương T&acirc;y kh&aacute;c l&agrave; những chiếc điện thoại đầu ti&ecirc;n được trang bị chip Snapdragon 835, trong khi phần c&ograve;n lại của thế giới sẽ c&oacute; vi xử l&yacute; Exynos với sức mạnh tương đương của Samsung. Cả hai đều gi&uacute;p S8 nhanh v&agrave; đ&aacute;p ứng tức thời, nhưng nếu so với những flagship h&agrave;ng đầu kh&aacute;c th&igrave; S8 kh&oacute; nhanh hơn đ&aacute;ng kể như Pixel hay G6. C&acirc;u hỏi lớn l&agrave; liệu để S8 vẫn đạt được hiệu năng như vậy sau v&agrave;i th&aacute;ng sử dụng, t&ocirc;i vẫn chưa kiểm tra được.</p>\r\n\r\n<p>Đi k&egrave;m bộ vi xử l&yacute; l&agrave; 4GB RAM, 64GB bộ nhớ trong, hỗ trợ thẻ nhớ mở rộng, v&agrave; s&oacute;ng radio chuẩn LTE gigabit v&agrave; Wi-Fi gigabit. S8 l&agrave; chiếc điện thoại đầu ti&ecirc;n c&oacute; LTE gigabit m&agrave; bạn c&oacute; thể mua, nhưng ngay cả khi ch&uacute;ng ta sử dụng th&igrave; vẫn kh&ocirc;ng c&oacute; vấn đề g&igrave; qu&aacute; to t&aacute;t. S8 cũng l&agrave; chiếc điện thoại đầu ti&ecirc;n c&oacute; Bluetooh 5.0, điều n&agrave;y hứa hẹn gi&uacute;p bạn c&oacute; khoảng c&aacute;ch xa hơn v&agrave; chất lượng kết nối tốt hơn với c&aacute;c thiết bị kh&ocirc;ng d&acirc;y. T&ocirc;i cũng đ&atilde; chơi thử một số b&agrave;i nhạc tr&ecirc;n Youtube th&ocirc;ng qua 2 tai nghe kh&ocirc;ng d&acirc;y kết nối bằng Bluetooth c&ugrave;ng l&uacute;c tr&ecirc;n c&ugrave;ng một b&agrave;i nhạc.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/danh-gia-s8-11.jpg" style="width:80%" /></p>\r\n\r\n<p>Về thời lượng pin S8 c&oacute; dung lượng pin l&agrave; 3,000 mAh v&agrave; S8 Plus sẽ l&agrave; 3,500 mAh, cả hai chiếc đều thấp hơn chiếc S7 Edge năm ngo&aacute;i, c&oacute; thể l&agrave; do thiết kế mới tr&ecirc;n S8. T&ocirc;i đ&atilde; thực hiện kiểm tra thời lượng pin với hai chiếc si&ecirc;u phẩm mới n&agrave;y, S8 c&oacute; thời lượng sử dụng thấp hơn, trong khi S8 Plus sẽ cho thời gian sử dụng tương &nbsp;đường S7 Edge. Chiếc S8 c&oacute; thể ho&agrave;n th&agrave;nh 1 ng&agrave;y l&agrave;m việc với một người d&ugrave;ng c&oacute; cường độ nhẹ, phi&ecirc;n bản lớn hơn sẽ ph&ugrave; hợp với những bạn c&oacute; nhu cầu cao. Cả hai đều c&oacute; chuẩn sạc nhanh v&agrave; sạc kh&ocirc;ng d&acirc;y, đ&acirc;y l&agrave; một t&iacute;nh năng rất tiện.</p>\r\n\r\n<p>Về pin, ắt hẳn nhiều người c&ograve;n e ngại khi sự cố với chiếc Note 7 l&agrave; qu&aacute; lớn, t&ocirc;i kh&ocirc;ng chắc liệu n&oacute; sẽ lặp lại tr&ecirc;n S8. Nhưng Samsung đ&atilde; đầu tư r&acirc;t nhiều về đảm bảo rằng pin của S8 an to&agrave;n, tất nhi&ecirc;n h&atilde;ng sản xuất lớn như Samsung sẽ kh&ocirc;ng để chuyện của Note 7 xuất hiện một lần nữa tr&ecirc;n những trang b&aacute;o.</p>\r\n\r\n<h3>Camera</h3>\r\n\r\n<p>Một điểm kh&ocirc;ng thay đổi so với năm trước l&agrave; camera ch&iacute;nh, vẫn 12MP f/1.7 OIS. Samsung chỉ n&acirc;ng cấp khả năng xử l&yacute; phần mềm, sử dụng kỹ thuật tương tự như Apple v&agrave; Google để cho chi tiết ảnh tốt hơn khi &aacute;nh s&aacute;ng yếu, nhưng sự kh&aacute;c biệt thật sự l&agrave; rất &iacute;t. Điểm n&acirc;ng cấp đ&aacute;ng khen nhất tr&ecirc;n S8 ch&iacute;nh l&agrave; khả năng lấy n&eacute;t tốt hơn kh&aacute; nhiều so với S7 trong nhiều trường hợp kh&aacute;c nhau.</p>\r\n\r\n<p>Một số h&igrave;nh ảnh chụp từ S8 v&agrave; S8 Plus</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/anh-chup-s8-12.jpg" style="width:50%" /></p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/anh-chup-s8-13.jpg" style="width:50%" /></p>\r\n\r\n<h3>Kết luận</h3>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/danh-gia-s8-15.jpg" style="width:60%" /></p>\r\n\r\n<p>Thật sự m&agrave; n&oacute;i, trong nhiều năm liền, Samsung đ&atilde; cố gắng rất nhiều để tho&aacute;t khỏi c&aacute;i b&oacute;ng của Apple v&agrave; họ đ&atilde; thật sự th&agrave;nh c&ocirc;ng. S8 v&agrave; S8 Plus l&agrave; những thiết bị tuyệt vời, chiếc smartphone Android tốt nhất m&agrave; bạn c&oacute; thể mua trong thời điểm n&agrave;y, với khả năng hiển thị m&agrave;n h&igrave;nh ấn tượng, thiết kế đẹp v&agrave; một hiệu năng đỉnh.&nbsp;Kh&ocirc;ng c&oacute; g&igrave; l&agrave; ho&agrave;n hảo, vẫn sẽ c&oacute; những điểm chưa tốt như vị tr&iacute; đặt v&acirc;n tay hay một thời lượng pin trung b&igrave;nh. T&ocirc;i sẽ kh&ocirc;ng ngần ngại để tư vấn một ai đ&oacute; bất k&igrave; mua 2 chiếc si&ecirc;u phẩm n&agrave;y.</p>\r\n\r\n<blockquote>\r\n<p>S8 ho&agrave;n to&agrave;n c&oacute; thể thực hiện tốt việc th&aacute;ch thức những người &ldquo;kh&ocirc;ng c&ograve;n cảm thấy hứng th&uacute; với smartphone mới&rdquo;</p>\r\n</blockquote>\r\n', 'images/danh-gia-s8-0.jpg', 33, b'1', 'danh-gia-samsung-galaxy-s8-dan-dau-duong-cong', 'Đánh giá Samsung Galaxy S8: Dẫn đầu đường cong', 'dang gia s8,', 'Người ta nói nhiều về khái niệm hoàn hảo, nhưng họ cũng hay nói không có gì hoàn hảo. Galaxy S8 và S8 Plus tất nhiên cũng như vậy, nhưng nó cũng đạt đến mức tiệm cận chỉnh chu cần có.', '2018-01-29', 2, b'1'),
(30, 'Sony Xperia XZ Premium màn hình 4K HDR đã ra mắt ', 1, '<h2>Sony Xperia XZ Premium vừa mới được c&ocirc;ng bố, t&iacute;ch hợp m&agrave;n h&igrave;nh 4K HDR đầu ti&ecirc;n cho smartphone, c&ugrave;ng những t&iacute;nh năng mới đang đợi người d&ugrave;ng kh&aacute;m ph&aacute;.</h2>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/xperia-xa-p-1.jpg" style="width:90%" /></p>\r\n\r\n<p>Chiếc smartphone XZ Premium sở hữu m&agrave;n h&igrave;nh 4K HDR (2,160 x 3,840, High Dynamic Range) đầu ti&ecirc;n tr&ecirc;n thế giới t&iacute;ch hợp cho điện thoại. Đ&acirc;y l&agrave; c&ocirc;ng nghệ hấp dẫn mới nhất được t&iacute;ch hợp cho m&agrave;n h&igrave;nh TV, v&agrave; Sony đ&atilde; t&iacute;ch hợp n&oacute; v&agrave;o trong chiếc smatphone mới nhất của m&igrave;nh. Tuy ở chiếc điện thoại Xperia Z5 Preimum được Sony ra cho ra mắt v&agrave;o năm 2015 đ&atilde; t&iacute;ch hợp m&agrave;n h&igrave;nh 4K, nhưng kh&ocirc;ng thật sự l&agrave;m n&ecirc;n cuộc c&aacute;ch mạng về m&agrave;n h&igrave;nh hiển thị h&igrave;nh ảnh cho đến thời điểm n&agrave;y. Sony đ&atilde; hợp t&aacute;c với Amazon về vấn đề hiển thị 4K HDR, v&agrave; HDR l&agrave; yếu tố v&ocirc; c&ugrave;ng tuyệt vời trong việc t&aacute;i tạo m&agrave;u sắc. Trong bản demo của Sony, nếu so s&aacute;nh giữa Z5 Premium với XZ th&igrave; XZ cho h&igrave;nh ảnh ấm hơn, m&agrave;u v&agrave;ng tự nhi&ecirc;n hơn, t&aacute;i tạo h&igrave;nh ảnh nh&igrave;n chung thực tế hơn về đường phố Lisbon.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/xperia-xa-p-2.jpg" style="width:90%" /></p>\r\n\r\n<p>B&ecirc;n cạnh m&agrave;n h&igrave;nh hiển thị v&ocirc; c&ugrave;ng ấn tượng, chiếc flagship mới nhất của Sony c&ograve;n sở hữu hệ thống camera mới mang t&ecirc;n Motion Eye. Điểm đặc biệt của hệ thống camera n&agrave;y đ&oacute; l&agrave; Sony đ&atilde; t&iacute;ch hợp fast memory v&agrave;o camera stack, khiến chiếc điện thoại l&agrave; chiếc điện thoại đầu ti&ecirc;n tr&ecirc;n thế giới cho ph&eacute;p chụp ảnh super-slow motion tốc độ 960fps c&oacute; độ ph&acirc;n giải 720p. Với tốc độ chụp chỉ 0,18 gi&acirc;y, về mặt kĩ thuật th&igrave; người d&ugrave;ng chỉ c&oacute; thể chụp được một vật n&agrave;o đ&oacute; với khoảng 180 khung h&igrave;nh, nhưng hiệu quả vẫn kh&aacute; thuyết phục khi&nbsp; đạt tốc độ 30fps th&ocirc;ng thường. Tức người d&ugrave;ng c&oacute; thể chụp một giọt nước văng tung t&oacute;e hay một c&aacute;i chớp mắt.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/xperia-xa-p-3.jpg" style="width:90%" /></p>\r\n\r\n<p>Với việc bổ sung th&ecirc;m bộ nhớ cho camera, Sony cho ph&eacute;p người d&ugrave;ng chụp nhanh nhất c&oacute; thể khi ph&aacute;t hiện chuyển động trong khung h&igrave;nh &ndash; để khi bạn nhấn n&uacute;t chụp, sẽ kh&ocirc;ng xuất hiện t&igrave;nh trạng giật lag, camera sẽ chụp ngay khoảnh khắc l&uacute;c bạn nhấn n&uacute;t chụp. Hệ thống n&agrave;y sẽ phụ thuộc rất lớn v&agrave;o bộ cảm biến h&igrave;nh ảnh tốt hay kh&ocirc;ng, Sony lần n&agrave;y đ&atilde; t&iacute;ch hợp bộ ba cảm biến h&igrave;nh ảnh, bao gồm: laser AF, cảm biến hồng ngoại RGBC điều chỉnh c&acirc;n bằng trắng, cảm biến h&igrave;nh ảnh ExmorRS đ&atilde; được cập nhật. Camera đ&atilde; bị giảm xuống 19% pixel, xuống c&ograve;n 19 MP. C&ocirc;ng nghệ xử l&yacute;&nbsp;h&igrave;nh ảnh&nbsp;BIONZ&nbsp;của Sony cũng đ&atilde; được&nbsp;n&acirc;ng cấp&nbsp;với việc ph&aacute;t hiện&nbsp;chuyển động tốt hơn&nbsp;v&agrave;&nbsp;giảm nhiễu.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://localhost/congngheviet/images/xperia-xa-p-0.jpg" style="width:90%" /></p>\r\n\r\n<p>Camera sau của XZs &ndash; phi&ecirc;n bản thu nhỏ của XZ Premium &ndash; c&oacute; độ ph&acirc;n giải 19 MP tương tự XZ Premium, với t&iacute;nh năng nổi bật nhất ch&iacute;nh l&agrave; quay được Slow Motion với tốc độ 960fps. Chưa c&oacute; chiếc flagship n&agrave;o c&oacute; tốc độ fps đạt được như chiếc XZs v&agrave; XZ Premium lần n&agrave;y được Sony c&ocirc;ng bố. Cả hai phi&ecirc;n bản n&agrave;y đều sử dụng k&iacute;nh cường lực Gorilla Glass 5 ở mặt trước v&agrave; mặt sau, điểm trừ của sản phẩm n&agrave;y l&agrave; độ b&aacute;m v&acirc;n tay.</p>\r\n\r\n<p>Theo như c&aacute;c th&ocirc;ng số kĩ thuật đ&atilde; được c&ocirc;ng bố, bộ đ&ocirc;i flagship lần n&agrave;y sử dụng chip Snapdragon 835, khả năng chống nước IP65 v&agrave; IP68, thiết kế mỏng hơn 7,9 mm, hỗ trợ khe cắm thẻ nhớ microSD, pin c&oacute; dung lượng 3230 mAh v&agrave; cảm biến v&acirc;n tay được t&iacute;ch hợp tr&ecirc;n ph&iacute;m nguồn.</p>\r\n', 'images/xperia-xa-p-2.jpg', 89, b'1', 'sony-xperia-xz-premium-man-hinh-4k-hdr-da-ra-mat', 'Sony Xperia XZ Premium màn hình 4K HDR đã ra mắt ', 'Xperia XZ premium', 'Sony Xperia XZ Premium vừa mới được công bố, tích hợp màn hình 4K HDR đầu tiên cho smartphone, cùng những tính năng mới đang đợi người dùng khám phá.', '2018-01-29', 2, b'1');

