-- Đang đổ dữ liệu cho bảng `tblcm`
--

INSERT INTO `tblcm` (`idcm`, `tencm`, `linkcm`, `tukhoa`, `hienthi`, `mota`) VALUES
(1, 'Công nghệ', 'cong-nhe', 'cong nghe, bao cong nghe, cong nghe viet', b'1', 'Trang tin công nghệ cung cấp nội dung về tin tức'),
